import React, { useState, useEffect } from 'react';
import { toast } from 'react-toastify';
import { authService } from '../../services/auth';
import { touristAPI } from '../../services/api';
import { geolocationService } from '../../services/geolocation';
import { SOSLoading } from '../common/Loading';

const SOS = () => {
  const [sosActive, setSosActive] = useState(false);
  const [countdown, setCountdown] = useState(0);
  const [location, setLocation] = useState(null);
  const [emergencyContacts, setEmergencyContacts] = useState([
    { name: 'Emergency Services', number: '112' },
    { name: 'Local Police', number: '100' },
    { name: 'Tourist Helpline', number: '1363' },
    { name: 'Family Contact', number: '+91 98765 43210' }
  ]);

  const userInfo = authService.getUserInfo();

  useEffect(() => {
    getCurrentLocation();
  }, []);

  useEffect(() => {
    let interval = null;
    if (sosActive && countdown > 0) {
      interval = setInterval(() => {
        setCountdown(countdown => countdown - 1);
      }, 1000);
    } else if (countdown === 0 && sosActive) {
      // SOS activated
      handleSOSActivation();
    }
    return () => clearInterval(interval);
  }, [sosActive, countdown]);

  const getCurrentLocation = async () => {
    try {
      const position = await geolocationService.getEnhancedPosition();
      setLocation(position);
    } catch (error) {
      console.error('Location error:', error);
      toast.error('Unable to get location. SOS will still work.');
    }
  };

  const startSOS = () => {
    setSosActive(true);
    setCountdown(5); // 5-second countdown
    toast.warning('SOS Alert will be sent in 5 seconds. Press Cancel to stop.');
  };

  const cancelSOS = () => {
    setSosActive(false);
    setCountdown(0);
    toast.info('SOS Alert cancelled');
  };

  const handleSOSActivation = async () => {
    try {
      const sosData = {
        touristId: userInfo?.id,
        touristName: userInfo?.name,
        location: location || { lat: 28.6139, lng: 77.2090, address: 'Location unavailable' },
        timestamp: new Date().toISOString(),
        type: 'EMERGENCY_SOS',
        message: 'URGENT: Tourist requiring immediate assistance'
      };

      // In production: await touristAPI.sendSOSAlert(sosData);

      // Simulate API call
      await new Promise(resolve => setTimeout(resolve, 2000));

      toast.success('🚨 Emergency alerts sent successfully!');
      toast.info('📞 Emergency services have been notified');
      toast.info('👨‍👩‍👧‍👦 Your emergency contacts are being called');

      // Reset after successful send
      setTimeout(() => {
        setSosActive(false);
      }, 3000);

    } catch (error) {
      console.error('SOS Error:', error);
      toast.error('Failed to send SOS alert. Please call emergency services directly.');
      setSosActive(false);
    }
  };

  const callEmergency = (number) => {
    window.open(`tel:${number}`);
    toast.info(`Calling ${number}...`);
  };

  if (sosActive && countdown === 0) {
    return (
      <div className="min-vh-100 d-flex align-items-center justify-content-center bg-danger bg-opacity-10">
        <div className="container">
          <div className="row justify-content-center">
            <div className="col-md-8 col-lg-6">
              <div className="card border-danger shadow-lg">
                <div className="card-body p-5 text-center">
                  <SOSLoading />

                  <div className="mt-4">
                    <button 
                      className="btn btn-outline-secondary btn-lg"
                      onClick={() => setSosActive(false)}
                    >
                      ✕ Close Alert Status
                    </button>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-vh-100 py-5">
      <div className="container">
        <div className="row justify-content-center">
          <div className="col-md-10 col-lg-8">
            {/* Header */}
            <div className="card mb-4">
              <div className="card-body text-center p-4">
                <div className="mb-3" style={{ fontSize: '4rem' }}>🆘</div>
                <h2 className="text-danger mb-2">Emergency SOS System</h2>
                <p className="text-muted mb-0">
                  Press the emergency button below if you need immediate assistance
                </p>
                {location && (
                  <div className="mt-3">
                    <span className="badge bg-success">
                      📍 Location: {location.address || 'Coordinates available'}
                    </span>
                  </div>
                )}
              </div>
            </div>

            {/* SOS Button */}
            <div className="card mb-4">
              <div className="card-body text-center p-5">
                {sosActive ? (
                  <div>
                    <div className="mb-4">
                      <div 
                        className="sos-button mx-auto mb-3"
                        style={{
                          background: 'linear-gradient(135deg, #e74c3c 0%, #c0392b 100%)',
                          animation: 'sos-pulse 0.5s infinite'
                        }}
                      >
                        <div>
                          <div style={{ fontSize: '2rem' }}>🚨</div>
                          <div className="fw-bold">{countdown}</div>
                        </div>
                      </div>
                      <h3 className="text-danger mb-2">
                        SENDING SOS IN {countdown} SECONDS
                      </h3>
                      <p className="text-muted mb-4">
                        Emergency services and your contacts will be alerted
                      </p>
                    </div>

                    <button 
                      className="btn btn-warning btn-lg px-5"
                      onClick={cancelSOS}
                    >
                      ✕ CANCEL SOS
                    </button>
                  </div>
                ) : (
                  <div>
                    <div className="mb-4">
                      <button 
                        className="sos-button mx-auto mb-3"
                        onClick={startSOS}
                      >
                        <div>
                          <div style={{ fontSize: '2rem' }}>🆘</div>
                          <div className="fw-bold">SOS</div>
                        </div>
                      </button>
                      <h3 className="text-danger mb-2">EMERGENCY ALERT</h3>
                      <p className="text-muted mb-0">
                        Press and hold for immediate assistance
                      </p>
                    </div>
                  </div>
                )}
              </div>
            </div>

            {/* Emergency Contacts */}
            <div className="row">
              <div className="col-md-6 mb-4">
                <div className="card h-100">
                  <div className="card-header bg-danger text-white">
                    <h5 className="mb-0">🚨 Emergency Services</h5>
                  </div>
                  <div className="card-body">
                    <div className="list-group list-group-flush">
                      <div className="list-group-item d-flex justify-content-between align-items-center px-0">
                        <div>
                          <div className="fw-bold">National Emergency</div>
                          <div className="small text-muted">All emergency services</div>
                        </div>
                        <button 
                          className="btn btn-danger btn-sm"
                          onClick={() => callEmergency('112')}
                        >
                          📞 112
                        </button>
                      </div>
                      <div className="list-group-item d-flex justify-content-between align-items-center px-0">
                        <div>
                          <div className="fw-bold">Police</div>
                          <div className="small text-muted">Law enforcement</div>
                        </div>
                        <button 
                          className="btn btn-danger btn-sm"
                          onClick={() => callEmergency('100')}
                        >
                          📞 100
                        </button>
                      </div>
                      <div className="list-group-item d-flex justify-content-between align-items-center px-0">
                        <div>
                          <div className="fw-bold">Medical Emergency</div>
                          <div className="small text-muted">Ambulance services</div>
                        </div>
                        <button 
                          className="btn btn-danger btn-sm"
                          onClick={() => callEmergency('102')}
                        >
                          📞 102
                        </button>
                      </div>
                      <div className="list-group-item d-flex justify-content-between align-items-center px-0">
                        <div>
                          <div className="fw-bold">Tourist Helpline</div>
                          <div className="small text-muted">24/7 tourist assistance</div>
                        </div>
                        <button 
                          className="btn btn-primary btn-sm"
                          onClick={() => callEmergency('1363')}
                        >
                          📞 1363
                        </button>
                      </div>
                    </div>
                  </div>
                </div>
              </div>

              <div className="col-md-6 mb-4">
                <div className="card h-100">
                  <div className="card-header bg-info text-white">
                    <h5 className="mb-0">👨‍👩‍👧‍👦 Personal Contacts</h5>
                  </div>
                  <div className="card-body">
                    <div className="list-group list-group-flush">
                      {emergencyContacts.slice(3).map((contact, index) => (
                        <div key={index} className="list-group-item d-flex justify-content-between align-items-center px-0">
                          <div>
                            <div className="fw-bold">{contact.name}</div>
                            <div className="small text-muted">{contact.number}</div>
                          </div>
                          <button 
                            className="btn btn-info btn-sm"
                            onClick={() => callEmergency(contact.number)}
                          >
                            📞 Call
                          </button>
                        </div>
                      ))}

                      <div className="list-group-item px-0">
                        <button 
                          className="btn btn-outline-info w-100"
                          onClick={() => toast.info('Emergency contact management coming soon')}
                        >
                          ➕ Add Emergency Contact
                        </button>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </div>

            {/* Safety Tips */}
            <div className="card mb-4">
              <div className="card-header">
                <h5 className="mb-0">💡 Emergency Safety Tips</h5>
              </div>
              <div className="card-body">
                <div className="row">
                  <div className="col-md-6">
                    <h6 className="text-primary">🆘 When to use SOS:</h6>
                    <ul className="small">
                      <li>Medical emergencies</li>
                      <li>Personal safety threats</li>
                      <li>Lost in unsafe area</li>
                      <li>Witnessing crimes</li>
                      <li>Natural disasters</li>
                    </ul>
                  </div>
                  <div className="col-md-6">
                    <h6 className="text-primary">📱 What happens when you use SOS:</h6>
                    <ul className="small">
                      <li>Location shared with authorities</li>
                      <li>Emergency services contacted</li>
                      <li>Personal contacts notified</li>
                      <li>Tourist safety command alerted</li>
                      <li>Real-time tracking activated</li>
                    </ul>
                  </div>
                </div>

                <div className="alert alert-warning mt-3">
                  <strong>⚠️ Important:</strong> Only use the SOS feature for genuine emergencies. 
                  False alarms can delay response to real emergencies.
                </div>
              </div>
            </div>

            {/* Current Location Info */}
            {location && (
              <div className="card">
                <div className="card-header">
                  <h5 className="mb-0">📍 Your Current Location</h5>
                </div>
                <div className="card-body">
                  <div className="row">
                    <div className="col-md-6">
                      <p><strong>Address:</strong> {location.address}</p>
                      <p><strong>Coordinates:</strong> {location.formatted}</p>
                      <p><strong>Accuracy:</strong> ±{Math.round(location.accuracy)}m</p>
                    </div>
                    <div className="col-md-6">
                      <p><strong>Last Updated:</strong> {location.timestamp.toLocaleTimeString()}</p>
                      <button 
                        className="btn btn-outline-primary btn-sm"
                        onClick={getCurrentLocation}
                      >
                        🔄 Update Location
                      </button>
                    </div>
                  </div>
                </div>
              </div>
            )}
          </div>
        </div>
      </div>

      <style jsx>{`
        @keyframes sos-pulse {
          0%, 100% {
            transform: scale(1);
            box-shadow: 0 0 0 0 rgba(231, 76, 60, 0.7);
          }
          50% {
            transform: scale(1.05);
            box-shadow: 0 0 0 20px rgba(231, 76, 60, 0);
          }
        }
      `}</style>
    </div>
  );
};

export default SOS;
