import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { toast } from 'react-toastify';
import { authService } from '../../services/auth';
import { touristAPI, mockData } from '../../services/api';
import { geolocationService } from '../../services/geolocation';
import Loading from '../common/Loading';

const Home = () => {
  const [loading, setLoading] = useState(true);
  const [locationLoading, setLocationLoading] = useState(false);
  const [dashboardData, setDashboardData] = useState({
    safetyScore: 85,
    currentLocation: 'Loading...',
    lastCheckIn: new Date(),
    alertsCount: 0,
    nearbyTourists: 12,
    emergencyContacts: []
  });
  const [currentLocation, setCurrentLocation] = useState(null);
  const [weatherData, setWeatherData] = useState({
    temperature: '24Â°C',
    condition: 'Clear',
    humidity: '65%',
    visibility: 'Good'
  });

  const navigate = useNavigate();
  const userInfo = authService.getUserInfo();

  useEffect(() => {
    loadDashboardData();
    getCurrentLocation();
  }, []);

  const loadDashboardData = async () => {
    try {
      setLoading(true);
      // In production: const data = await touristAPI.getDashboard();
      
      // For demo, simulate API call
      await new Promise(resolve => setTimeout(resolve, 1000));
      
      setDashboardData({
        safetyScore: 85,
        currentLocation: 'India Gate, New Delhi',
        lastCheckIn: new Date(Date.now() - 30 * 60000), // 30 minutes ago
        alertsCount: 0,
        nearbyTourists: 12,
        emergencyContacts: [
          { name: 'Emergency Services', number: '112' },
          { name: 'Tourist Helpline', number: '1363' },
          { name: 'Local Police', number: '100' }
        ],
        upcomingDestinations: ['Red Fort', 'Qutub Minar', 'Lotus Temple'],
        recentActivity: [
          { action: 'Safety check-in', time: '30 minutes ago', status: 'success' },
          { action: 'Location updated', time: '1 hour ago', status: 'info' },
          { action: 'Entered safe zone', time: '2 hours ago', status: 'success' }
        ]
      });
    } catch (error) {
      console.error('Error loading dashboard:', error);
      toast.error('Failed to load dashboard data');
    } finally {
      setLoading(false);
    }
  };

  const getCurrentLocation = async () => {
    try {
      setLocationLoading(true);
      const position = await geolocationService.getEnhancedPosition();
      setCurrentLocation(position);
      
      setDashboardData(prev => ({
        ...prev,
        currentLocation: position.address || `${position.lat.toFixed(4)}, ${position.lng.toFixed(4)}`
      }));
    } catch (error) {
      console.error('Location error:', error);
      setDashboardData(prev => ({
        ...prev,
        currentLocation: 'Location unavailable'
      }));
    } finally {
      setLocationLoading(false);
    }
  };

  const handleLogout = () => {
    if (window.confirm('Are you sure you want to logout?')) {
      authService.removeToken();
      toast.success('Logged out successfully');
      navigate('/login');
      window.location.reload();
    }
  };

  const handleSafetyCheckIn = () => {
    toast.success('Safety check-in recorded! Your emergency contacts have been notified.');
    setDashboardData(prev => ({
      ...prev,
      lastCheckIn: new Date(),
      recentActivity: [
        { action: 'Safety check-in', time: 'Just now', status: 'success' },
        ...prev.recentActivity.slice(0, 2)
      ]
    }));
  };

  const handleEmergencySOS = () => {
    navigate('/tourist/sos');
  };

  const getSafetyScoreColor = (score) => {
    if (score >= 80) return 'success';
    if (score >= 60) return 'warning';
    return 'danger';
  };

  const getSafetyScoreText = (score) => {
    if (score >= 90) return 'Excellent';
    if (score >= 80) return 'Good';
    if (score >= 60) return 'Fair';
    return 'Needs Attention';
  };

  if (loading) {
    return <Loading message="Loading your safety dashboard..." size="large" />;
  }

  return (
    <div className="container-fluid py-4">
      {/* Welcome Header with Logout Button */}
      <div className="row">
        <div className="col-12">
          <div className="card bg-gradient-primary text-white">
            <div className="card-body p-4">
              <div className="row align-items-center">
                <div className="col-md-8">
                  <h2 className="mb-2">Welcome back, {userInfo?.name || 'Tourist'}! ðŸ‘‹</h2>
                  <p className="mb-2">Your safety is our priority. Stay connected and stay safe.</p>
                  <div className="d-flex align-items-center">
                    <span className="badge bg-white text-primary me-2">
                      ðŸ“ {dashboardData.currentLocation}
                    </span>
                    {locationLoading && (
                      <div className="spinner-border spinner-border-sm text-white" role="status">
                        <span className="visually-hidden">Updating location...</span>
                      </div>
                    )}
                  </div>
                </div>
                <div className="col-md-4 text-end">
                  <div className="mb-3">
                    <button 
                      className="btn btn-light btn-sm me-2"
                      onClick={handleLogout}
                    >
                      ðŸšª Logout
                    </button>
                  </div>
                  <div className="display-6">ðŸ›¡ï¸</div>
                  <div className="small">Last check-in:</div>
                  <div className="fw-bold">
                    {new Date(dashboardData.lastCheckIn).toLocaleTimeString()}
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Main Dashboard Stats */}
      <div className="row mt-4">
        <div className="col-md-3 mb-3">
          <div className="card">
            <div className="card-body text-center">
              <h5 className="mb-0">ðŸ›¡ï¸ Safety Score</h5>
              <div className={`display-6 text-${getSafetyScoreColor(dashboardData.safetyScore)} mt-2`}>
                {dashboardData.safetyScore}%
              </div>
              <div className="small text-muted">{getSafetyScoreText(dashboardData.safetyScore)}</div>
            </div>
          </div>
        </div>

        <div className="col-md-3 mb-3">
          <div className="card">
            <div className="card-body text-center">
              <h5 className="mb-0">ðŸ“Š Status</h5>
              <div className="display-6 text-success mt-2">Safe</div>
              <div className="small text-muted">You're in a safe zone</div>
            </div>
          </div>
        </div>

        <div className="col-md-3 mb-3">
          <div className="card">
            <div className="card-body text-center">
              <h5 className="mb-0">ðŸš¨ Alerts</h5>
              <div className="display-6 text-info mt-2">{dashboardData.alertsCount}</div>
              <div className="small text-muted">No current alerts</div>
            </div>
          </div>
        </div>

        <div className="col-md-3 mb-3">
          <div className="card">
            <div className="card-body text-center">
              <h5 className="mb-0">ðŸ‘¥ Community</h5>
              <div className="display-6 text-primary mt-2">{dashboardData.nearbyTourists}</div>
              <div className="small text-muted">Tourists nearby</div>
            </div>
          </div>
        </div>
      </div>

      <div className="row mt-4">
        {/* Quick Actions */}
        <div className="col-md-6">
          <div className="card h-100">
            <div className="card-header">
              <h5 className="mb-0">âš¡ Quick Actions</h5>
            </div>
            <div className="card-body">
              <div className="row">
                <div className="col-6 mb-3">
                  <button 
                    className="btn btn-danger w-100 py-3"
                    onClick={handleEmergencySOS}
                  >
                    ðŸ†˜<br />Emergency SOS
                  </button>
                </div>
                <div className="col-6 mb-3">
                  <button 
                    className="btn btn-success w-100 py-3"
                    onClick={handleSafetyCheckIn}
                  >
                    âœ…<br />Safety Check-in
                  </button>
                </div>
                <div className="col-6 mb-3">
                  <button 
                    className="btn btn-primary w-100 py-3"
                    onClick={() => navigate('/tourist/map')}
                  >
                    ðŸ—ºï¸<br />Safety Map
                  </button>
                </div>
                <div className="col-6 mb-3">
                  <button 
                    className="btn btn-info w-100 py-3"
                    onClick={() => navigate('/tourist/qr')}
                  >
                    ðŸ†”<br />My Digital ID
                  </button>
                </div>
              </div>

              <div className="mt-3">
                <button 
                  className="btn btn-outline-secondary w-100"
                  onClick={getCurrentLocation}
                  disabled={locationLoading}
                >
                  {locationLoading ? (
                    <>
                      <span className="spinner-border spinner-border-sm me-2" />
                      Updating Location...
                    </>
                  ) : (
                    'ðŸ“ Update My Location'
                  )}
                </button>
              </div>
              
              {/* Additional Logout Button in Actions */}
              <div className="mt-3">
                <button 
                  className="btn btn-outline-danger w-100"
                  onClick={handleLogout}
                >
                  ðŸšª Logout from System
                </button>
              </div>
            </div>
          </div>
        </div>

        {/* Recent Activity & Weather */}
        <div className="col-md-6">
          <div className="card h-100">
            <div className="card-header">
              <h5 className="mb-0">ðŸ“Š Recent Activity</h5>
            </div>
            <div className="card-body">
              {dashboardData.recentActivity && dashboardData.recentActivity.length > 0 ? (
                dashboardData.recentActivity.map((activity, index) => (
                  <div key={index} className="d-flex align-items-center mb-3">
                    <div className="me-3">
                      <span className={`badge ${
                        activity.status === 'success' ? 'bg-success' :
                        activity.status === 'warning' ? 'bg-warning' : 'bg-info'
                      }`}>
                        {activity.status === 'success' ? 'âœ…' :
                         activity.status === 'warning' ? 'âš ï¸' : 'â„¹ï¸'}
                      </span>
                    </div>
                    <div className="flex-grow-1">
                      <div className="fw-semibold">{activity.action}</div>
                      <div className="small text-muted">{activity.time}</div>
                    </div>
                  </div>
                ))
              ) : (
                <div className="text-center text-muted py-4">
                  <div style={{ fontSize: '3rem' }}>ðŸ“Š</div>
                  <p>No recent activity</p>
                  <small>Your safety activities will appear here</small>
                </div>
              )}

              {/* Weather Info */}
              <div className="mt-4 p-3 bg-light rounded">
                <h6 className="mb-2">ðŸŒ¤ï¸ Current Weather</h6>
                <div className="row text-center">
                  <div className="col-3">
                    <div className="fw-bold">{weatherData.temperature}</div>
                    <div className="small text-muted">Temp</div>
                  </div>
                  <div className="col-3">
                    <div className="fw-bold">{weatherData.condition}</div>
                    <div className="small text-muted">Sky</div>
                  </div>
                  <div className="col-3">
                    <div className="fw-bold">{weatherData.humidity}</div>
                    <div className="small text-muted">Humidity</div>
                  </div>
                  <div className="col-3">
