                <div className="text-center text-muted py-4">
                  <div style={{ fontSize: '3rem' }}>ðŸ—ºï¸</div>
                  <p>Explore nearby destinations</p>
                  <button 
                    className="btn btn-primary"
                    onClick={() => navigate('/tourist/map')}
