import React, { useState, useEffect } from 'react';
import { QRCodeSVG } from 'qrcode.react';
import { toast } from 'react-toastify';
import { authService } from '../../services/auth';
import { touristAPI } from '../../services/api';
import Loading from '../common/Loading';

const QRDisplay = () => {
  const [qrData, setQrData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [showBlockchain, setShowBlockchain] = useState(false);

  const userInfo = authService.getUserInfo();

  useEffect(() => {
    generateQRData();
  }, []);

  const generateQRData = async () => {
    try {
      setLoading(true);

      // Generate blockchain hash (simulation)
      const blockchainHash = generateBlockchainHash();

      const qrInfo = {
        id: userInfo?.id || 'tourist-demo',
        name: userInfo?.name || 'Demo Tourist',
        email: userInfo?.email || 'demo@tourist.com',
        phone: '+91 98765 43210',
        destination: 'Delhi',
        emergencyContact: '+91 87654 32109',
        blockchainHash: blockchainHash,
        safetyScore: 88,
        timestamp: new Date().toISOString(),
        version: '2.0',
        verified: true,
        issuer: 'Tourist Safety Authority',
        expiryDate: new Date(Date.now() + 30 * 24 * 60 * 60 * 1000).toISOString() // 30 days
      };

      setQrData(qrInfo);

      // Simulate API call delay
      await new Promise(resolve => setTimeout(resolve, 1000));

    } catch (error) {
      console.error('Error generating QR data:', error);
      toast.error('Failed to generate QR code');
    } finally {
      setLoading(false);
    }
  };

  const generateBlockchainHash = () => {
    const timestamp = Date.now().toString();
    const random = Math.random().toString(36).substring(7);
    const userData = userInfo?.name + userInfo?.email + timestamp + random;

    // Simulate blockchain hash generation
    let hash = '';
    const chars = 'abcdef0123456789';
    for (let i = 0; i < 64; i++) {
      hash += chars.charAt(Math.floor(Math.random() * chars.length));
    }
    return hash;
  };

  const downloadQR = (format = 'png') => {
    const svg = document.getElementById('tourist-qr-code');
    const svgData = new XMLSerializer().serializeToString(svg);

    if (format === 'svg') {
      const svgBlob = new Blob([svgData], {type: 'image/svg+xml;charset=utf-8'});
      const svgUrl = URL.createObjectURL(svgBlob);
      const downloadLink = document.createElement('a');
      downloadLink.href = svgUrl;
      downloadLink.download = `tourist-id-${userInfo?.name || 'demo'}.svg`;
      document.body.appendChild(downloadLink);
      downloadLink.click();
      document.body.removeChild(downloadLink);
    } else {
      // Convert to PNG
      const canvas = document.createElement('canvas');
      const ctx = canvas.getContext('2d');
      const img = new Image();

      img.onload = function() {
        canvas.width = 512;
        canvas.height = 512;
        ctx.fillStyle = 'white';
        ctx.fillRect(0, 0, canvas.width, canvas.height);
        ctx.drawImage(img, 0, 0, canvas.width, canvas.height);

        canvas.toBlob(function(blob) {
          const url = URL.createObjectURL(blob);
          const downloadLink = document.createElement('a');
          downloadLink.href = url;
          downloadLink.download = `tourist-id-${userInfo?.name || 'demo'}.png`;
          document.body.appendChild(downloadLink);
          downloadLink.click();
          document.body.removeChild(downloadLink);
        });
      };

      img.src = 'data:image/svg+xml;base64,' + btoa(unescape(encodeURIComponent(svgData)));
    }

    toast.success(`QR Code downloaded as ${format.toUpperCase()}`);
  };

  const copyToClipboard = () => {
    const qrText = JSON.stringify(qrData, null, 2);
    navigator.clipboard.writeText(qrText).then(() => {
      toast.success('QR data copied to clipboard');
    }).catch(() => {
      toast.error('Failed to copy to clipboard');
    });
  };

  const shareQR = async () => {
    if (navigator.share) {
      try {
        await navigator.share({
          title: 'My Tourist Safety ID',
          text: `Tourist ID: ${qrData.name} - ${qrData.destination}`,
          url: window.location.href
        });
        toast.success('QR code shared successfully');
      } catch (error) {
        toast.info('Share cancelled');
      }
    } else {
      copyToClipboard();
    }
  };

  if (loading) {
    return <Loading message="Generating your digital ID..." size="medium" />;
  }

  if (!qrData) {
    return (
      <div className="container py-5">
        <div className="text-center">
          <h3 className="text-danger">Failed to generate QR code</h3>
          <button className="btn btn-primary" onClick={generateQRData}>
            Try Again
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="container-fluid py-4">
      <div className="row">
        <div className="col-12">
          <div className="card">
            <div className="card-header">
              <h3 className="mb-0">🆔 My Digital Tourist ID</h3>
              <p className="mb-0 mt-2">Your secure blockchain-verified digital identity</p>
            </div>
          </div>
        </div>
      </div>

      <div className="row mt-4">
        {/* QR Code Display */}
        <div className="col-md-6">
          <div className="card h-100">
            <div className="card-header bg-primary text-white">
              <h5 className="mb-0">📱 Scannable QR Code</h5>
            </div>
            <div className="card-body text-center">
              <div className="mb-4">
                <div className="p-4 bg-white d-inline-block rounded shadow">
                  <QRCodeSVG
                    id="tourist-qr-code"
                    value={JSON.stringify(qrData)}
                    size={280}
                    level="H"
                    includeMargin={true}
                    bgColor="#ffffff"
                    fgColor="#000000"
                  />
                </div>
              </div>

              <div className="mb-4">
                <h6 className="text-primary">{qrData.name}</h6>
                <p className="text-muted mb-2">Tourist ID: {qrData.id}</p>
                <div className="d-flex justify-content-center">
                  <span className="badge bg-success me-2">✓ Verified</span>
                  <span className="badge bg-info">🛡️ Safety Score: {qrData.safetyScore}%</span>
                </div>
              </div>

              {/* Download Buttons */}
              <div className="d-grid gap-2">
                <button 
                  className="btn btn-primary"
                  onClick={() => downloadQR('png')}
                >
                  📥 Download as PNG
                </button>
                <div className="row">
                  <div className="col-6">
                    <button 
                      className="btn btn-outline-primary w-100"
                      onClick={() => downloadQR('svg')}
                    >
                      📄 SVG
                    </button>
                  </div>
                  <div className="col-6">
                    <button 
                      className="btn btn-outline-info w-100"
                      onClick={shareQR}
                    >
                      📤 Share
                    </button>
                  </div>
                </div>
                <button 
                  className="btn btn-outline-secondary"
                  onClick={copyToClipboard}
                >
                  📋 Copy Data
                </button>
              </div>
            </div>
          </div>
        </div>

        {/* Tourist Information */}
        <div className="col-md-6">
          <div className="card h-100">
            <div className="card-header bg-success text-white">
              <h5 className="mb-0">👤 Tourist Profile</h5>
            </div>
            <div className="card-body">
              <div className="mb-4">
                <h6 className="text-success mb-3">Personal Information</h6>
                <table className="table table-borderless">
                  <tbody>
                    <tr>
                      <td><strong>Full Name:</strong></td>
                      <td>{qrData.name}</td>
                    </tr>
                    <tr>
                      <td><strong>Tourist ID:</strong></td>
                      <td><code>{qrData.id}</code></td>
                    </tr>
                    <tr>
                      <td><strong>Email:</strong></td>
                      <td>{qrData.email}</td>
                    </tr>
                    <tr>
                      <td><strong>Phone:</strong></td>
                      <td>{qrData.phone}</td>
                    </tr>
                    <tr>
                      <td><strong>Destination:</strong></td>
                      <td>
                        <span className="badge bg-info">{qrData.destination}</span>
                      </td>
                    </tr>
                    <tr>
                      <td><strong>Emergency Contact:</strong></td>
                      <td className="text-danger">{qrData.emergencyContact}</td>
                    </tr>
                  </tbody>
                </table>
              </div>

              <div className="mb-4">
                <h6 className="text-success mb-3">Safety & Status</h6>
                <div className="row text-center">
                  <div className="col-6">
                    <div className="p-3 bg-light rounded">
                      <div className="h4 text-success mb-1">{qrData.safetyScore}%</div>
                      <div className="small text-muted">Safety Score</div>
                    </div>
                  </div>
                  <div className="col-6">
                    <div className="p-3 bg-light rounded">
                      <div className="h4 text-primary mb-1">✓</div>
                      <div className="small text-muted">Verified Status</div>
                    </div>
                  </div>
                </div>
              </div>

              <div>
                <h6 className="text-success mb-3">System Information</h6>
                <div className="small text-muted">
                  <div className="row">
                    <div className="col-6">
                      <p><strong>Generated:</strong><br />{new Date(qrData.timestamp).toLocaleString()}</p>
                      <p><strong>Version:</strong><br />{qrData.version}</p>
                    </div>
                    <div className="col-6">
                      <p><strong>Expires:</strong><br />{new Date(qrData.expiryDate).toLocaleDateString()}</p>
                      <p><strong>Issuer:</strong><br />{qrData.issuer}</p>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Blockchain Verification */}
      <div className="row mt-4">
        <div className="col-12">
          <div className="card">
            <div className="card-header">
              <div className="d-flex justify-content-between align-items-center">
                <h5 className="mb-0">🔗 Blockchain Verification</h5>
                <button 
                  className="btn btn-outline-primary btn-sm"
                  onClick={() => setShowBlockchain(!showBlockchain)}
                >
                  {showBlockchain ? '👁️ Hide' : '👁️ Show'} Hash
                </button>
              </div>
            </div>
            <div className="card-body">
              <div className="row align-items-center">
                <div className="col-md-8">
                  <h6 className="text-primary mb-2">🛡️ Secure Digital Identity</h6>
                  <p className="mb-2">
                    Your tourist ID is secured using blockchain technology, ensuring 
                    authenticity and preventing tampering.
                  </p>
                  {showBlockchain && (
                    <div className="mt-3">
                      <label className="form-label small">Blockchain Hash:</label>
                      <div className="bg-light p-3 rounded">
                        <code className="small text-break">{qrData.blockchainHash}</code>
                      </div>
                    </div>
                  )}
                </div>
                <div className="col-md-4 text-center">
                  <div className="display-1 text-success">🔐</div>
                  <div className="badge bg-success fs-6">BLOCKCHAIN VERIFIED</div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Usage Instructions */}
      <div className="row mt-4">
        <div className="col-md-6">
          <div className="card">
            <div className="card-header">
              <h5 className="mb-0">📖 How to Use Your QR Code</h5>
            </div>
            <div className="card-body">
              <div className="small">
                <h6 className="text-primary">📱 For Mobile Use:</h6>
                <ul>
                  <li>Save the QR code image to your phone</li>
                  <li>Take a screenshot for quick access</li>
                  <li>Show to police when requested</li>
                  <li>Use at tourist checkpoints</li>
                </ul>

                <h6 className="text-primary mt-3">🖨️ For Physical Copy:</h6>
                <ul>
                  <li>Print the QR code on paper</li>
                  <li>Laminate for durability</li>
                  <li>Keep with your travel documents</li>
                  <li>Make multiple copies</li>
                </ul>
              </div>
            </div>
          </div>
        </div>

        <div className="col-md-6">
          <div className="card">
            <div className="card-header">
              <h5 className="mb-0">🔒 Security Features</h5>
            </div>
            <div className="card-body">
              <div className="small">
                <h6 className="text-primary">✅ Built-in Security:</h6>
                <ul>
                  <li>Blockchain hash verification</li>
                  <li>Tamper-proof design</li>
                  <li>Time-stamped generation</li>
                  <li>Expiry date protection</li>
                </ul>

                <h6 className="text-primary mt-3">⚠️ Safety Tips:</h6>
                <ul>
                  <li>Don't share your QR code publicly</li>
                  <li>Regenerate if compromised</li>
                  <li>Keep emergency contacts updated</li>
                  <li>Report any suspicious scanning</li>
                </ul>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Actions */}
      <div className="row mt-4">
        <div className="col-12">
          <div className="card">
            <div className="card-header">
              <h5 className="mb-0">⚡ Quick Actions</h5>
            </div>
            <div className="card-body">
              <div className="row">
                <div className="col-md-3 mb-2">
                  <button 
                    className="btn btn-success w-100"
                    onClick={generateQRData}
                  >
                    🔄 Regenerate QR
                  </button>
                </div>
                <div className="col-md-3 mb-2">
                  <button 
                    className="btn btn-primary w-100"
                    onClick={() => toast.info('QR validation feature coming soon')}
                  >
                    ✅ Validate QR
                  </button>
                </div>
                <div className="col-md-3 mb-2">
                  <button 
                    className="btn btn-info w-100"
                    onClick={() => toast.info('Profile update feature coming soon')}
                  >
                    ✏️ Update Profile
                  </button>
                </div>
                <div className="col-md-3 mb-2">
                  <button 
                    className="btn btn-warning w-100"
                    onClick={() => toast.info('Emergency update sent to contacts')}
                  >
                    📞 Notify Contacts
                  </button>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* System Status */}
      <div className="row mt-4">
        <div className="col-12">
          <div className="alert alert-info">
            <h6 className="alert-heading">📡 Digital ID Status</h6>
            <div className="row">
              <div className="col-md-3">
                <strong>Status:</strong> <span className="badge bg-success">Active</span>
              </div>
              <div className="col-md-3">
                <strong>Last Updated:</strong> {new Date(qrData.timestamp).toLocaleTimeString()}
              </div>
              <div className="col-md-3">
                <strong>Expires:</strong> {new Date(qrData.expiryDate).toLocaleDateString()}
              </div>
              <div className="col-md-3">
                <strong>Blockchain:</strong> <span className="badge bg-success">Verified</span>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default QRDisplay;
