import React, { useEffect, useRef, useState } from 'react';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';
import 'leaflet.heat';
import './HeatMap.css';

// Fix for default markers in Leaflet with React
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: require('leaflet/dist/images/marker-icon-2x.png'),
  iconUrl: require('leaflet/dist/images/marker-icon.png'),
  shadowUrl: require('leaflet/dist/images/marker-shadow.png'),
});

const HeatMap = () => {
  const mapRef = useRef(null);
  const mapInstance = useRef(null);
  const heatmapLayer = useRef(null);
  const markersGroup = useRef(null);
  const [viewMode, setViewMode] = useState('density');
  const [timeOfDay, setTimeOfDay] = useState('current');
  const [selectedZone, setSelectedZone] = useState(null);
  const [isLoading, setIsLoading] = useState(false);

  // Delhi tourist zones with real GPS coordinates
  const delhiTouristZones = [
    {
      id: 'zone-1',
      name: 'India Gate Area',
      location: 'Central Delhi',
      coordinates: [28.6129, 77.2295],
      tourists: 85,
      intensity: 0.95,
      risk: 'low',
      activity: 'high',
      details: {
        active: 78,
        offline: 5,
        alerts: 2,
        popularTimes: { morning: 65, afternoon: 85, evening: 95, night: 25 },
        attractions: ['India Gate', 'Rajpath', 'National War Memorial'],
        avgVisitTime: '2.5 hours',
        crowdLevel: 'Very High',
        safetyRating: 4.5
      }
    },
    {
      id: 'zone-2',
      name: 'Red Fort Complex',
      location: 'Old Delhi',
      coordinates: [28.6562, 77.2410],
      tourists: 72,
      intensity: 0.85,
      risk: 'medium',
      activity: 'high',
      details: {
        active: 65,
        offline: 4,
        alerts: 3,
        popularTimes: { morning: 72, afternoon: 68, evening: 45, night: 15 },
        attractions: ['Red Fort', 'Chandni Chowk', 'Jama Masjid'],
        avgVisitTime: '3 hours',
        crowdLevel: 'High',
        safetyRating: 4.0
      }
    },
    {
      id: 'zone-3',
      name: 'Connaught Place',
      location: 'Central Delhi',
      coordinates: [28.6315, 77.2167],
      tourists: 64,
      intensity: 0.78,
      risk: 'low',
      activity: 'very-high',
      details: {
        active: 60,
        offline: 2,
        alerts: 2,
        popularTimes: { morning: 45, afternoon: 64, evening: 78, night: 52 },
        attractions: ['Connaught Place', 'Janpath Market', 'Palika Bazaar'],
        avgVisitTime: '2 hours',
        crowdLevel: 'Very High',
        safetyRating: 4.8
      }
    },
    {
      id: 'zone-4',
      name: 'Qutub Minar Area',
      location: 'South Delhi',
      coordinates: [28.5245, 77.1855],
      tourists: 58,
      intensity: 0.70,
      risk: 'low',
      activity: 'medium',
      details: {
        active: 54,
        offline: 3,
        alerts: 1,
        popularTimes: { morning: 58, afternoon: 52, evening: 38, night: 8 },
        attractions: ['Qutub Minar', 'Iron Pillar', 'Mehrauli Archaeological Park'],
        avgVisitTime: '2.5 hours',
        crowdLevel: 'High',
        safetyRating: 4.6
      }
    },
    {
      id: 'zone-5',
      name: 'Lotus Temple Zone',
      location: 'South Delhi',
      coordinates: [28.5535, 77.2588],
      tourists: 48,
      intensity: 0.62,
      risk: 'low',
      activity: 'medium',
      details: {
        active: 45,
        offline: 2,
        alerts: 1,
        popularTimes: { morning: 48, afternoon: 42, evening: 35, night: 12 },
        attractions: ['Lotus Temple', 'Kalkaji Temple', 'Okhla Bird Sanctuary'],
        avgVisitTime: '1.5 hours',
        crowdLevel: 'Medium',
        safetyRating: 4.7
      }
    },
    {
      id: 'zone-6',
      name: 'Humayun\'s Tomb Area',
      location: 'Central Delhi',
      coordinates: [28.5933, 77.2507],
      tourists: 42,
      intensity: 0.55,
      risk: 'low',
      activity: 'medium',
      details: {
        active: 39,
        offline: 2,
        alerts: 1,
        popularTimes: { morning: 42, afternoon: 38, evening: 28, night: 5 },
        attractions: ['Humayun\'s Tomb', 'Nizamuddin Dargah', 'Khan-i-Khanan Tomb'],
        avgVisitTime: '2 hours',
        crowdLevel: 'Medium',
        safetyRating: 4.4
      }
    },
    {
      id: 'zone-7',
      name: 'Akshardham Complex',
      location: 'East Delhi',
      coordinates: [28.6127, 77.2773],
      tourists: 55,
      intensity: 0.68,
      risk: 'low',
      activity: 'high',
      details: {
        active: 52,
        offline: 2,
        alerts: 1,
        popularTimes: { morning: 35, afternoon: 55, evening: 48, night: 0 },
        attractions: ['Akshardham Temple', 'Yamuna Sports Complex'],
        avgVisitTime: '4 hours',
        crowdLevel: 'High',
        safetyRating: 4.9
      }
    },
    {
      id: 'zone-8',
      name: 'Lodhi Gardens Area',
      location: 'Central Delhi',
      coordinates: [28.5918, 77.2219],
      tourists: 38,
      intensity: 0.48,
      risk: 'low',
      activity: 'medium',
      details: {
        active: 36,
        offline: 1,
        alerts: 1,
        popularTimes: { morning: 45, afternoon: 32, evening: 38, night: 15 },
        attractions: ['Lodhi Gardens', 'Safdarjung Tomb', 'India International Centre'],
        avgVisitTime: '1.5 hours',
        crowdLevel: 'Medium',
        safetyRating: 4.5
      }
    },
    {
      id: 'zone-9',
      name: 'Karol Bagh Market',
      location: 'Central Delhi',
      coordinates: [28.6519, 77.1909],
      tourists: 45,
      intensity: 0.58,
      risk: 'medium',
      activity: 'high',
      details: {
        active: 42,
        offline: 2,
        alerts: 1,
        popularTimes: { morning: 25, afternoon: 45, evening: 52, night: 28 },
        attractions: ['Karol Bagh Market', 'Gaffar Market', 'Jhandewalan Temple'],
        avgVisitTime: '2.5 hours',
        crowdLevel: 'High',
        safetyRating: 4.2
      }
    },
    {
      id: 'zone-10',
      name: 'Khan Market Area',
      location: 'Central Delhi',
      coordinates: [28.5983, 77.2319],
      tourists: 32,
      intensity: 0.42,
      risk: 'low',
      activity: 'medium',
      details: {
        active: 30,
        offline: 1,
        alerts: 1,
        popularTimes: { morning: 18, afternoon: 32, evening: 38, night: 22 },
        attractions: ['Khan Market', 'Lodi Colony', 'IIC'],
        avgVisitTime: '2 hours',
        crowdLevel: 'Medium',
        safetyRating: 4.6
      }
    }
  ];

  // Generate dummy individual tourist points for more realistic heat distribution
  const generateDummyTouristPoints = () => {
    let points = [];
    
    delhiTouristZones.forEach(zone => {
      const currentTourists = getTimeBasedData(zone);
      const intensity = getIntensityForMode(zone, currentTourists);
      
      // Add main zone point
      points.push([zone.coordinates[0], zone.coordinates[1], intensity]);
      
      // Add surrounding random points for realistic heat spread
      const numPoints = Math.floor(currentTourists / 3);
      for (let i = 0; i < numPoints; i++) {
        const offsetLat = (Math.random() - 0.5) * 0.02; // ~1km radius
        const offsetLng = (Math.random() - 0.5) * 0.02;
        const randomIntensity = intensity * (0.4 + Math.random() * 0.6);
        
        points.push([
          zone.coordinates[0] + offsetLat,
          zone.coordinates[1] + offsetLng,
          randomIntensity
        ]);
      }
    });
    
    return points;
  };

  const getTimeBasedData = (zone) => {
    const currentHour = new Date().getHours();
    let timeKey;
    if (currentHour >= 6 && currentHour < 12) timeKey = 'morning';
    else if (currentHour >= 12 && currentHour < 18) timeKey = 'afternoon';
    else if (currentHour >= 18 && currentHour < 22) timeKey = 'evening';
    else timeKey = 'night';
    
    if (timeOfDay === 'current') {
      return zone.details.popularTimes[timeKey];
    }
    return zone.details.popularTimes[timeOfDay] || zone.tourists;
  };

  const getIntensityForMode = (zone, tourists) => {
    switch (viewMode) {
      case 'risk':
        if (zone.risk === 'high') return 1.0;
        if (zone.risk === 'medium') return 0.6;
        return 0.3;
      case 'activity':
        if (zone.activity === 'very-high') return 1.0;
        if (zone.activity === 'high') return 0.8;
        if (zone.activity === 'medium') return 0.5;
        return 0.2;
      default: // density
        return Math.min(tourists / 100, 1.0);
    }
  };

  const getHeatmapOptions = () => {
    const baseOptions = {
      maxZoom: 15,
      max: 1.0,
      minOpacity: 0.1
    };

    switch (viewMode) {
      case 'risk':
        return {
          ...baseOptions,
          radius: 30,
          blur: 20,
          gradient: {
            0.0: '#2ecc71',  // Low risk - Green
            0.5: '#f39c12',  // Medium risk - Orange
            1.0: '#e74c3c'   // High risk - Red
          }
        };
      case 'activity':
        return {
          ...baseOptions,
          radius: 25,
          blur: 15,
          gradient: {
            0.0: '#95a5a6',  // Low activity - Gray
            0.3: '#1abc9c',  // Medium activity - Teal
            0.6: '#3498db',  // High activity - Blue
            1.0: '#8e44ad'   // Very high activity - Purple
          }
        };
      default: // density
        return {
          ...baseOptions,
          radius: 35,
          blur: 25,
          gradient: {
            0.0: '#4caf50',  // Very low - Green
            0.2: '#ffeb3b',  // Low - Yellow
            0.4: '#ff9800',  // Medium - Orange
            0.6: '#ff5722',  // High - Red-Orange
            0.8: '#f44336',  // Very high - Red
            1.0: '#d32f2f'   // Extreme - Dark Red
          }
        };
    }
  };

  // Initialize map
  useEffect(() => {
    if (!mapInstance.current && mapRef.current) {
      setIsLoading(true);
      
      // Create map
      mapInstance.current = L.map(mapRef.current, {
        center: [28.6139, 77.2090], // Delhi center
        zoom: 11,
        zoomControl: true,
        scrollWheelZoom: true,
        attributionControl: true
      });

      // Add tile layer (OpenStreetMap)
      L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
        attribution: '¬© <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors | Tourist Safety Monitoring System',
        maxZoom: 18,
        tileSize: 256
      }).addTo(mapInstance.current);

      // Create markers group
      markersGroup.current = L.layerGroup().addTo(mapInstance.current);

      // Add zone markers with popups
      delhiTouristZones.forEach(zone => {
        const marker = L.marker(zone.coordinates, {
          title: zone.name
        });

        const popupContent = `
          <div class="zone-popup">
            <h3>${zone.name}</h3>
            <p><strong>üìç Location:</strong> ${zone.location}</p>
            <p><strong>üë• Current Tourists:</strong> ${getTimeBasedData(zone)}</p>
            <p><strong>‚ö†Ô∏è Risk Level:</strong> <span class="risk-${zone.risk}">${zone.risk.toUpperCase()}</span></p>
            <p><strong>‚≠ê Safety Rating:</strong> ${'‚≠ê'.repeat(Math.floor(zone.details.safetyRating))} ${zone.details.safetyRating}/5</p>
            <p><strong>üéØ Key Attractions:</strong></p>
            <ul class="attraction-list">
              ${zone.details.attractions.map(attraction => `<li>${attraction}</li>`).join('')}
            </ul>
            <div class="popup-actions">
              <button class="popup-btn" onclick="window.selectZone('${zone.id}')">View Details</button>
            </div>
          </div>
        `;

        marker.bindPopup(popupContent, {
          maxWidth: 300,
          className: 'custom-popup'
        });

        markersGroup.current.addLayer(marker);
      });

      // Global function for popup actions
      window.selectZone = (zoneId) => {
        const zone = delhiTouristZones.find(z => z.id === zoneId);
        if (zone) {
          setSelectedZone(zone);
          mapInstance.current.closePopup();
        }
      };

      setIsLoading(false);
    }

    return () => {
      if (mapInstance.current) {
        mapInstance.current.remove();
        mapInstance.current = null;
      }
      if (window.selectZone) {
        delete window.selectZone;
      }
    };
  }, []);

  // Update heatmap when view mode or time changes
  useEffect(() => {
    if (mapInstance.current) {
      // Remove existing heatmap layer
      if (heatmapLayer.current) {
        mapInstance.current.removeLayer(heatmapLayer.current);
      }

      // Generate new heatmap data
      const heatmapData = generateDummyTouristPoints();
      const options = getHeatmapOptions();

      // Create new heatmap layer
      heatmapLayer.current = L.heatLayer(heatmapData, options);
      mapInstance.current.addLayer(heatmapLayer.current);
    }
  }, [viewMode, timeOfDay]);

  const getTotalStats = () => {
    return delhiTouristZones.reduce((acc, zone) => ({
      total: acc.total + zone.tourists,
      active: acc.active + zone.details.active,
      offline: acc.offline + zone.details.offline,
      alerts: acc.alerts + zone.details.alerts
    }), { total: 0, active: 0, offline: 0, alerts: 0 });
  };

  const refreshData = () => {
    setIsLoading(true);
    // Simulate data refresh
    setTimeout(() => {
      // Regenerate heatmap with slight variations
      const heatmapData = generateDummyTouristPoints();
      const options = getHeatmapOptions();
      
      if (heatmapLayer.current && mapInstance.current) {
        mapInstance.current.removeLayer(heatmapLayer.current);
        heatmapLayer.current = L.heatLayer(heatmapData, options);
        mapInstance.current.addLayer(heatmapLayer.current);
      }
      
      setIsLoading(false);
    }, 1000);
  };

  const stats = getTotalStats();

  return (
    <div className="leaflet-heatmap-container">
      {/* Header Controls */}
      <div className="leaflet-heatmap-header">
        <div className="header-content">
          <div className="title-section">
            <h2>üó∫Ô∏è Tourist Safety Monitoring - Live Heatmap</h2>
            <p>Real-time interactive tourist distribution mapping across Delhi</p>
          </div>
          
          <div className="view-controls">
            <div className="control-group">
              <label>View Mode:</label>
              <select 
                value={viewMode} 
                onChange={(e) => setViewMode(e.target.value)}
                className="view-selector"
              >
                <option value="density">üî• Tourist Density</option>
                <option value="risk">‚ö†Ô∏è Risk Assessment</option>
                <option value="activity">üìä Activity Level</option>
              </select>
            </div>
            
            <div className="control-group">
              <label>Time Period:</label>
              <select 
                value={timeOfDay} 
                onChange={(e) => setTimeOfDay(e.target.value)}
                className="view-selector"
              >
                <option value="current">üïê Current Time</option>
                <option value="morning">üåÖ Morning (6-12)</option>
                <option value="afternoon">‚òÄÔ∏è Afternoon (12-18)</option>
                <option value="evening">üåÜ Evening (18-22)</option>
                <option value="night">üåô Night (22-6)</option>
              </select>
            </div>

            <button 
              className="refresh-btn"
              onClick={refreshData}
              disabled={isLoading}
            >
              {isLoading ? 'üîÑ Refreshing...' : 'üîÑ Refresh Data'}
            </button>
          </div>
        </div>
      </div>

      <div className="leaflet-content">
        {/* Map Container */}
        <div className="map-container">
          <div 
            ref={mapRef} 
            className="leaflet-map"
            style={{ height: '600px', width: '100%' }}
          />
          {isLoading && (
            <div className="map-loader">
              <div className="loader-spinner"></div>
              <p>Loading map data...</p>
            </div>
          )}
        </div>

        {/* Info Panel */}
        <div className="info-panel">
          <div className="stats-overview">
            <h3>üìä Live Statistics</h3>
            <div className="stats-grid">
              <div className="stat-item total">
                <span className="stat-icon">üë•</span>
                <span className="stat-value">{stats.total}</span>
                <span className="stat-label">Total Tourists</span>
              </div>
              <div className="stat-item active">
                <span className="stat-icon">‚úÖ</span>
                <span className="stat-value">{stats.active}</span>
                <span className="stat-label">Currently Active</span>
              </div>
              <div className="stat-item offline">
                <span className="stat-icon">üì¥</span>
                <span className="stat-value">{stats.offline}</span>
                <span className="stat-label">Offline</span>
              </div>
              <div className="stat-item alerts">
                <span className="stat-icon">üö®</span>
                <span className="stat-value">{stats.alerts}</span>
                <span className="stat-label">Active Alerts</span>
              </div>
            </div>
          </div>

          {selectedZone && (
            <div className="zone-details">
              <h3>üìç {selectedZone.name}</h3>
              <p className="zone-location">{selectedZone.location}</p>
              
              <div className="zone-stats">
                <div className="stat-row">
                  <span className="stat-label">Current Tourists:</span>
                  <span className="stat-value-inline">{getTimeBasedData(selectedZone)}</span>
                </div>
                <div className="stat-row">
                  <span className="stat-label">Safety Rating:</span>
                  <span className="stat-value-inline">{'‚≠ê'.repeat(Math.floor(selectedZone.details.safetyRating))} {selectedZone.details.safetyRating}/5</span>
                </div>
                <div className="stat-row">
                  <span className="stat-label">Risk Level:</span>
                  <span className={`stat-badge risk-${selectedZone.risk}`}>{selectedZone.risk.toUpperCase()}</span>
                </div>
                <div className="stat-row">
                  <span className="stat-label">Crowd Level:</span>
                  <span className="stat-value-inline">{selectedZone.details.crowdLevel}</span>
                </div>
                <div className="stat-row">
                  <span className="stat-label">Avg Visit Time:</span>
                  <span className="stat-value-inline">{selectedZone.details.avgVisitTime}</span>
                </div>
              </div>

              <div className="popular-times">
                <h5>Popular Times</h5>
                <div className="time-bars">
                  {Object.entries(selectedZone.details.popularTimes).map(([time, value]) => (
                    <div key={time} className="time-bar">
                      <span className="time-label">{time.charAt(0).toUpperCase() + time.slice(1)}:</span>
                      <div className="bar-container">
                        <div 
                          className="bar-fill" 
                          style={{ width: `${(value / 100) * 100}%` }}
                        ></div>
                        <span className="bar-value">{value}</span>
                      </div>
                    </div>
                  ))}
                </div>
              </div>

              <div className="attractions">
                <h5>Key Attractions:</h5>
                <div className="attraction-tags">
                  {selectedZone.details.attractions.map((attraction, index) => (
                    <span key={index} className="attraction-tag">{attraction}</span>
                  ))}
                </div>
              </div>

              <div className="zone-actions">
                <button className="action-btn primary">üìû Contact Zone</button>
                <button className="action-btn secondary" onClick={() => setSelectedZone(null)}>‚úñ Close</button>
              </div>
            </div>
          )}

          <div className="update-info">
            <div className="update-header">
              <h4>üïê System Status</h4>
            </div>
            <div className="update-details">
              <p><strong>Last Update:</strong> {new Date().toLocaleTimeString()}</p>
              <p><strong>Auto-refresh:</strong> Every 30 seconds</p>
              <p><strong>View Mode:</strong> {viewMode.charAt(0).toUpperCase() + viewMode.slice(1)}</p>
              <p><strong>Time Period:</strong> {timeOfDay === 'current' ? 'Live Data' : timeOfDay.charAt(0).toUpperCase() + timeOfDay.slice(1)}</p>
              <p><strong>Total Zones:</strong> {delhiTouristZones.length} monitored</p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default HeatMap;
