import React, { useEffect, useRef, useState } from 'react';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css'; // MUST be first import
import './Map.css';

// Fix Leaflet default markers
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: require('leaflet/dist/images/marker-icon-2x.png'),
  iconUrl: require('leaflet/dist/images/marker-icon.png'),
  shadowUrl: require('leaflet/dist/images/marker-shadow.png'),
});

const Map = () => {
  const mapRef = useRef(null);
  const mapInstance = useRef(null);
  const zonesLayer = useRef(null);
  const markersGroup = useRef(null);
  const [selectedZone, setSelectedZone] = useState(null);
  const [activeFilter, setActiveFilter] = useState('all');
  const [viewType, setViewType] = useState('attraction');
  const [showLegend, setShowLegend] = useState(true);
  const [showAttractions, setShowAttractions] = useState(true);

  // Your existing data arrays here (touristZones, safetyZones)...
  const touristZones = [
    // ... your existing tourist zones data
  ];
  
  const safetyZones = [
    // ... your existing safety zones data
  ];

  // Initialize map with proper sizing
  useEffect(() => {
    if (!mapInstance.current && mapRef.current) {
      // Create map with explicit dimensions
      mapInstance.current = L.map(mapRef.current, {
        center: [28.6139, 77.2090],
        zoom: 11,
        minZoom: 9,
        maxZoom: 16,
        preferCanvas: true, // Better performance
      });

      // Add tile layer
      L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
        attribution: 'Â© OpenStreetMap contributors',
        maxZoom: 18,
      }).addTo(mapInstance.current);

      // Initialize layers
      zonesLayer.current = L.layerGroup().addTo(mapInstance.current);
      markersGroup.current = L.layerGroup().addTo(mapInstance.current);

      // Force map resize after initialization
      setTimeout(() => {
        if (mapInstance.current) {
          mapInstance.current.invalidateSize();
          addZones();
          if (showAttractions && viewType === 'attraction') {
            addAttractionMarkers();
          }
        }
      }, 100);
    }

    return () => {
      if (mapInstance.current) {
        mapInstance.current.remove();
        mapInstance.current = null;
      }
    };
  }, []);

  // Force resize when container changes
  useEffect(() => {
    const handleResize = () => {
      if (mapInstance.current) {
        setTimeout(() => {
          mapInstance.current.invalidateSize();
        }, 100);
      }
    };

    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  // Your existing functions (addZones, addAttractionMarkers, etc.)...
  const addZones = () => {
    // Your existing addZones implementation
  };

  const addAttractionMarkers = () => {
    // Your existing addAttractionMarkers implementation
  };

  // ... other existing functions

  return (
    <div className="tourist-map-container">
      {/* Header */}
      <div className="map-header">
        <div className="header-content">
          <div className="title-section">
            <h2>ðºï¸ Delhi Tourist Zone Explorer</h2>
            <p>Discover Delhi's attractions and safety information with interactive maps</p>
          </div>
          
          <div className="map-controls">
            <div className="view-type-controls">
              <label>MAP TYPE:</label>
              <div className="view-type-buttons">
                <button 
                  className={`view-type-btn ${viewType === 'attraction' ? 'active' : ''}`}
                  onClick={() => setViewType('attraction')}
                >
                  <span className="btn-icon">ð¯</span>
                  <span className="btn-text">Tourist Attractions</span>
                </button>
                <button 
                  className={`view-type-btn ${viewType === 'safety' ? 'active' : ''}`}
                  onClick={() => setViewType('safety')}
                >
                  <span className="btn-icon">ð¡ï¸</span>
                  <span className="btn-text">Safety Zones</span>
                </button>
              </div>
            </div>
            
            <div className="filter-controls">
              <label>FILTER:</label>
              <select 
                value={activeFilter} 
                onChange={(e) => setActiveFilter(e.target.value)}
                className="zone-filter"
              >
                <option value="all">ð All Zones</option>
                {/* Add your filter options */}
              </select>
            </div>
            
            <div className="toggle-controls">
              <button 
                className={`toggle-btn ${showLegend ? 'active' : ''}`}
                onClick={() => setShowLegend(!showLegend)}
              >
                ð Legend
              </button>
              <button 
                className={`toggle-btn ${showAttractions ? 'active' : ''}`}
                onClick={() => setShowAttractions(!showAttractions)}
              >
                ð Attractions
              </button>
            </div>
          </div>
        </div>
      </div>

      {/* Map Content */}
      <div className="map-content">
        <div className="map-container">
          <div 
            ref={mapRef} 
            className="leaflet-zone-map"
            style={{ height: '100%', width: '100%' }} // Explicit sizing
          />
          
          {/* Legend - conditionally rendered */}
          {showLegend && (
            <div className="zone-legend">
              <div className="legend-header">
                <h4>ð¯ Zone Guide</h4>
                <p>Click on colored zones to explore attractions</p>
              </div>
              {/* Legend content */}
            </div>
          )}
        </div>

        {/* Details Panel */}
        <div className="zone-details-panel">
          {selectedZone ? (
            <div className="zone-details-content">
              {/* Zone details content */}
            </div>
          ) : (
            <div className="no-zone-selected">
              <div className="no-zone-icon">ðºï¸</div>
              <h3>Explore Delhi's Tourist Zones</h3>
              <p>Click on any colored zone on the map to discover attractions, facilities, and travel tips.</p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Map;
