import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { toast } from 'react-toastify';
import { authService } from '../../services/auth';
import { touristAPI, mockData } from '../../services/api';
import { geolocationService } from '../../services/geolocation';
import Loading from '../common/Loading';
import './TouristHome.css'; // Import the CSS file

const TouristHome = () => {
  const [loading, setLoading] = useState(true);
  const [locationLoading, setLocationLoading] = useState(false);
  const [dashboardData, setDashboardData] = useState({
    safetyScore: 85,
    currentLocation: 'Loading...',
    lastCheckIn: new Date(),
    alertsCount: 0,
    nearbyTourists: 12,
    emergencyContacts: []
  });
  const [currentLocation, setCurrentLocation] = useState(null);
  const [weatherData, setWeatherData] = useState({
    temperature: '24Â°C',
    condition: 'Clear',
    humidity: '65%',
    visibility: 'Good'
  });

  const navigate = useNavigate();
  const userInfo = authService.getUserInfo();

  useEffect(() => {
    loadDashboardData();
    getCurrentLocation();
  }, []);

  const loadDashboardData = async () => {
    try {
      setLoading(true);
      await new Promise(resolve => setTimeout(resolve, 1000));

      setDashboardData({
        safetyScore: 85,
        currentLocation: 'India Gate, New Delhi',
        lastCheckIn: new Date(Date.now() - 30 * 60000),
        alertsCount: 0,
        nearbyTourists: 12,
        emergencyContacts: [
          { name: 'Emergency Services', number: '112' },
          { name: 'Tourist Helpline', number: '1363' },
          { name: 'Local Police', number: '100' }
        ],
        upcomingDestinations: ['Red Fort', 'Qutub Minar', 'Lotus Temple'],
        recentActivity: [
          { action: 'Safety check-in', time: '30 minutes ago', status: 'success' },
          { action: 'Location updated', time: '1 hour ago', status: 'info' },
          { action: 'Entered safe zone', time: '2 hours ago', status: 'success' }
        ]
      });
    } catch (error) {
      console.error('Error loading dashboard:', error);
      toast.error('Failed to load dashboard data');
    } finally {
      setLoading(false);
    }
  };

  const getCurrentLocation = async () => {
    try {
      setLocationLoading(true);
      const position = await geolocationService.getEnhancedPosition();
      setCurrentLocation(position);

      setDashboardData(prev => ({
        ...prev,
        currentLocation: position.address || `${position.lat.toFixed(4)}, ${position.lng.toFixed(4)}`
      }));
    } catch (error) {
      console.error('Location error:', error);
      setDashboardData(prev => ({
        ...prev,
        currentLocation: 'Location unavailable'
      }));
    } finally {
      setLocationLoading(false);
    }
  };

  const handleSafetyCheckIn = () => {
    toast.success('Safety check-in recorded! Your emergency contacts have been notified.');
    setDashboardData(prev => ({
      ...prev,
      lastCheckIn: new Date(),
      recentActivity: [
        { action: 'Safety check-in', time: 'Just now', status: 'success' },
        ...prev.recentActivity.slice(0, 2)
      ]
    }));
  };

  const handleEmergencySOS = () => {
    navigate('/tourist/sos');
  };

  const getSafetyScoreColor = (score) => {
    if (score >= 80) return 'success';
    if (score >= 60) return 'warning';
    return 'danger';
  };

  const getSafetyScoreText = (score) => {
    if (score >= 90) return 'Excellent';
    if (score >= 80) return 'Good';
    if (score >= 60) return 'Fair';
    return 'Needs Attention';
  };

  if (loading) {
    return <Loading message="Loading your safety dashboard..." size="large" />;
  }

  return (
    <div className="tourist-home-container">
      <div className="dashboard-content">
        {/* Welcome Header */}
        <div className="welcome-header">
          <div className="welcome-content">
            <div className="welcome-text">
              <h1 className="welcome-title">Welcome back, {userInfo?.name || 'John Doe'}! ð</h1>
              <p className="welcome-subtitle">Your safety is our priority. Stay connected and stay safe.</p>
              <div className="location-badge">
                <span className="location-pin">ð</span>
                <span className="location-text">{dashboardData.currentLocation}</span>
                {locationLoading && (
                  <div className="location-spinner">
                    <div className="spinner"></div>
                  </div>
                )}
              </div>
            </div>
            <div className="welcome-status">
              <div className="status-icon">ð¡ï¸</div>
              <div className="status-details">
                <div className="status-label">Last check-in</div>
                <div className="status-time">
                  {new Date(dashboardData.lastCheckIn).toLocaleTimeString()}
                </div>
              </div>
            </div>
          </div>
        </div>

        {/* Main Dashboard Stats */}
        <div className="dashboard-stats">
          {/* Safety Score */}
          <div className="stat-card safety-score-card">
            <div className="stat-header">
              <div className="stat-title">
                <span className="stat-icon">ð¡ï¸</span>
                <span>Safety Score</span>
              </div>
              <button 
                className="view-details-btn"
                onClick={() => navigate('/tourist/map')}
              >
                View Details
              </button>
            </div>
            <div className={`stat-number ${getSafetyScoreColor(dashboardData.safetyScore)}`}>
              {dashboardData.safetyScore}%
            </div>
            <div className="stat-label">{getSafetyScoreText(dashboardData.safetyScore)}</div>
            <div className="progress-container">
              <div className="progress-bar">
                <div 
                  className={`progress-fill ${getSafetyScoreColor(dashboardData.safetyScore)}`}
                  style={{ width: `${dashboardData.safetyScore}%` }}
                />
              </div>
            </div>
          </div>

          {/* Current Status */}
          <div className="stat-card status-card">
            <div className="stat-header">
              <div className="stat-title">
                <span className="stat-icon">ð</span>
                <span>Current Status</span>
              </div>
              <span className="status-badge active">Active</span>
            </div>
            <div className="stat-number success">Safe</div>
            <div className="stat-label">You're in a safe zone</div>
            <div className="status-details-list">
              <div className="status-item">ð¢ GPS: Active</div>
              <div className="status-item">ð± Connection: Strong</div>
            </div>
          </div>

          {/* Alerts */}
          <div className="stat-card alerts-card">
            <div className="stat-header">
              <div className="stat-title">
                <span className="stat-icon">ð¨</span>
                <span>Active Alerts</span>
              </div>
              <button 
                className="view-all-btn"
                onClick={() => toast.info('No active alerts')}
              >
                View All
              </button>
            </div>
            <div className="stat-number info">{dashboardData.alertsCount}</div>
            <div className="stat-label">No current alerts</div>
            <div className="all-normal">â All systems normal</div>
          </div>

          {/* Nearby Tourists */}
          <div className="stat-card community-card">
            <div className="stat-header">
              <div className="stat-title">
                <span className="stat-icon">ð¥</span>
                <span>Community</span>
              </div>
              <span className="community-badge">{dashboardData.nearbyTourists}</span>
            </div>
            <div className="stat-number primary">{dashboardData.nearbyTourists}</div>
            <div className="stat-label">Tourists nearby</div>
            <div className="community-radius">Within 2km radius</div>
          </div>
        </div>

        <div className="dashboard-sections">
          {/* Quick Actions */}
          <div className="section-card quick-actions">
            <div className="section-header">
              <h3 className="section-title">â¡ Quick Actions</h3>
            </div>
            <div className="section-body">
              <div className="actions-grid">
                <button 
                  className="action-btn emergency"
                  onClick={handleEmergencySOS}
                >
                  <span className="action-icon">ð</span>
                  <span className="action-text">Emergency SOS</span>
                </button>
                <button 
                  className="action-btn success"
                  onClick={handleSafetyCheckIn}
                >
                  <span className="action-icon">â</span>
                  <span className="action-text">Safety Check-in</span>
                </button>
                <button 
                  className="action-btn primary"
                  onClick={() => navigate('/tourist/map')}
                >
                  <span className="action-icon">ðºï¸</span>
                  <span className="action-text">Safety Map</span>
                </button>
                <button 
                  className="action-btn info"
                  onClick={() => navigate('/tourist/qr')}
                >
                  <span className="action-icon">ð</span>
                  <span className="action-text">My Digital ID</span>
                </button>
              </div>
              
              <div className="location-update">
                <button 
                  className="location-btn"
                  onClick={getCurrentLocation}
                  disabled={locationLoading}
                >
                  {locationLoading ? (
                    <>
                      <span className="btn-spinner"></span>
