import React, { useEffect, useRef, useState } from 'react';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';
import './Map.css';

// Fix for default markers in Leaflet with React
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: require('leaflet/dist/images/marker-icon-2x.png'),
  iconUrl: require('leaflet/dist/images/marker-icon.png'),
  shadowUrl: require('leaflet/dist/images/marker-shadow.png'),
});

const Map = () => {
  const mapRef = useRef(null);
  const mapInstance = useRef(null);
  const zonesLayer = useRef(null);
  const markersGroup = useRef(null);
  const [selectedZone, setSelectedZone] = useState(null);
  const [activeFilter, setActiveFilter] = useState('all');
  const [viewType, setViewType] = useState('attraction');
  const [showLegend, setShowLegend] = useState(true);
  const [showAttractions, setShowAttractions] = useState(true);
  const [showStatistics, setShowStatistics] = useState(false); // NEW: Statistics state

  // Your existing touristZones and safetyZones data here...
  const touristZones = [
    {
      id: 'heritage-zone',
      name: 'Heritage Zone (Old Delhi)',
      type: 'heritage',
      color: '#8B4513',
      description: 'Historical monuments and cultural sites in Old Delhi',
      safetyRating: 4.2,
      crowdLevel: 'High',
      bestTime: 'Morning & Evening',
      avgDuration: '4-6 hours',
      coordinates: [
        [28.6700, 77.2200], [28.6750, 77.2450], [28.6600, 77.2500], 
        [28.6500, 77.2400], [28.6550, 77.2150], [28.6700, 77.2200]
      ],
      center: [28.6562, 77.2410],
      attractions: [
        { name: 'Red Fort', type: 'UNESCO World Heritage', rating: 4.6, timings: '6:00 AM - 6:00 PM', visitors: '25000/day' },
        { name: 'Jama Masjid', type: 'Mosque', rating: 4.4, timings: '7:00 AM - 12:00 PM, 1:30 PM - 6:30 PM', visitors: '15000/day' },
        { name: 'Chandni Chowk', type: 'Market', rating: 4.2, timings: '10:00 AM - 8:00 PM', visitors: '50000/day' },
        { name: 'Raj Ghat', type: 'Memorial', rating: 4.3, timings: '6:00 AM - 6:00 PM', visitors: '8000/day' }
      ],
      facilities: ['Metro Station', 'Parking', 'Food Courts', 'Tourist Police', 'ATMs', 'Restrooms'],
      transportation: ['Delhi Metro (Red Line)', 'Bus Routes: 101, 102, 103', 'Auto Rickshaws'],
      tips: ['Visit early morning to avoid crowds', 'Carry water and wear comfortable shoes'],
      nearbyHotels: 8,
      restaurants: 25
    },
    // Add other zones with similar structure...
    {
      id: 'central-delhi-zone',
      name: 'Central Delhi Zone',
      type: 'modern',
      color: '#4169E1',
      description: 'Government area and modern attractions',
      safetyRating: 4.8,
      crowdLevel: 'Very High',
      bestTime: 'All Day',
      avgDuration: '3-5 hours',
      coordinates: [
        [28.6000, 77.1900], [28.6300, 77.1950], [28.6350, 77.2300], 
        [28.6200, 77.2350], [28.5950, 77.2100], [28.6000, 77.1900]
      ],
      center: [28.6129, 77.2295],
      attractions: [
        { name: 'India Gate', type: 'War Memorial', rating: 4.7, timings: '24 hours', visitors: '30000/day' },
        { name: 'Rashtrapati Bhavan', type: 'Presidential Palace', rating: 4.6, timings: 'By appointment', visitors: '500/day' },
        { name: 'Parliament House', type: 'Government Building', rating: 4.4, timings: 'Restricted access', visitors: '200/day' }
      ],
      facilities: ['Metro Stations', 'Security', 'Restaurants', 'Government Buildings'],
      transportation: ['Delhi Metro (Central Line)', 'Bus Routes: 201, 202, 203'],
      tips: ['Security checks at government buildings', 'Great for evening walks around India Gate'],
      nearbyHotels: 15,
      restaurants: 20
    }
  ];

  const safetyZones = [
    // Your existing safety zones data...
    {
      id: 'safe-zone-1',
      name: 'South Delhi Safe Zone',
      type: 'safe',
      color: '#00C851',
      description: 'Well-patrolled upscale residential and commercial areas with excellent safety record',
      safetyLevel: 'Very Safe',
      riskFactors: 'Minimal',
      policePresence: 'High',
      lightingQuality: 'Excellent',
      crimeStat: 'Very Low',
      recommendedTime: '24/7 Safe',
      coordinates: [
        [28.5400, 77.2000], [28.5700, 77.2100], [28.5800, 77.2400], 
        [28.5600, 77.2500], [28.5300, 77.2300], [28.5400, 77.2000]
      ],
      center: [28.5550, 77.2250],
      safetyFeatures: [
        'CCTV surveillance network',
        '24/7 police patrolling',
        'Well-lit streets and parks'
      ],
      emergencyContacts: [
        'Tourist Police: 1363',
        'Medical Emergency: 102'
      ],
      tips: [
        'Ideal for solo travelers and families',
        'Safe for evening walks'
      ]
    }
  ];

  // NEW: Calculate Statistics
  const calculateStatistics = () => {
    const zones = viewType === 'attraction' ? touristZones : safetyZones;
    
    if (viewType === 'attraction') {
      const totalAttractions = zones.reduce((sum, zone) => sum + zone.attractions.length, 0);
      const totalVisitors = zones.reduce((sum, zone) => 
        sum + zone.attractions.reduce((zoneSum, attraction) => 
          zoneSum + parseInt(attraction.visitors?.replace(/\D/g, '') || '0'), 0), 0);
      const avgSafety = (zones.reduce((sum, zone) => sum + zone.safetyRating, 0) / zones.length).toFixed(1);
      const totalHotels = zones.reduce((sum, zone) => sum + (zone.nearbyHotels || 0), 0);
      const totalRestaurants = zones.reduce((sum, zone) => sum + (zone.restaurants || 0), 0);
      
      return {
        totalZones: zones.length,
        totalAttractions,
        dailyVisitors: totalVisitors,
        avgSafetyRating: avgSafety,
        totalHotels,
        totalRestaurants,
        heritageZones: zones.filter(z => z.type === 'heritage').length,
        modernZones: zones.filter(z => z.type === 'modern').length,
        spiritualZones: zones.filter(z => z.type === 'spiritual').length,
        natureZones: zones.filter(z => z.type === 'nature').length
      };
    } else {
      const safeZones = zones.filter(z => z.type === 'safe').length;
      const moderateZones = zones.filter(z => z.type === 'moderate').length;
      const riskyZones = zones.filter(z => z.type === 'risky').length;
      
      return {
        totalZones: zones.length,
        safeZones,
        moderateZones,
        riskyZones,
        totalEmergencyContacts: zones.reduce((sum, zone) => sum + (zone.emergencyContacts?.length || 0), 0),
        safetyFeatures: zones.reduce((sum, zone) => sum + (zone.safetyFeatures?.length || 0), 0)
      };
    }
  };

  // Your existing useEffect hooks and functions...
  useEffect(() => {
    if (!mapInstance.current && mapRef.current) {
      mapInstance.current = L.map(mapRef.current, {
        center: [28.6139, 77.2090],
        zoom: 11,
        minZoom: 9,
        maxZoom: 16,
        zoomControl: true,
        scrollWheelZoom: true,
        attributionControl: true,
        renderer: L.svg()
      });

      L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
        attribution: 'Â© OpenStreetMap contributors',
        maxZoom: 18
      }).addTo(mapInstance.current);

      zonesLayer.current = L.layerGroup().addTo(mapInstance.current);
      markersGroup.current = L.layerGroup().addTo(mapInstance.current);

      setTimeout(() => {
        if (mapInstance.current) {
          mapInstance.current.invalidateSize();
          addZones();
          if (showAttractions && viewType === 'attraction') {
            addAttractionMarkers();
          }
        }
      }, 200);
    }

    return () => {
      if (mapInstance.current) {
        mapInstance.current.remove();
        mapInstance.current = null;
      }
    };
  }, []);

  useEffect(() => {
    if (zonesLayer.current) {
      zonesLayer.current.clearLayers();
      addZones();
    }
    if (markersGroup.current) {
      markersGroup.current.clearLayers();
      if (showAttractions && viewType === 'attraction') {
        addAttractionMarkers();
      }
    }
  }, [activeFilter, viewType, showAttractions]);

  const addZones = () => {
    if (!zonesLayer.current) return;

    const zones = viewType === 'attraction' ? touristZones : safetyZones;
    const filteredZones = activeFilter === 'all' ? zones : zones.filter(zone => zone.type === activeFilter);

    filteredZones.forEach(zone => {
      const polygon = L.polygon(zone.coordinates, {
        color: zone.color,
        fillColor: zone.color,
        fillOpacity: viewType === 'safety' ? 0.5 : 0.4,
        weight: viewType === 'safety' ? 4 : 3,
        opacity: 0.8,
        interactive: true
      });

      polygon.on('click', function(e) {
        setSelectedZone(zone);
        setShowStatistics(false); // Hide statistics when zone is selected
        mapInstance.current.fitBounds(polygon.getBounds(), { padding: [20, 20] });
      });

      zonesLayer.current.addLayer(polygon);
    });
  };

  const addAttractionMarkers = () => {
    if (!markersGroup.current || viewType !== 'attraction') return;
    
    markersGroup.current.clearLayers();
    const filteredZones = activeFilter === 'all' ? touristZones : touristZones.filter(zone => zone.type === activeFilter);
    
    filteredZones.forEach(zone => {
      zone.attractions.forEach((attraction, index) => {
        const center = zone.center;
        const offsetLat = (Math.random() - 0.5) * 0.008;
        const offsetLng = (Math.random() - 0.5) * 0.008;
        
        const marker = L.marker([center[0] + offsetLat, center[1] + offsetLng], {
          title: attraction.name
        });

        markersGroup.current.addLayer(marker);
      });
    });
  };

  const filterZones = (type) => {
    setActiveFilter(type);
  };

  const getFilteredZones = () => {
    const zones = viewType === 'attraction' ? touristZones : safetyZones;
    return activeFilter === 'all' ? zones : zones.filter(zone => zone.type === activeFilter);
  };

  const getFilterOptions = () => {
    if (viewType === 'attraction') {
      return [
        { value: 'all', label: 'ðŸŒ All Zones' },
        { value: 'heritage', label: 'ðŸ›ï¸ Heritage' },
        { value: 'modern', label: 'ðŸ™ï¸ Modern' },
        { value: 'spiritual', label: 'ðŸ•‰ï¸ Spiritual' },
        { value: 'nature', label: 'ðŸŒ³ Nature' }
      ];
    } else {
      return [
        { value: 'all', label: 'ðŸŒ All Safety Zones' },
        { value: 'safe', label: 'ðŸ›¡ï¸ Safe Areas' },
        { value: 'moderate', label: 'âš ï¸ Moderate Areas' },
        { value: 'crowdy', label: 'ðŸ‘¥ Crowded Areas' },
        { value: 'risky', label: 'ðŸš¨ High Alert Areas' }
      ];
    }
  };

  const statistics = calculateStatistics();
  const filterOptions = getFilterOptions();

  return (
    <div className="tourist-map-container">
      {/* ENHANCED Header with better space utilization */}
      <div className="map-header">
        <div className="header-left">
          <h2>ðŸ—ºï¸ Delhi Tourist Zone Explorer</h2>
          <p>Discover Delhi's attractions and safety information with interactive maps</p>
        </div>
        
        <div className="header-right">
          <div className="control-group">
            <div className="map-type-section">
              <label>Map Type:</label>
              <div className="view-type-buttons">
                <button 
                  className={`view-type-btn ${viewType === 'attraction' ? 'active' : ''}`}
                  onClick={() => setViewType('attraction')}
                >
                  <span className="btn-icon">ðŸŽ¯</span>
                  <span className="btn-text">Tourist Attractions</span>
                </button>
                <button 
                  className={`view-type-btn ${viewType === 'safety' ? 'active' : ''}`}
                  onClick={() => setViewType('safety')}
                >
                  <span className="btn-icon">ðŸ›¡ï¸</span>
                  <span className="btn-text">Safety Zones</span>
                </button>
              </div>
            </div>
            
            <div className="filter-section">
              <label>Filter:</label>
              <select 
                value={activeFilter} 
                onChange={(e) => filterZones(e.target.value)}
                className="zone-filter"
              >
                {filterOptions.map(option => (
                  <option key={option.value} value={option.value}>
                    {option.label}
                  </option>
                ))}
              </select>
            </div>
            
            <div className="action-buttons">
              <button 
                className={`toggle-btn ${showLegend ? 'active' : ''}`}
                onClick={() => setShowLegend(!showLegend)}
              >
                ðŸ“‹ Legend
              </button>
              {viewType === 'attraction' && (
                <button 
                  className={`toggle-btn ${showAttractions ? 'active' : ''}`}
                  onClick={() => setShowAttractions(!showAttractions)}
                >
                  ðŸ“ Attractions
                </button>
              )}
              {/* NEW: Statistics Button */}
              <button 
                className={`toggle-btn stats-btn ${showStatistics ? 'active' : ''}`}
                onClick={() => {
                  setShowStatistics(!showStatistics);
                  setSelectedZone(null); // Clear selected zone when showing stats
                }}
              >
                ðŸ“Š Statistics
              </button>
            </div>
          </div>
        </div>
      </div>

      <div className="map-content">
        {/* Map Container */}
        <div className="map-container">
          <div 
            ref={mapRef} 
            className="leaflet-zone-map"
            style={{ height: '100%', width: '100%', minHeight: '600px' }}
          />
          
          {/* Legend */}
          {showLegend && (
            <div className="zone-legend">
              <div className="legend-header">
                <h4>{viewType === 'safety' ? 'ðŸ›¡ï¸ Safety Guide' : 'ðŸŽ¯ Zone Guide'}</h4>
                <p>{viewType === 'safety' ? 'Safety information' : 'Click zones to explore'}</p>
              </div>
              <div className="legend-zones">
                {getFilteredZones().map((zone) => (
                  <div 
                    key={zone.id} 
                    className="legend-zone-item"
                    onClick={() => {
                      const polygon = L.polygon(zone.coordinates);
                      mapInstance.current.fitBounds(polygon.getBounds(), { padding: [20, 20] });
                      setSelectedZone(zone);
                      setShowStatistics(false);
                    }}
                  >
                    <div 
                      className="zone-color-indicator" 
                      style={{ backgroundColor: zone.color }}
                    ></div>
                    <div className="zone-info">
                      <span className="zone-name">{zone.name}</span>
                      {viewType === 'safety' ? (
                        <span className="safety-level">{zone.safetyLevel}</span>
                      ) : (
                        <span className="zone-attractions">{zone.attractions.length} attractions</span>
                      )}
                    </div>
                  </div>
                ))}
              </div>
            </div>
          )}
        </div>

        {/* Enhanced Details/Statistics Panel */}
        <div className="zone-details-panel">
          {/* NEW: Statistics View */}
          {showStatistics ? (
            <div className="statistics-content">
              <div className="statistics-header">
                <h3>ðŸ“Š Delhi Tourism Statistics</h3>
                <button 
                  className="close-details-btn"
                  onClick={() => setShowStatistics(false)}
                >
                  âœ–
                </button>
              </div>
              
              <div className="statistics-body">
                {viewType === 'attraction' ? (
                  <>
                    <div className="stats-overview">
                      <div className="stats-grid">
                        <div className="stat-card primary">
                          <div className="stat-icon">ðŸ—ºï¸</div>
                          <div className="stat-content">
                            <span className="stat-number">{statistics.totalZones}</span>
                            <span className="stat-label">Tourist Zones</span>
                          </div>
                        </div>
                        
                        <div className="stat-card secondary">
                          <div className="stat-icon">ðŸŽ¯</div>
                          <div className="stat-content">
                            <span className="stat-number">{statistics.totalAttractions}</span>
                            <span className="stat-label">Attractions</span>
                          </div>
                        </div>
                        
                        <div className="stat-card accent">
                          <div className="stat-icon">ðŸ‘¥</div>
                          <div className="stat-content">
                            <span className="stat-number">{Math.round(statistics.dailyVisitors / 1000)}K</span>
                            <span className="stat-label">Daily Visitors</span>
                          </div>
                        </div>
                        
                        <div className="stat-card success">
                          <div className="stat-icon">ðŸ›¡ï¸</div>
                          <div className="stat-content">
                            <span className="stat-number">{statistics.avgSafetyRating}</span>
                            <span className="stat-label">Avg Safety</span>
                          </div>
                        </div>
                      </div>
                    </div>

                    <div className="stats-details">
                      <div className="stats-section">
                        <h4>ðŸ¨ Infrastructure</h4>
                        <div className="stats-list">
                          <div className="stats-item">
                            <span className="stats-icon">ðŸ¨</span>
                            <span className="stats-text">Hotels: {statistics.totalHotels}</span>
                          </div>
                          <div className="stats-item">
                            <span className="stats-icon">ðŸ½ï¸</span>
                            <span className="stats-text">Restaurants: {statistics.totalRestaurants}</span>
                          </div>
                        </div>
                      </div>

                      <div className="stats-section">
                        <h4>ðŸ›ï¸ Zone Distribution</h4>
                        <div className="zone-distribution">
                          <div className="zone-stat">
                            <div className="zone-bar heritage" style={{width: `${(statistics.heritageZones / statistics.totalZones) * 100}%`}}></div>
                            <span>Heritage ({statistics.heritageZones})</span>
                          </div>
                          <div className="zone-stat">
                            <div className="zone-bar modern" style={{width: `${(statistics.modernZones / statistics.totalZones) * 100}%`}}></div>
                            <span>Modern ({statistics.modernZones})</span>
                          </div>
                          {statistics.spiritualZones > 0 && (
                            <div className="zone-stat">
                              <div className="zone-bar spiritual" style={{width: `${(statistics.spiritualZones / statistics.totalZones) * 100}%`}}></div>
                              <span>Spiritual ({statistics.spiritualZones})</span>
                            </div>
                          )}
                        </div>
                      </div>
                    </div>
                  </>
                ) : (
                  // Safety Statistics
                  <>
                    <div className="stats-overview">
                      <div className="stats-grid">
                        <div className="stat-card primary">
                          <div className="stat-icon">ðŸ›¡ï¸</div>
                          <div className="stat-content">
                            <span className="stat-number">{statistics.safeZones}</span>
                            <span className="stat-label">Safe Zones</span>
                          </div>
                        </div>
                        
                        <div className="stat-card warning">
                          <div className="stat-icon">âš ï¸</div>
                          <div className="stat-content">
                            <span className="stat-number">{statistics.moderateZones}</span>
                            <span className="stat-label">Moderate Zones</span>
                          </div>
                        </div>
                        
                        <div className="stat-card danger">
                          <div className="stat-icon">ðŸš¨</div>
                          <div className="stat-content">
                            <span className="stat-number">{statistics.riskyZones}</span>
                            <span className="stat-label">High Alert</span>
                          </div>
                        </div>
                        
                        <div className="stat-card info">
                          <div className="stat-icon">ðŸ“ž</div>
                          <div className="stat-content">
                            <span className="stat-number">{statistics.totalEmergencyContacts}</span>
                            <span className="stat-label">Emergency Contacts</span>
                          </div>
                        </div>
                      </div>
                    </div>

                    <div className="stats-details">
                      <div className="stats-section">
                        <h4>ðŸš¨ Safety Resources</h4>
                        <div className="stats-list">
                          <div className="stats-item">
                            <span className="stats-icon">ðŸ›¡ï¸</span>
                            <span className="stats-text">Safety Features: {statistics.safetyFeatures}</span>
                          </div>
                          <div className="stats-item">
                            <span className="stats-icon">ðŸ“ž</span>
                            <span className="stats-text">Emergency Hotlines: {statistics.totalEmergencyContacts}</span>
                          </div>
                        </div>
                      </div>
                    </div>
                  </>
                )}
              </div>
            </div>
          ) : selectedZone ? (
            // Zone Details (existing code)
            <div className="zone-details-content">
              <div className="zone-details-header" style={{ backgroundColor: selectedZone.color }}>
                <h3>{selectedZone.name}</h3>
                <button 
                  className="close-details-btn"
                  onClick={() => setSelectedZone(null)}
                >
                  âœ–
                </button>
              </div>
              
              <div className="zone-details-body">
                <p className="zone-description">{selectedZone.description}</p>
                
                {/* Zone content display logic */}
                {viewType === 'attraction' ? (
                  <>
                    <div className="attractions-section">
                      <h4>ðŸŽ¯ Top Attractions</h4>
                      <div className="attractions-list">
                        {selectedZone.attractions.map((attraction, index) => (
                          <div key={index} className="attraction-item">
                            <div className="attraction-main">
                              <h5>{attraction.name}</h5>
                              <span className="attraction-rating">{attraction.rating}â­</span>
                            </div>
                            <p className="attraction-type">{attraction.type}</p>
                            <p className="attraction-timings">ðŸ• {attraction.timings}</p>
                          </div>
                        ))}
                      </div>
                    </div>
                    
                    <div className="facilities-section">
                      <h4>ðŸ¢ Facilities</h4>
                      <div className="facilities-tags">
                        {selectedZone.facilities.map((facility, index) => (
                          <span key={index} className="facility-tag">{facility}</span>
                        ))}
                      </div>
                    </div>
                  </>
                ) : (
                  <>
                    <div className="safety-features-section">
                      <h4>ðŸ›¡ï¸ Safety Features</h4>
                      <div className="features-list">
                        {selectedZone.safetyFeatures.map((feature, index) => (
                          <div key={index} className="feature-item">
                            <span className="feature-bullet">âœ“</span>
                            <span>{feature}</span>
                          </div>
                        ))}
                      </div>
                    </div>
                  </>
                )}
              </div>
            </div>
          ) : (
            // Default Empty State
            <div className="no-zone-selected">
              <div className="no-zone-icon">ðŸ—ºï¸</div>
              <h3>Explore Delhi's {viewType === 'safety' ? 'Safety Zones' : 'Tourist Zones'}</h3>
              <p>Click on any colored zone on the map to discover detailed information, or view comprehensive statistics about Delhi's zones.</p>
              
              <div className="action-buttons">
                <button 
                  className="action-button primary"
                  onClick={() => setShowStatistics(true)}
                >
                  ðŸ“Š View Statistics
                </button>
                <button 
                  className="action-button secondary"
                  onClick={() => setShowLegend(!showLegend)}
                >
                  ðŸ“‹ {showLegend ? 'Hide' : 'Show'} Legend
                </button>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Map;
