import React, { useEffect, useRef, useState } from 'react';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';
import './TouristZoneMap.css';

// Fix for default markers in Leaflet with React
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: require('leaflet/dist/images/marker-icon-2x.png'),
  iconUrl: require('leaflet/dist/images/marker-icon.png'),
  shadowUrl: require('leaflet/dist/images/marker-shadow.png'),
});

const Map = () => {
  const mapRef = useRef(null);
  const mapInstance = useRef(null);
  const zonesLayer = useRef(null);
  const markersGroup = useRef(null);
  const [selectedZone, setSelectedZone] = useState(null);
  const [activeFilter, setActiveFilter] = useState('all');
  const [showLegend, setShowLegend] = useState(true);
  const [showAttractions, setShowAttractions] = useState(true);

  // Delhi Tourist Zones with polygon coordinates and detailed information
  const touristZones = [
    {
      id: 'heritage-zone',
      name: 'Heritage Zone',
      type: 'heritage',
      color: '#8B4513',
      description: 'Historical monuments and cultural sites',
      safetyRating: 4.5,
      crowdLevel: 'High',
      bestTime: 'Morning & Evening',
      avgDuration: '4-6 hours',
      coordinates: [
        [28.6600, 77.2300], [28.6650, 77.2450], [28.6500, 77.2500], 
        [28.6400, 77.2400], [28.6450, 77.2250], [28.6600, 77.2300]
      ],
      attractions: [
        { name: 'Red Fort', type: 'UNESCO World Heritage', rating: 4.6, timings: '6:00 AM - 6:00 PM' },
        { name: 'Jama Masjid', type: 'Mosque', rating: 4.4, timings: '7:00 AM - 12:00 PM, 1:30 PM - 6:30 PM' },
        { name: 'Chandni Chowk', type: 'Market', rating: 4.2, timings: '10:00 AM - 8:00 PM' },
        { name: 'Raj Ghat', type: 'Memorial', rating: 4.3, timings: '6:00 AM - 6:00 PM' }
      ],
      facilities: ['Metro Station', 'Parking', 'Food Courts', 'Tourist Police', 'ATMs', 'Restrooms'],
      transportation: ['Delhi Metro (Red Line)', 'Bus Routes: 101, 102, 103', 'Auto Rickshaws', 'Cycle Rickshaws'],
      tips: [
        'Visit early morning to avoid crowds',
        'Carry water and wear comfortable shoes',
        'Bargain while shopping at Chandni Chowk',
        'Respect religious customs at Jama Masjid'
      ]
    },
    {
      id: 'modern-delhi-zone',
      name: 'Modern Delhi Zone',
      type: 'modern',
      color: '#4169E1',
      description: 'Contemporary attractions and business districts',
      safetyRating: 4.8,
      crowdLevel: 'Very High',
      bestTime: 'All Day',
      avgDuration: '3-5 hours',
      coordinates: [
        [28.6200, 77.2000], [28.6400, 77.2100], [28.6350, 77.2300], 
        [28.6250, 77.2350], [28.6100, 77.2200], [28.6200, 77.2000]
      ],
      attractions: [
        { name: 'India Gate', type: 'War Memorial', rating: 4.7, timings: '24 hours' },
        { name: 'Connaught Place', type: 'Shopping District', rating: 4.5, timings: '10:00 AM - 10:00 PM' },
        { name: 'National Museum', type: 'Museum', rating: 4.3, timings: '10:00 AM - 6:00 PM (Closed Monday)' },
        { name: 'Rashtrapati Bhavan', type: 'Presidential Palace', rating: 4.6, timings: 'By appointment only' }
      ],
      facilities: ['Metro Stations', 'Luxury Hotels', 'Restaurants', 'Shopping Malls', 'Banks', 'Hospitals'],
      transportation: ['Delhi Metro (Blue, Yellow Lines)', 'Bus Routes: 201, 202, 203', 'Taxis', 'App Cabs'],
      tips: [
        'Best for photography during golden hour',
        'Multiple metro connectivity available',
        'Great for evening walks around India Gate',
        'Connaught Place offers diverse dining options'
      ]
    },
    {
      id: 'south-delhi-zone',
      name: 'South Delhi Heritage Zone',
      type: 'heritage',
      color: '#FF6347',
      description: 'Ancient monuments and gardens in South Delhi',
      safetyRating: 4.4,
      crowdLevel: 'Medium',
      bestTime: 'Morning & Late Afternoon',
      avgDuration: '4-6 hours',
      coordinates: [
        [28.5200, 77.1800], [28.5400, 77.1900], [28.5500, 77.2100], 
        [28.5300, 77.2200], [28.5100, 77.2000], [28.5200, 77.1800]
      ],
      attractions: [
        { name: 'Qutub Minar', type: 'UNESCO World Heritage', rating: 4.5, timings: '7:00 AM - 5:00 PM' },
        { name: 'Mehrauli Archaeological Park', type: 'Archaeological Site', rating: 4.2, timings: '6:00 AM - 6:00 PM' },
        { name: 'Iron Pillar', type: 'Ancient Monument', rating: 4.1, timings: '7:00 AM - 5:00 PM' },
        { name: 'Tomb of Iltutmish', type: 'Historical Tomb', rating: 4.0, timings: '7:00 AM - 5:00 PM' }
      ],
      facilities: ['Parking', 'Cafeteria', 'Souvenir Shop', 'First Aid', 'Guide Services'],
      transportation: ['Delhi Metro (Yellow Line)', 'Bus Routes: 501, 502', 'Private Vehicles', 'Auto Rickshaws'],
      tips: [
        'Perfect for history enthusiasts',
        'Carry sun protection during summers',
        'Photography is allowed with ticket',
        'Guided tours available for better understanding'
      ]
    },
    {
      id: 'spiritual-zone',
      name: 'Spiritual Zone',
      type: 'spiritual',
      color: '#FFD700',
      description: 'Temples and spiritual centers',
      safetyRating: 4.7,
      crowdLevel: 'Medium',
      bestTime: 'Early Morning & Evening',
      avgDuration: '2-4 hours',
      coordinates: [
        [28.5500, 77.2500], [28.5700, 77.2700], [28.5600, 77.2800], 
        [28.5400, 77.2650], [28.5500, 77.2500]
      ],
      attractions: [
        { name: 'Lotus Temple', type: 'Baha\'i House of Worship', rating: 4.6, timings: '6:00 AM - 7:00 PM (Closed Monday)' },
        { name: 'ISKCON Temple', type: 'Hindu Temple', rating: 4.5, timings: '4:30 AM - 1:00 PM, 4:00 PM - 9:00 PM' },
        { name: 'Kalkaji Temple', type: 'Hindu Temple', rating: 4.4, timings: '5:00 AM - 12:00 AM' },
        { name: 'Birla Mandir', type: 'Hindu Temple', rating: 4.3, timings: '6:00 AM - 10:00 PM' }
      ],
      facilities: ['Meditation Halls', 'Parking', 'Shoe Storage', 'Pure Vegetarian Food', 'Bookstore'],
      transportation: ['Delhi Metro (Violet Line)', 'Bus Routes: 601, 602', 'Auto Rickshaws', 'E-Rickshaws'],
      tips: [
        'Maintain silence in meditation areas',
        'Remove shoes before entering temples',
        'Dress modestly and respectfully',
        'Free entry to most spiritual sites'
      ]
    },
    {
      id: 'garden-zone',
      name: 'Garden & Nature Zone',
      type: 'nature',
      color: '#32CD32',
      description: 'Parks, gardens and natural spaces',
      safetyRating: 4.6,
      crowdLevel: 'Low-Medium',
      bestTime: 'Early Morning & Evening',
      avgDuration: '2-3 hours',
      coordinates: [
        [28.5850, 77.2150], [28.6000, 77.2300], [28.5950, 77.2400], 
        [28.5800, 77.2350], [28.5850, 77.2150]
      ],
      attractions: [
        { name: 'Lodhi Gardens', type: 'Historical Garden', rating: 4.5, timings: '6:00 AM - 8:00 PM' },
        { name: 'Humayun\'s Tomb', type: 'UNESCO World Heritage', rating: 4.4, timings: '6:00 AM - 6:00 PM' },
        { name: 'Deer Park', type: 'Urban Forest', rating: 4.2, timings: '5:30 AM - 6:30 PM' },
        { name: 'Rose Garden', type: 'Botanical Garden', rating: 4.1, timings: '5:00 AM - 8:00 PM' }
      ],
      facilities: ['Walking Trails', 'Exercise Equipment', 'Children\'s Play Area', 'Cafeteria', 'Parking'],
      transportation: ['Delhi Metro (Violet Line)', 'Bus Routes: 701, 702', 'Private Vehicles', 'Cycling Paths'],
      tips: [
        'Perfect for morning jogs and yoga',
        'Great for family picnics',
        'Bird watching opportunities available',
        'Photography paradise during spring'
      ]
    },
    {
      id: 'cultural-zone',
      name: 'Cultural Zone',
      type: 'cultural',
      color: '#9932CC',
      description: 'Museums, galleries and cultural centers',
      safetyRating: 4.5,
      crowdLevel: 'Medium',
      bestTime: 'Morning & Afternoon',
      avgDuration: '3-5 hours',
      coordinates: [
        [28.6100, 77.2650], [28.6250, 77.2800], [28.6200, 77.2900], 
        [28.6050, 77.2750], [28.6100, 77.2650]
      ],
      attractions: [
        { name: 'Akshardham Temple', type: 'Cultural Complex', rating: 4.8, timings: '9:30 AM - 6:30 PM (Closed Monday)' },
        { name: 'National Science Centre', type: 'Science Museum', rating: 4.3, timings: '9:30 AM - 5:30 PM (Closed Monday)' },
        { name: 'Crafts Museum', type: 'Handicrafts Museum', rating: 4.2, timings: '10:00 AM - 5:00 PM (Closed Monday)' },
        { name: 'Purana Qila', type: 'Archaeological Museum', rating: 4.1, timings: '7:00 AM - 5:00 PM' }
      ],
      facilities: ['Audio Guides', 'Cafeteria', 'Gift Shop', 'Wheelchair Access', 'Parking', 'Lockers'],
      transportation: ['Delhi Metro (Blue Line)', 'Bus Routes: 801, 802', 'Auto Rickshaws', 'Taxis'],
      tips: [
        'Book tickets online to avoid queues',
        'Audio guides enhance the experience',
        'Photography restrictions may apply',
        'Educational programs available for groups'
      ]
    }
  ];

  // Color scheme for different zone types
  const zoneColors = {
    heritage: '#8B4513',
    modern: '#4169E1',
    spiritual: '#FFD700',
    nature: '#32CD32',
    cultural: '#9932CC'
  };

  // Initialize map
  useEffect(() => {
    if (!mapInstance.current && mapRef.current) {
      // Create map centered on Delhi
      mapInstance.current = L.map(mapRef.current, {
        center: [28.6139, 77.2090],
        zoom: 11,
        zoomControl: true,
        scrollWheelZoom: true,
        attributionControl: true
      });

      // Add beautiful tile layer
      L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
        attribution: '© OpenStreetMap contributors | Delhi Tourist Zone Map',
        maxZoom: 18,
        minZoom: 10
      }).addTo(mapInstance.current);

      // Create layers for zones and markers
      zonesLayer.current = L.layerGroup().addTo(mapInstance.current);
      markersGroup.current = L.layerGroup().addTo(mapInstance.current);

      // Add zone polygons
      addZonePolygons();
      
      // Add attraction markers
      if (showAttractions) {
        addAttractionMarkers();
      }
    }

    return () => {
      if (mapInstance.current) {
        mapInstance.current.remove();
        mapInstance.current = null;
      }
    };
  }, []);

  // Update attractions visibility
  useEffect(() => {
    if (mapInstance.current) {
      if (showAttractions) {
        addAttractionMarkers();
      } else {
        markersGroup.current.clearLayers();
      }
    }
  }, [showAttractions]);

  // Add zone polygons to map
  const addZonePolygons = () => {
    if (!zonesLayer.current) return;

    touristZones.forEach(zone => {
      if (activeFilter === 'all' || activeFilter === zone.type) {
        const polygon = L.polygon(zone.coordinates, {
          color: zone.color,
          fillColor: zone.color,
          fillOpacity: 0.3,
          weight: 3,
          opacity: 0.8,
          interactive: true
        });

        polygon.on('mouseover', function(e) {
          this.setStyle({
            fillOpacity: 0.5,
            weight: 4
          });
        });

        polygon.on('mouseout', function(e) {
          this.setStyle({
            fillOpacity: 0.3,
            weight: 3
          });
        });

        polygon.on('click', function(e) {
          setSelectedZone(zone);
          mapInstance.current.fitBounds(polygon.getBounds());
        });

        // Create comprehensive popup content
        const popupContent = `
          <div class="zone-popup-content">
            <div class="popup-header" style="background: ${zone.color}">
              <h3>${zone.name}</h3>
              <span class="zone-type">${zone.type.charAt(0).toUpperCase() + zone.type.slice(1)}</span>
            </div>
            <div class="popup-body">
              <p class="zone-description">${zone.description}</p>
              
              <div class="zone-metrics">
                <div class="metric">
                  <span class="metric-icon">🛡️</span>
                  <span class="metric-label">Safety:</span>
                  <span class="metric-value">${zone.safetyRating}/5</span>
                </div>
                <div class="metric">
                  <span class="metric-icon">👥</span>
                  <span class="metric-label">Crowd:</span>
                  <span class="metric-value">${zone.crowdLevel}</span>
                </div>
                <div class="metric">
                  <span class="metric-icon">⏰</span>
                  <span class="metric-label">Best Time:</span>
                  <span class="metric-value">${zone.bestTime}</span>
                </div>
                <div class="metric">
                  <span class="metric-icon">🕐</span>
                  <span class="metric-label">Duration:</span>
                  <span class="metric-value">${zone.avgDuration}</span>
                </div>
              </div>
              
              <div class="top-attractions">
                <h4>🎯 Top Attractions:</h4>
                <ul>
                  ${zone.attractions.slice(0, 3).map(attraction => 
                    `<li><strong>${attraction.name}</strong> (${attraction.rating}⭐)</li>`
                  ).join('')}
                </ul>
              </div>
              
              <div class="popup-actions">
                <button class="detail-btn" onclick="window.viewZoneDetails('${zone.id}')">
                  📋 View Full Details
                </button>
              </div>
            </div>
          </div>
        `;

        polygon.bindPopup(popupContent, {
          maxWidth: 350,
          className: 'custom-zone-popup'
        });

        zonesLayer.current.addLayer(polygon);
      }
    });

    // Global function for popup actions
    window.viewZoneDetails = (zoneId) => {
      const zone = touristZones.find(z => z.id === zoneId);
      if (zone) {
        setSelectedZone(zone);
        mapInstance.current.closePopup();
      }
    };
  };

  // Add attraction markers
  const addAttractionMarkers = () => {
    if (!markersGroup.current) return;
    
    markersGroup.current.clearLayers();
    
    touristZones.forEach(zone => {
      if (activeFilter === 'all' || activeFilter === zone.type) {
        zone.attractions.forEach((attraction, index) => {
          // Calculate random position within zone bounds
          const bounds = L.polygon(zone.coordinates).getBounds();
          const center = bounds.getCenter();
          const offsetLat = (Math.random() - 0.5) * 0.01;
          const offsetLng = (Math.random() - 0.5) * 0.01;
          
          const marker = L.marker([center.lat + offsetLat, center.lng + offsetLng], {
            title: attraction.name
          });

          const markerPopup = `
            <div class="attraction-popup">
              <h4>${attraction.name}</h4>
              <p><strong>Type:</strong> ${attraction.type}</p>
              <p><strong>Rating:</strong> ${attraction.rating}⭐</p>
              <p><strong>Timings:</strong> ${attraction.timings}</p>
              <p><strong>Zone:</strong> ${zone.name}</p>
            </div>
          `;

          marker.bindPopup(markerPopup, {
            className: 'attraction-popup-wrapper'
          });

          markersGroup.current.addLayer(marker);
        });
      }
    });
  };

  // Filter zones by type
  const filterZones = (type) => {
    setActiveFilter(type);
    zonesLayer.current.clearLayers();
    addZonePolygons();
    if (showAttractions) {
      addAttractionMarkers();
    }
  };

  // Get filtered zones for legend
  const getFilteredZones = () => {
    return activeFilter === 'all' ? 
      touristZones : 
      touristZones.filter(zone => zone.type === activeFilter);
  };

  return (
    <div className="tourist-zone-map-container">
      {/* Header Controls */}
      <div className="map-header">
        <div className="header-content">
          <div className="title-section">
            <h2>🗺️ Delhi Tourist Zone Explorer</h2>
            <p>Discover Delhi's attractions organized by themed zones with interactive maps</p>
          </div>
          
          <div className="map-controls">
            <div class="filter-controls">
              <label>Filter by Zone Type:</label>
              <select 
                value={activeFilter} 
                onChange={(e) => filterZones(e.target.value)}
                className="zone-filter"
              >
                <option value="all">🌍 All Zones</option>
                <option value="heritage">🏛️ Heritage</option>
                <option value="modern">🏙️ Modern</option>
                <option value="spiritual">🕉️ Spiritual</option>
                <option value="nature">🌳 Nature</option>
                <option value="cultural">🎭 Cultural</option>
              </select>
            </div>
            
            <div className="toggle-controls">
              <button 
                className={`toggle-btn ${showLegend ? 'active' : ''}`}
                onClick={() => setShowLegend(!showLegend)}
              >
                📋 Legend
              </button>
              <button 
                className={`toggle-btn ${showAttractions ? 'active' : ''}`}
                onClick={() => setShowAttractions(!showAttractions)}
              >
                📍 Attractions
              </button>
            </div>
          </div>
        </div>
      </div>

      <div className="map-content">
        {/* Map Container */}
        <div className="map-container">
          <div 
            ref={mapRef} 
            className="leaflet-zone-map"
            style={{ height: '100%', width: '100%' }}
          />
          
          {/* Zone Legend Overlay */}
          {showLegend && (
            <div className="zone-legend">
              <div className="legend-header">
                <h4>🎯 Zone Guide</h4>
                <p>Click on colored zones to explore attractions</p>
              </div>
              <div className="legend-zones">
                {getFilteredZones().map((zone, index) => (
                  <div key={zone.id} className="legend-zone-item">
                    <div 
                      className="zone-color-indicator" 
                      style={{ backgroundColor: zone.color }}
                    ></div>
                    <div className="zone-info">
                      <span className="zone-name">{zone.name}</span>
                      <span className="zone-attractions">{zone.attractions.length} attractions</span>
                      <span className="zone-rating">🛡️ {zone.safetyRating}/5</span>
                    </div>
                  </div>
                ))}
              </div>
              <div className="legend-footer">
                <small>💡 Hover zones for preview, click for details</small>
              </div>
            </div>
          )}
        </div>

        {/* Zone Details Panel */}
        <div className="zone-details-panel">
          {selectedZone ? (
            <div className="zone-details-content">
              <div className="zone-details-header" style={{ backgroundColor: selectedZone.color }}>
                <h3>{selectedZone.name}</h3>
                <button 
                  className="close-details-btn"
                  onClick={() => setSelectedZone(null)}
                >
                  ✖
                </button>
              </div>
              
              <div className="zone-details-body">
                <div className="zone-overview">
                  <p className="zone-description">{selectedZone.description}</p>
                  
                  <div className="zone-metrics-grid">
                    <div className="metric-card">
                      <span className="metric-icon">🛡️</span>
                      <div className="metric-content">
                        <span className="metric-value">{selectedZone.safetyRating}/5</span>
                        <span className="metric-label">Safety Rating</span>
                      </div>
                    </div>
                    <div className="metric-card">
                      <span className="metric-icon">👥</span>
                      <div className="metric-content">
                        <span className="metric-value">{selectedZone.crowdLevel}</span>
                        <span className="metric-label">Crowd Level</span>
                      </div>
                    </div>
                    <div className="metric-card">
                      <span className="metric-icon">⏰</span>
                      <div className="metric-content">
                        <span className="metric-value">{selectedZone.bestTime}</span>
                        <span className="metric-label">Best Time</span>
                      </div>
                    </div>
                    <div className="metric-card">
                      <span className="metric-icon">🕐</span>
                      <div className="metric-content">
                        <span className="metric-value">{selectedZone.avgDuration}</span>
                        <span className="metric-label">Average Duration</span>
                      </div>
                    </div>
                  </div>
                </div>
                
                <div className="attractions-section">
                  <h4>🎯 Top Attractions</h4>
                  <div className="attractions-list">
                    {selectedZone.attractions.map((attraction, index) => (
                      <div key={index} className="attraction-item">
                        <div className="attraction-main">
                          <h5>{attraction.name}</h5>
                          <span className="attraction-rating">{attraction.rating}⭐</span>
                        </div>
                        <p className="attraction-type">{attraction.type}</p>
                        <p className="attraction-timings">🕐 {attraction.timings}</p>
                      </div>
                    ))}
                  </div>
                </div>
                
                <div className="facilities-section">
                  <h4>🏢 Facilities Available</h4>
                  <div className="facilities-tags">
                    {selectedZone.facilities.map((facility, index) => (
                      <span key={index} className="facility-tag">{facility}</span>
                    ))}
                  </div>
                </div>
                
                <div className="transportation-section">
                  <h4>🚇 How to Reach</h4>
                  <div className="transportation-list">
                    {selectedZone.transportation.map((transport, index) => (
                      <div key={index} className="transport-item">
                        {transport}
                      </div>
                    ))}
                  </div>
                </div>
                
                <div className="tips-section">
                  <h4>💡 Tourist Tips</h4>
                  <div className="tips-list">
                    {selectedZone.tips.map((tip, index) => (
                      <div key={index} className="tip-item">
                        <span className="tip-bullet">•</span>
                        <span>{tip}</span>
                      </div>
                    ))}
                  </div>
                </div>
              </div>
            </div>
          ) : (
            <div className="no-zone-selected">
              <div className="no-zone-icon">🗺️</div>
              <h3>Explore Delhi's Tourist Zones</h3>
              <p>Click on any colored zone on the map to discover attractions, facilities, and travel tips for that area.</p>
              <div className="zone-types-grid">
                <div className="zone-type-card heritage" onClick={() => filterZones('heritage')}>
                  <span className="zone-type-icon">🏛️</span>
                  <span className="zone-type-name">Heritage</span>
                </div>
                <div className="zone-type-card modern" onClick={() => filterZones('modern')}>
                  <span className="zone-type-icon">🏙️</span>
                  <span className="zone-type-name">Modern</span>
                </div>
                <div className="zone-type-card spiritual" onClick={() => filterZones('spiritual')}>
                  <span className="zone-type-icon">🕉️</span>
                  <span className="zone-type-name">Spiritual</span>
                </div>
                <div className="zone-type-card nature" onClick={() => filterZones('nature')}>
                  <span className="zone-type-icon">🌳</span>
                  <span className="zone-type-name">Nature</span>
                </div>
                <div className="zone-type-card cultural" onClick={() => filterZones('cultural')}>
                  <span className="zone-type-icon">🎭</span>
                  <span className="zone-type-name">Cultural</span>
                </div>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Map;
