import React, { useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import { authService } from '../../services/auth';
import { toast } from 'react-toastify';

const Navbar = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const navigate = useNavigate();
  const location = useLocation();
  const userType = authService.getUserType();
  const isAuthenticated = authService.isAuthenticated();

  const handleLogout = () => {
    if (window.confirm('Are you sure you want to logout?')) {
      // Clear all authentication data
      authService.removeToken();
      toast.success('Logged out successfully');
      
      // Force redirect to login page
      navigate('/login');
      window.location.reload(); // Ensure clean state
    }
  };

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  const closeMenu = () => {
    setIsMenuOpen(false);
  };

  const handleNavigation = (path) => {
    navigate(path);
    closeMenu(); // Close menu after navigation
  };

  // Don't show navbar on login/register pages
  if (['/login', '/register'].includes(location.pathname)) {
    return null;
  }

  const isActivePath = (path) => {
    return location.pathname === path ? 'nav-link active' : 'nav-link';
  };

  const getUserDisplayName = () => {
    const userInfo = authService.getUserInfo();
    return userInfo?.name || (userType === 'tourist' ? 'Tourist User' : 'Officer Smith');
  };

  return (
    <>
      <nav className="navbar navbar-expand-lg navbar-light bg-white shadow-sm sticky-top">
        <div className="container-fluid">
          {/* Brand */}
          <a 
            className="navbar-brand d-flex align-items-center" 
            href="#" 
            onClick={(e) => { e.preventDefault(); handleNavigation('/'); }}
            style={{ cursor: 'pointer' }}
          >
            <span className="me-2" style={{ fontSize: '1.5rem' }}>üõ°Ô∏è</span>
            <div>
              <strong>Tourist Safety System</strong>
              <div className="small text-muted d-none d-md-block">Real-time Monitoring & Protection</div>
            </div>
          </a>
          
          {/* Mobile toggle button */}
          <button 
            className="navbar-toggler" 
            type="button" 
            onClick={toggleMenu}
            aria-controls="navbarNav" 
            aria-expanded={isMenuOpen}
            aria-label="Toggle navigation"
          >
            <span className="navbar-toggler-icon"></span>
          </button>
          
          <div className={`collapse navbar-collapse ${isMenuOpen ? 'show' : ''}`} id="navbarNav">
            {/* Navigation Links */}
            <ul className="navbar-nav me-auto">
              {isAuthenticated && userType === 'tourist' && (
                <>
                  <li className="nav-item">
                    <a 
                      className={isActivePath('/tourist/home')}
                      href="#" 
                      onClick={(e) => { e.preventDefault(); handleNavigation('/tourist/home'); }}
                    >
                      <span className="me-1">üè†</span> Home
                    </a>
                  </li>
                  <li className="nav-item">
                    <a 
                      className={isActivePath('/tourist/map')}
                      href="#" 
                      onClick={(e) => { e.preventDefault(); handleNavigation('/tourist/map'); }}
                    >
                      <span className="me-1">üó∫Ô∏è</span> Safety Map
                    </a>
                  </li>
                  <li className="nav-item">
                    <a 
                      className={isActivePath('/tourist/qr')}
                      href="#" 
                      onClick={(e) => { e.preventDefault(); handleNavigation('/tourist/qr'); }}
                    >
                      <span className="me-1">üÜî</span> My Digital ID
                    </a>
                  </li>
                  <li className="nav-item">
                    <a 
                      className={isActivePath('/tourist/safety-check')}
                      href="#" 
                      onClick={(e) => { e.preventDefault(); handleNavigation('/tourist/safety-check'); }}
                    >
                      <span className="me-1">‚úÖ</span> Safety Check
                    </a>
                  </li>
                  <li className="nav-item">
                    <a 
                      className={`${isActivePath('/tourist/sos')} text-danger`}
                      href="#" 
                      onClick={(e) => { e.preventDefault(); handleNavigation('/tourist/sos'); }}
                    >
                      <span className="me-1">üÜò</span> Emergency SOS
                    </a>
                  </li>
                </>
              )}
              
              {isAuthenticated && userType === 'police' && (
                <>
                  <li className="nav-item">
                    <a 
                      className={isActivePath('/police/dashboard')}
                      href="#" 
                      onClick={(e) => { e.preventDefault(); handleNavigation('/police/dashboard'); }}
                    >
                      <span className="me-1">üìä</span> Dashboard
                    </a>
                  </li>
                  <li className="nav-item">
                    <a 
                      className={isActivePath('/police/tourists')}
                      href="#" 
                      onClick={(e) => { e.preventDefault(); handleNavigation('/police/tourists'); }}
                    >
                      <span className="me-1">üë•</span> Tourists
                    </a>
                  </li>
                  <li className="nav-item">
                    <a 
                      className={isActivePath('/police/heatmap')}
                      href="#" 
                      onClick={(e) => { e.preventDefault(); handleNavigation('/police/heatmap'); }}
                    >
                      <span className="me-1">üó∫Ô∏è</span> Heatmap
                    </a>
                  </li>
                  <li className="nav-item">
                    <a 
                      className={isActivePath('/police/alerts')}
                      href="#" 
                      onClick={(e) => { e.preventDefault(); handleNavigation('/police/alerts'); }}
                    >
                      <span className="me-1">üö®</span> Alerts
                    </a>
                  </li>
                </>
              )}
            </ul>
            
            {/* Right side - User menu and actions */}
            {isAuthenticated && (
              <ul className="navbar-nav ms-auto">
                {/* System Status Indicator - Hidden on small screens */}
                <li className="nav-item me-3 d-none d-lg-block">
                  <div className="d-flex align-items-center">
                    <span className="badge bg-success me-2 pulse">‚óè</span>
                    <small className="text-muted">System Online</small>
                  </div>
                </li>
                
                {/* Emergency Button for Tourists - Always visible */}
                {userType === 'tourist' && (
                  <li className="nav-item me-3">
                    <button 
                      className="btn btn-danger btn-sm emergency-btn"
                      onClick={() => handleNavigation('/tourist/sos')}
                    >
                      üÜò Emergency
                    </button>
                  </li>
                )}
                
                {/* User Info - Responsive display */}
                <li className="nav-item me-3">
                  <div className="d-flex align-items-center">
                    <span className="me-2">
                      {userType === 'tourist' ? 'üß≥' : 'üëÆ'}
                    </span>
                    <div className="me-3">
                      <div className="small fw-bold">{getUserDisplayName()}</div>
                      <div className="small text-muted d-none d-sm-block">
                        {userType?.charAt(0).toUpperCase() + userType?.slice(1)}
                      </div>
                    </div>
                  </div>
                </li>
                
                {/* Logout Button */}
                <li className="nav-item">
                  <button 
                    className="btn btn-outline-danger btn-sm"
                    onClick={() => {
                      handleLogout();
                      closeMenu();
                    }}
                    title="Logout"
                  >
                    <span className="d-none d-sm-inline">üö™ Logout</span>
                    <span className="d-sm-none">üö™</span>
                  </button>
                </li>
              </ul>
            )}
            
            {/* Login button for non-authenticated users */}
            {!isAuthenticated && (
              <ul className="navbar-nav ms-auto">
                <li className="nav-item me-2">
                  <button 
                    className="btn btn-outline-primary btn-sm"
                    onClick={() => handleNavigation('/register')}
                  >
                    üìù Register
                  </button>
                </li>
                <li className="nav-item">
                  <button 
                    className="btn btn-primary btn-sm"
                    onClick={() => handleNavigation('/login')}
                  >
                    üîê Login
                  </button>
                </li>
              </ul>
            )}
          </div>
        </div>
      </nav>

      {/* Overlay for mobile menu */}
      {isMenuOpen && (
        <div 
          className="navbar-backdrop d-lg-none"
          onClick={closeMenu}
          style={{
            position: 'fixed',
            top: 0,
            left: 0,
            width: '100%',
            height: '100%',
            backgroundColor: 'rgba(0,0,0,0.5)',
            zIndex: 1040
          }}
        />
      )}

      {/* Custom CSS for navbar styling and animations */}
      <style jsx>{`
        .nav-link.active {
          background-color: rgba(13, 110, 253, 0.1) !important;
          border-radius: 6px;
          font-weight: 600;
          color: #0d6efd !important;
        }
        
        .nav-link:hover {
          background-color: rgba(13, 110, 253, 0.05);
          border-radius: 6px;
          transition: all 0.2s ease;
        }
        
        .emergency-btn {
          animation: emergency-pulse 2s infinite;
          font-weight: 600;
        }
        
        .pulse {
          animation: status-pulse 2s infinite;
        }
        
        /* Mobile menu improvements */
        @media (max-width: 991.98px) {
          .navbar-collapse {
            background-color: white;
            border-radius: 8px;
            box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
            margin-top: 8px;
            padding: 1rem;
            position: absolute;
            top: 100%;
            left: 15px;
            right: 15px;
            z-index: 1050;
          }
          
          .navbar-nav .nav-item {
            margin-bottom: 0.5rem;
          }
          
          .navbar-nav .nav-link {
            padding: 0.75rem 1rem;
            border-radius: 6px;
            margin-bottom: 0.25rem;
          }
          
          .navbar-nav .nav-link:hover {
            background-color: rgba(13, 110, 253, 0.1);
          }
          
          .emergency-btn {
            width: 100%;
            margin-bottom: 1rem;
          }
        }
        
        @keyframes emergency-pulse {
          0% { 
            box-shadow: 0 0 0 0 rgba(220, 53, 69, 0.7);
            transform: scale(1);
          }
          50% {
            transform: scale(1.05);
          }
          70% { 
            box-shadow: 0 0 0 10px rgba(220, 53, 69, 0); 
          }
          100% { 
            box-shadow: 0 0 0 0 rgba(220, 53, 69, 0);
            transform: scale(1);
          }
        }
        
        @keyframes status-pulse {
          0%, 100% { opacity: 1; }
          50% { opacity: 0.5; }
        }
        
        /* Ensure proper stacking */
        .navbar {
          z-index: 1030;
        }
        
        /* Improve mobile button sizing */
        @media (max-width: 575.98px) {
          .btn-sm {
            font-size: 0.875rem;
            padding: 0.375rem 0.75rem;
          }
        }
      `}</style>
    </>
  );
};

export default Navbar;
