import React, { useState, useEffect } from 'react';
import './HeatMap.css';

const HeatMap = () => {
  const [selectedZone, setSelectedZone] = useState(null);
  const [animationActive, setAnimationActive] = useState(true);
  const [viewMode, setViewMode] = useState('density'); // density, risk, activity
  const [timeOfDay, setTimeOfDay] = useState('current');

  // New Delhi tourist zones with realistic coordinates and comprehensive data
  const delhiTouristZones = [
    {
      id: 'zone-1',
      name: 'India Gate Area',
      location: 'Central Delhi',
      tourists: 85,
      intensity: 0.95,
      risk: 'low',
      activity: 'high',
      coordinates: { x: 60, y: 45 },
      radius: 90,
      details: { 
        active: 78, offline: 5, alerts: 2,
        popularTimes: { morning: 65, afternoon: 85, evening: 95, night: 25 },
        attractions: ['India Gate', 'Rajpath', 'National War Memorial'],
        avgVisitTime: '2.5 hours',
        crowdLevel: 'Very High',
        safetyRating: 4.5
      }
    },
    {
      id: 'zone-2',
      name: 'Red Fort Complex',
      location: 'Old Delhi',
      tourists: 72,
      intensity: 0.85,
      risk: 'medium',
      activity: 'high',
      coordinates: { x: 45, y: 25 },
      radius: 80,
      details: { 
        active: 65, offline: 4, alerts: 3,
        popularTimes: { morning: 72, afternoon: 68, evening: 45, night: 15 },
        attractions: ['Red Fort', 'Chandni Chowk', 'Jama Masjid'],
        avgVisitTime: '3 hours',
        crowdLevel: 'High',
        safetyRating: 4.0
      }
    },
    {
      id: 'zone-3',
      name: 'Connaught Place',
      location: 'Central Delhi',
      tourists: 64,
      intensity: 0.78,
      risk: 'low',
      activity: 'very-high',
      coordinates: { x: 52, y: 40 },
      radius: 75,
      details: { 
        active: 60, offline: 2, alerts: 2,
        popularTimes: { morning: 45, afternoon: 64, evening: 78, night: 52 },
        attractions: ['Connaught Place', 'Janpath Market', 'Palika Bazaar'],
        avgVisitTime: '2 hours',
        crowdLevel: 'Very High',
        safetyRating: 4.8
      }
    },
    {
      id: 'zone-4',
      name: 'Qutub Minar Area',
      location: 'South Delhi',
      tourists: 58,
      intensity: 0.70,
      risk: 'low',
      activity: 'medium',
      coordinates: { x: 35, y: 75 },
      radius: 70,
      details: { 
        active: 54, offline: 3, alerts: 1,
        popularTimes: { morning: 58, afternoon: 52, evening: 38, night: 8 },
        attractions: ['Qutub Minar', 'Iron Pillar', 'Mehrauli Archaeological Park'],
        avgVisitTime: '2.5 hours',
        crowdLevel: 'High',
        safetyRating: 4.6
      }
    },
    {
      id: 'zone-5',
      name: 'Lotus Temple Zone',
      location: 'South Delhi',
      tourists: 48,
      intensity: 0.62,
      risk: 'low',
      activity: 'medium',
      coordinates: { x: 75, y: 65 },
      radius: 65,
      details: { 
        active: 45, offline: 2, alerts: 1,
        popularTimes: { morning: 48, afternoon: 42, evening: 35, night: 12 },
        attractions: ['Lotus Temple', 'Kalkaji Temple', 'Okhla Bird Sanctuary'],
        avgVisitTime: '1.5 hours',
        crowdLevel: 'Medium',
        safetyRating: 4.7
      }
    },
    {
      id: 'zone-6',
      name: 'Humayun\'s Tomb Area',
      location: 'Central Delhi',
      tourists: 42,
      intensity: 0.55,
      risk: 'low',
      activity: 'medium',
      coordinates: { x: 68, y: 55 },
      radius: 60,
      details: { 
        active: 39, offline: 2, alerts: 1,
        popularTimes: { morning: 42, afternoon: 38, evening: 28, night: 5 },
        attractions: ['Humayun\'s Tomb', 'Nizamuddin Dargah', 'Khan-i-Khanan Tomb'],
        avgVisitTime: '2 hours',
        crowdLevel: 'Medium',
        safetyRating: 4.4
      }
    },
    {
      id: 'zone-7',
      name: 'Akshardham Complex',
      location: 'East Delhi',
      tourists: 55,
      intensity: 0.68,
      risk: 'low',
      activity: 'high',
      coordinates: { x: 85, y: 50 },
      radius: 68,
      details: { 
        active: 52, offline: 2, alerts: 1,
        popularTimes: { morning: 35, afternoon: 55, evening: 48, night: 0 },
        attractions: ['Akshardham Temple', 'Yamuna Sports Complex'],
        avgVisitTime: '4 hours',
        crowdLevel: 'High',
        safetyRating: 4.9
      }
    },
    {
      id: 'zone-8',
      name: 'Lodhi Gardens Area',
      location: 'Central Delhi',
      tourists: 38,
      intensity: 0.48,
      risk: 'low',
      activity: 'medium',
      coordinates: { x: 58, y: 60 },
      radius: 55,
      details: { 
        active: 36, offline: 1, alerts: 1,
        popularTimes: { morning: 45, afternoon: 32, evening: 38, night: 15 },
        attractions: ['Lodhi Gardens', 'Safdarjung Tomb', 'India International Centre'],
        avgVisitTime: '1.5 hours',
        crowdLevel: 'Medium',
        safetyRating: 4.5
      }
    },
    {
      id: 'zone-9',
      name: 'Karol Bagh Market',
      location: 'Central Delhi',
      tourists: 45,
      intensity: 0.58,
      risk: 'medium',
      activity: 'high',
      coordinates: { x: 42, y: 35 },
      radius: 62,
      details: { 
        active: 42, offline: 2, alerts: 1,
        popularTimes: { morning: 25, afternoon: 45, evening: 52, night: 28 },
        attractions: ['Karol Bagh Market', 'Gaffar Market', 'Jhandewalan Temple'],
        avgVisitTime: '2.5 hours',
        crowdLevel: 'High',
        safetyRating: 4.2
      }
    },
    {
      id: 'zone-10',
      name: 'Khan Market Area',
      location: 'Central Delhi',
      tourists: 32,
      intensity: 0.42,
      risk: 'low',
      activity: 'medium',
      coordinates: { x: 65, y: 52 },
      radius: 50,
      details: { 
        active: 30, offline: 1, alerts: 1,
        popularTimes: { morning: 18, afternoon: 32, evening: 38, night: 22 },
        attractions: ['Khan Market', 'Lodi Colony', 'IIC'],
        avgVisitTime: '2 hours',
        crowdLevel: 'Medium',
        safetyRating: 4.6
      }
    }
  ];

  // Generate individual tourist dots based on zone data
  const generateIndividualTourists = () => {
    let allTourists = [];
    delhiTouristZones.forEach(zone => {
      const numTourists = Math.floor(zone.tourists * 0.3); // 30% visible as dots
      for (let i = 0; i < numTourists; i++) {
        const angle = (Math.PI * 2 * i) / numTourists;
        const distance = Math.random() * (zone.radius / 2);
        const x = zone.coordinates.x + (Math.cos(angle) * distance) / 2;
        const y = zone.coordinates.y + (Math.sin(angle) * distance) / 2;
        
        allTourists.push({
          id: `${zone.id}-tourist-${i}`,
          x: Math.max(5, Math.min(95, x)),
          y: Math.max(5, Math.min(95, y)),
          status: Math.random() > 0.85 ? 'offline' : Math.random() > 0.95 ? 'alert' : 'active',
          intensity: Math.random() * 0.6 + 0.4,
          zone: zone.name
        });
      }
    });
    return allTourists;
  };

  const [individualTourists] = useState(generateIndividualTourists());

  const getIntensityColor = (intensity, mode = 'density', zone = null) => {
    if (mode === 'risk') {
      if (!zone) return '#95a5a6';
      if (zone.risk === 'high') return '#e74c3c';
      if (zone.risk === 'medium') return '#f39c12';
      return '#27ae60';
    }
    
    if (mode === 'activity') {
      if (!zone) return '#95a5a6';
      if (zone.activity === 'very-high') return '#8e44ad';
      if (zone.activity === 'high') return '#3498db';
      if (zone.activity === 'medium') return '#1abc9c';
      return '#95a5a6';
    }

    // Default density mode - Heat colors
    if (intensity > 0.9) return '#ff1744';
    if (intensity > 0.8) return '#ff5722';
    if (intensity > 0.7) return '#ff9800';
    if (intensity > 0.6) return '#ffc107';
    if (intensity > 0.5) return '#ffeb3b';
    if (intensity > 0.4) return '#8bc34a';
    return '#4caf50';
  };

  const handleZoneClick = (zone) => {
    setSelectedZone(zone);
  };

  const getTotalStats = () => {
    return delhiTouristZones.reduce((acc, zone) => ({
      total: acc.total + zone.tourists,
      active: acc.active + zone.details.active,
      offline: acc.offline + zone.details.offline,
      alerts: acc.alerts + zone.details.alerts
    }), { total: 0, active: 0, offline: 0, alerts: 0 });
  };

  const getTimeBasedData = (zone) => {
    const currentHour = new Date().getHours();
    let timeKey;
    if (currentHour >= 6 && currentHour < 12) timeKey = 'morning';
    else if (currentHour >= 12 && currentHour < 18) timeKey = 'afternoon';
    else if (currentHour >= 18 && currentHour < 22) timeKey = 'evening';
    else timeKey = 'night';
    
    if (timeOfDay === 'current') {
      return zone.details.popularTimes[timeKey];
    }
    return zone.details.popularTimes[timeOfDay] || zone.tourists;
  };

  const stats = getTotalStats();

  return (
    <div className="delhi-heat-map-container">
      <div className="delhi-heat-map-header">
        <div className="header-content">
          <div className="title-section">
            <h2 className="heat-map-title">
              <span className="title-icon">🏛️</span>
              New Delhi Tourist Heat Map
            </h2>
            <p className="subtitle">Real-time tourist distribution across Delhi's major attractions</p>
          </div>
          
          <div className="view-controls">
            <div className="control-group">
              <label>View Mode:</label>
              <select 
                value={viewMode} 
                onChange={(e) => setViewMode(e.target.value)}
                className="view-selector"
              >
                <option value="density">🔥 Tourist Density</option>
                <option value="risk">⚠️ Risk Assessment</option>
                <option value="activity">📊 Activity Level</option>
              </select>
            </div>
            
            <div className="control-group">
              <label>Time Period:</label>
              <select 
                value={timeOfDay} 
                onChange={(e) => setTimeOfDay(e.target.value)}
                className="view-selector"
              >
                <option value="current">🕐 Current Time</option>
                <option value="morning">🌅 Morning (6-12)</option>
                <option value="afternoon">☀️ Afternoon (12-18)</option>
                <option value="evening">🌆 Evening (18-22)</option>
                <option value="night">🌙 Night (22-6)</option>
              </select>
            </div>
            
            <button 
              className={`animation-toggle ${animationActive ? 'active' : ''}`}
              onClick={() => setAnimationActive(!animationActive)}
            >
              {animationActive ? '⏸️ Pause' : '▶️ Play'}
            </button>
          </div>
        </div>
      </div>

      <div className="delhi-heat-map-content">
        <div className="map-section">
          <div className="delhi-map-container">
            {/* Delhi outline with landmarks */}
            <svg className="delhi-outline" viewBox="0 0 100 100">
              {/* Delhi boundary */}
              <path
                d="M25 15 L75 15 L85 25 L85 75 L75 85 L25 85 L15 75 L15 25 Z"
                fill="rgba(255,255,255,0.05)"
                stroke="rgba(255,255,255,0.2)"
                strokeWidth="0.5"
              />
              
              {/* Yamuna River */}
              <path
                d="M80 10 Q85 30 82 50 Q78 70 75 90"
                fill="none"
                stroke="rgba(135,206,235,0.4)"
                strokeWidth="1.5"
              />
              
              {/* Major roads */}
              <line x1="15" y1="50" x2="85" y2="50" stroke="rgba(255,255,255,0.1)" strokeWidth="0.3"/>
              <line x1="50" y1="15" x2="50" y2="85" stroke="rgba(255,255,255,0.1)" strokeWidth="0.3"/>
            </svg>

            {/* Heat zones */}
            {delhiTouristZones.map((zone) => {
              const currentTourists = getTimeBasedData(zone);
              const adjustedIntensity = (currentTourists / 100);
              
              return (
                <div
                  key={zone.id}
                  className={`delhi-heat-zone ${animationActive ? 'animated' : ''} ${selectedZone?.id === zone.id ? 'selected' : ''}`}
                  style={{
                    left: `${zone.coordinates.x}%`,
                    top: `${zone.coordinates.y}%`,
                    width: `${zone.radius}px`,
                    height: `${zone.radius}px`,
                    background: `radial-gradient(circle, ${getIntensityColor(adjustedIntensity, viewMode, zone)}40 0%, ${getIntensityColor(adjustedIntensity, viewMode, zone)}20 50%, transparent 100%)`,
                    border: `2px solid ${getIntensityColor(adjustedIntensity, viewMode, zone)}80`
                  }}
                  onClick={() => handleZoneClick(zone)}
                >
                  <div className="zone-label">
                    <span className="zone-count">{currentTourists}</span>
                    <span className="zone-name">{zone.name}</span>
                    <span className="zone-location">{zone.location}</span>
                  </div>
                </div>
              );
            })}

            {/* Individual tourist dots */}
            {individualTourists.map((tourist) => (
              <div
                key={tourist.id}
                className={`tourist-dot ${tourist.status} ${animationActive ? 'pulse' : ''}`}
                style={{
                  left: `${tourist.x}%`,
                  top: `${tourist.y}%`,
                  opacity: tourist.intensity
                }}
                title={`Tourist in ${tourist.zone} - Status: ${tourist.status}`}
              />
            ))}
          </div>

          {/* Enhanced Legend */}
          <div className="delhi-heat-map-legend">
            <h4>{viewMode === 'density' ? '🔥 Tourist Density' : viewMode === 'risk' ? '⚠️ Risk Levels' : '📊 Activity Levels'}</h4>
            <div className="legend-items">
              {viewMode === 'density' && (
                <>
                  <div className="legend-item">
                    <span className="legend-color" style={{background: '#ff1744'}}></span>
                    <span>Very High (80+)</span>
                  </div>
                  <div className="legend-item">
                    <span className="legend-color" style={{background: '#ff5722'}}></span>
                    <span>High (60-80)</span>
                  </div>
                  <div className="legend-item">
                    <span className="legend-color" style={{background: '#ff9800'}}></span>
                    <span>Medium (40-60)</span>
                  </div>
                  <div className="legend-item">
                    <span className="legend-color" style={{background: '#ffc107'}}></span>
                    <span>Low (20-40)</span>
                  </div>
                  <div className="legend-item">
                    <span className="legend-color" style={{background: '#4caf50'}}></span>
                    <span>Very Low (0-20)</span>
                  </div>
                </>
              )}
              
              {viewMode === 'risk' && (
                <>
                  <div className="legend-item">
                    <span className="legend-color" style={{background: '#e74c3c'}}></span>
                    <span>High Risk</span>
                  </div>
                  <div className="legend-item">
                    <span className="legend-color" style={{background: '#f39c12'}}></span>
                    <span>Medium Risk</span>
                  </div>
                  <div className="legend-item">
                    <span className="legend-color" style={{background: '#27ae60'}}></span>
                    <span>Low Risk</span>
                  </div>
                </>
              )}
              
              {viewMode === 'activity' && (
                <>
                  <div className="legend-item">
                    <span className="legend-color" style={{background: '#8e44ad'}}></span>
                    <span>Very High</span>
                  </div>
                  <div className="legend-item">
                    <span className="legend-color" style={{background: '#3498db'}}></span>
                    <span>High</span>
                  </div>
                  <div className="legend-item">
                    <span className="legend-color" style={{background: '#1abc9c'}}></span>
                    <span>Medium</span>
                  </div>
                  <div className="legend-item">
                    <span className="legend-color" style={{background: '#95a5a6'}}></span>
                    <span>Low</span>
                  </div>
                </>
              )}
              
              <div className="legend-separator"></div>
              <div className="legend-item">
                <span className="tourist-dot-legend active"></span>
                <span>Active Tourist</span>
              </div>
              <div className="legend-item">
                <span className="tourist-dot-legend offline"></span>
                <span>Offline Tourist</span>
              </div>
              <div className="legend-item">
                <span className="tourist-dot-legend alert"></span>
                <span>Alert Status</span>
              </div>
            </div>
          </div>
        </div>

        <div className="info-panel">
          <div className="stats-overview">
            <h3>📊 Delhi Tourism Statistics</h3>
            <div className="stats-grid">
              <div className="stat-item">
                <span className="stat-value">{stats.total}</span>
                <span className="stat-label">Total Tourists</span>
                <span className="stat-sublabel">Across all zones</span>
              </div>
              <div className="stat-item active">
                <span className="stat-value">{stats.active}</span>
                <span className="stat-label">Currently Active</span>
                <span className="stat-sublabel">Online tracking</span>
              </div>
              <div className="stat-item offline">
                <span className="stat-value">{stats.offline}</span>
                <span className="stat-label">Offline</span>
                <span className="stat-sublabel">Lost connection</span>
              </div>
              <div className="stat-item alerts">
                <span className="stat-value">{stats.alerts}</span>
                <span className="stat-label">Active Alerts</span>
                <span className="stat-sublabel">Require attention</span>
              </div>
            </div>
          </div>

          {selectedZone && (
            <div className="zone-details">
              <h3>📍 {selectedZone.name}</h3>
              <p className="zone-location-detail">{selectedZone.location}</p>
              
              <div className="zone-info">
                <div className="info-section">
                  <h5>Current Status</h5>
                  <div className="info-row">
                    <span>Active Tourists:</span>
                    <span className="value active">{selectedZone.details.active}</span>
                  </div>
                  <div className="info-row">
                    <span>Crowd Level:</span>
                    <span className="value">{selectedZone.details.crowdLevel}</span>
                  </div>
                  <div className="info-row">
                    <span>Safety Rating:</span>
                    <span className="value">{'⭐'.repeat(Math.floor(selectedZone.details.safetyRating))} {selectedZone.details.safetyRating}/5</span>
                  </div>
                </div>
                
                <div className="info-section">
                  <h5>Popular Times (Tourists)</h5>
                  <div className="time-chart">
                    <div className="time-bar">
                      <span>Morning:</span>
                      <div className="bar-container">
                        <div className="bar" style={{width: `${(selectedZone.details.popularTimes.morning / 100) * 100}%`}}></div>
                        <span className="bar-value">{selectedZone.details.popularTimes.morning}</span>
                      </div>
                    </div>
                    <div className="time-bar">
                      <span>Afternoon:</span>
                      <div className="bar-container">
                        <div className="bar" style={{width: `${(selectedZone.details.popularTimes.afternoon / 100) * 100}%`}}></div>
                        <span className="bar-value">{selectedZone.details.popularTimes.afternoon}</span>
                      </div>
                    </div>
                    <div className="time-bar">
                      <span>Evening:</span>
                      <div className="bar-container">
                        <div className="bar" style={{width: `${(selectedZone.details.popularTimes.evening / 100) * 100}%`}}></div>
                        <span className="bar-value">{selectedZone.details.popularTimes.evening}</span>
                      </div>
                    </div>
                    <div className="time-bar">
                      <span>Night:</span>
                      <div className="bar-container">
                        <div className="bar" style={{width: `${(selectedZone.details.popularTimes.night / 100) * 100}%`}}></div>
                        <span className="bar-value">{selectedZone.details.popularTimes.night}</span>
                      </div>
                    </div>
                  </div>
                </div>
                
                <div className="info-section">
                  <h5>Key Attractions</h5>
                  <div className="attractions-list">
                    {selectedZone.details.attractions.map((attraction, index) => (
                      <span key={index} className="attraction-tag">{attraction}</span>
                    ))}
                  </div>
                </div>
                
                <div className="info-section">
                  <h5>Visit Information</h5>
                  <div className="info-row">
                    <span>Avg. Visit Time:</span>
                    <span className="value">{selectedZone.details.avgVisitTime}</span>
                  </div>
                  <div className="info-row">
                    <span>Risk Level:</span>
                    <span className={`value risk-${selectedZone.risk}`}>{selectedZone.risk.toUpperCase()}</span>
                  </div>
                </div>
              </div>
              
              <button 
                className="close-details"
                onClick={() => setSelectedZone(null)}
              >
                ✖ Close Details
              </button>
            </div>
          )}

          <div className="update-info">
            <h4>🕐 Live Updates</h4>
            <p>🔄 Last refresh: {new Date().toLocaleTimeString()}</p>
            <p>📡 Auto-refresh: Every 2 minutes</p>
            <p>🎯 Coverage: 10 major Delhi tourist zones</p>
            <p>📊 Mode: {viewMode.charAt(0).toUpperCase() + viewMode.slice(1)} View</p>
            <p>⏰ Time: {timeOfDay === 'current' ? 'Live Data' : timeOfDay.charAt(0).toUpperCase() + timeOfDay.slice(1)}</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default HeatMap;
