import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';

// Common Components
import Navbar from './components/common/Navbar';

// Tourist Components
import Login from './components/tourist/Login';
import Register from './components/tourist/Register';
import TouristHome from './components/tourist/Home';
import TouristMap from './components/tourist/Map';
import QRDisplay from './components/tourist/QRCode';
import SOS from './components/tourist/SOS';
import SafetyCheck from './components/tourist/SafetyCheck';

// Police Components  
import PoliceDashboard from './components/police/Dashboard';
import TouristList from './components/police/TouristList';
import Heatmap from './components/police/Heatmap';
import AlertPanel from './components/police/AlertPanel';

// Services
import { authService } from './services/auth';

function App() {
  const [authState, setAuthState] = useState({
    isAuthenticated: false,
    userType: null,
    loading: true
  });

  useEffect(() => {
    // Check authentication status on app load
    const checkAuth = () => {
      const isAuth = authService.isAuthenticated();
      const type = authService.getUserType();
      
      console.log('App - checking auth:', { isAuth, type });
      
      setAuthState({
        isAuthenticated: isAuth,
        userType: type,
        loading: false
      });
    };

    checkAuth();

    // Listen for storage changes (when user logs in/out in another tab)
    const handleStorageChange = () => {
      checkAuth();
    };

    window.addEventListener('storage', handleStorageChange);
    return () => window.removeEventListener('storage', handleStorageChange);
  }, []);

  const ProtectedRoute = ({ children, requiredUserType }) => {
    console.log('ProtectedRoute check:', { 
      isAuthenticated: authState.isAuthenticated, 
      userType: authState.userType, 
      requiredUserType 
    });

    if (authState.loading) {
      return (
        <div className="d-flex justify-content-center align-items-center min-vh-100">
          <div className="spinner-border text-primary" role="status">
            <span className="visually-hidden">Loading...</span>
          </div>
        </div>
      );
    }

    if (!authState.isAuthenticated) {
      console.log('Not authenticated, redirecting to login');
      return <Navigate to="/login" replace />;
    }
    
    if (requiredUserType && authState.userType !== requiredUserType) {
      console.log('Wrong user type, redirecting to login');
      return <Navigate to="/login" replace />;
    }
    
    return children;
  };

  const DefaultRoute = () => {
    if (authState.loading) {
      return (
        <div className="d-flex justify-content-center align-items-center min-vh-100">
          <div className="spinner-border text-primary" role="status">
            <span className="visually-hidden">Loading...</span>
          </div>
        </div>
      );
    }

    if (authState.isAuthenticated) {
      if (authState.userType === 'tourist') {
        return <Navigate to="/tourist/home" replace />;
      } else if (authState.userType === 'police') {
        return <Navigate to="/police/dashboard" replace />;
      }
    }
    
    return <Navigate to="/login" replace />;
  };

  return (
    <Router>
      <div className="App">
        <Navbar />
        <div className="main-content">
          <Routes>
            {/* Public Routes */}
            <Route path="/login" element={<Login />} />
            <Route path="/register" element={<Register />} />
            
            {/* Tourist Routes */}
            <Route 
              path="/tourist/home" 
              element={
                <ProtectedRoute requiredUserType="tourist">
                  <TouristHome />
                </ProtectedRoute>
              } 
            />
            <Route 
              path="/tourist/map" 
              element={
                <ProtectedRoute requiredUserType="tourist">
                  <TouristMap />
                </ProtectedRoute>
              } 
            />
            <Route 
              path="/tourist/qr" 
              element={
                <ProtectedRoute requiredUserType="tourist">
                  <QRDisplay />
                </ProtectedRoute>
              } 
            />
            <Route 
              path="/tourist/sos" 
              element={
                <ProtectedRoute requiredUserType="tourist">
                  <SOS />
                </ProtectedRoute>
              } 
            />
            <Route 
              path="/tourist/safety-check" 
              element={
                <ProtectedRoute requiredUserType="tourist">
                  <SafetyCheck />
                </ProtectedRoute>
              } 
            />
            
            {/* Police Routes */}
            <Route 
              path="/police/dashboard" 
              element={
                <ProtectedRoute requiredUserType="police">
                  <PoliceDashboard />
                </ProtectedRoute>
              } 
            />
            <Route 
              path="/police/tourists" 
              element={
                <ProtectedRoute requiredUserType="police">
                  <TouristList />
                </ProtectedRoute>
              } 
            />
            <Route 
              path="/police/heatmap" 
              element={
                <ProtectedRoute requiredUserType="police">
                  <Heatmap />
                </ProtectedRoute>
              } 
            />
            <Route 
              path="/police/alerts" 
              element={
                <ProtectedRoute requiredUserType="police">
                  <AlertPanel />
                </ProtectedRoute>
              } 
            />
            
            {/* Default Route */}
            <Route path="/" element={<DefaultRoute />} />
            
            {/* 404 Route */}
            <Route path="*" element={<Navigate to="/" />} />
          </Routes>
        </div>
        
        <ToastContainer 
          position="top-right" 
          autoClose={5000} 
          hideProgressBar={false}
          newestOnTop={false}
          closeOnClick
          rtl={false}
          pauseOnFocusLoss
          draggable
          pauseOnHover
          theme="light"
        />
      </div>
    </Router>
  );
}

export default App;
