import React, { useState, useEffect } from 'react';
import { QRCodeSVG } from 'qrcode.react';
import { toast } from 'react-toastify';
import { authService } from '../../services/auth';
import { touristAPI } from '../../services/api';
import Loading from '../common/Loading';
import './EnhancedQRCode.css';

const EnhancedQRCode = () => {
  const [qrData, setQrData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [showBlockchain, setShowBlockchain] = useState(false);
  const [activeTab, setActiveTab] = useState('qr');
  const [isQRFlipped, setIsQRFlipped] = useState(false);

  const userInfo = authService.getUserInfo();

  useEffect(() => {
    generateQRData();
  }, []);

  const generateQRData = async () => {
    try {
      setLoading(true);
      const blockchainHash = generateBlockchainHash();

      const qrInfo = {
        id: userInfo?.id || 'tourist-demo',
        name: userInfo?.name || 'Alex Johnson',
        email: userInfo?.email || 'alex.johnson@tourist.com',
        phone: '+91 98765 43210',
        destination: 'Delhi Heritage Tour',
        emergencyContact: '+91 87654 32109',
        blockchainHash: blockchainHash,
        safetyScore: 92,
        timestamp: new Date().toISOString(),
        version: '2.0',
        verified: true,
        issuer: 'Tourist Safety Authority',
        expiryDate: new Date(Date.now() + 30 * 24 * 60 * 60 * 1000).toISOString()
      };

      setQrData(qrInfo);
      await new Promise(resolve => setTimeout(resolve, 1000));
    } catch (error) {
      console.error('Error generating QR data:', error);
      toast.error('Failed to generate QR code');
    } finally {
      setLoading(false);
    }
  };

  const generateBlockchainHash = () => {
    const timestamp = Date.now().toString();
    const random = Math.random().toString(36).substring(7);
    let hash = '';
    const chars = 'abcdef0123456789';
    for (let i = 0; i < 64; i++) {
      hash += chars.charAt(Math.floor(Math.random() * chars.length));
    }
    return hash;
  };

  const downloadQR = (format = 'png') => {
    const svg = document.getElementById('tourist-qr-code');
    const svgData = new XMLSerializer().serializeToString(svg);

    if (format === 'svg') {
      const svgBlob = new Blob([svgData], {type: 'image/svg+xml;charset=utf-8'});
      const svgUrl = URL.createObjectURL(svgBlob);
      const downloadLink = document.createElement('a');
      downloadLink.href = svgUrl;
      downloadLink.download = `tourist-id-${userInfo?.name || 'demo'}.svg`;
      document.body.appendChild(downloadLink);
      downloadLink.click();
      document.body.removeChild(downloadLink);
    } else {
      const canvas = document.createElement('canvas');
      const ctx = canvas.getContext('2d');
      const img = new Image();

      img.onload = function() {
        canvas.width = 512;
        canvas.height = 512;
        ctx.fillStyle = 'white';
        ctx.fillRect(0, 0, canvas.width, canvas.height);
        ctx.drawImage(img, 0, 0, canvas.width, canvas.height);

        canvas.toBlob(function(blob) {
          const url = URL.createObjectURL(blob);
          const downloadLink = document.createElement('a');
          downloadLink.href = url;
          downloadLink.download = `tourist-id-${userInfo?.name || 'demo'}.png`;
          document.body.appendChild(downloadLink);
          downloadLink.click();
          document.body.removeChild(downloadLink);
        });
      };

      img.src = 'data:image/svg+xml;base64,' + btoa(unescape(encodeURIComponent(svgData)));
    }

    toast.success(`QR Code downloaded as ${format.toUpperCase()}`);
  };

  const copyToClipboard = () => {
    const qrText = JSON.stringify(qrData, null, 2);
    navigator.clipboard.writeText(qrText).then(() => {
      toast.success('QR data copied to clipboard');
    }).catch(() => {
      toast.error('Failed to copy to clipboard');
    });
  };

  const shareQR = async () => {
    if (navigator.share) {
      try {
        await navigator.share({
          title: 'My Tourist Safety ID',
          text: `Tourist ID: ${qrData.name} - ${qrData.destination}`,
          url: window.location.href
        });
        toast.success('QR code shared successfully');
      } catch (error) {
        toast.info('Share cancelled');
      }
    } else {
      copyToClipboard();
    }
  };

  if (loading) {
    return <Loading message="Generating your digital ID..." size="medium" />;
  }

  if (!qrData) {
    return (
      <div className="enhanced-qr-container">
        <div className="error-state">
          <div className="error-icon">âš ï¸</div>
          <h3>Failed to generate QR code</h3>
          <button className="retry-btn" onClick={generateQRData}>
            ðŸ”„ Try Again
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="enhanced-qr-container">
      {/* Floating Header */}
      <div className="floating-header">
        <div className="header-content">
          <div className="header-info">
            <h1>ðŸ†” Digital Tourist Identity</h1>
            <p>Secure â€¢ Verified â€¢ Blockchain Protected</p>
          </div>
          <div className="header-status">
            <div className="status-indicator active">
              <div className="pulse"></div>
            </div>
            <span>Live</span>
          </div>
        </div>
      </div>

      {/* Tab Navigation */}
      <div className="tab-navigation">
        <button 
          className={`tab-btn ${activeTab === 'qr' ? 'active' : ''}`}
          onClick={() => setActiveTab('qr')}
        >
          <span className="tab-icon">ðŸ“±</span>
          <span>QR Code</span>
        </button>
        <button 
          className={`tab-btn ${activeTab === 'profile' ? 'active' : ''}`}
          onClick={() => setActiveTab('profile')}
        >
          <span className="tab-icon">ðŸ‘¤</span>
          <span>Profile</span>
        </button>
        <button 
          className={`tab-btn ${activeTab === 'security' ? 'active' : ''}`}
          onClick={() => setActiveTab('security')}
        >
          <span className="tab-icon">ðŸ”</span>
          <span>Security</span>
        </button>
      </div>

      {/* Content Based on Active Tab */}
      <div className="tab-content">
        {/* QR Code Tab */}
        {activeTab === 'qr' && (
          <div className="qr-section">
            <div className="qr-card-container">
              <div className={`qr-card ${isQRFlipped ? 'flipped' : ''}`}>
                {/* Front Side */}
                <div className="qr-card-front">
                  <div className="qr-header">
                    <div className="qr-title">
                      <h3>ðŸ“± Scannable ID</h3>
                      <div className="verification-badges">
                        <span className="badge verified">âœ“ Verified</span>
                        <span className="badge score">ðŸ›¡ï¸ {qrData.safetyScore}%</span>
                      </div>
                    </div>
                  </div>

                  <div className="qr-display">
                    <div className="qr-wrapper">
                      <QRCodeSVG
                        id="tourist-qr-code"
                        value={JSON.stringify(qrData)}
                        size={220}
                        level="H"
                        includeMargin={true}
                        bgColor="#ffffff"
                        fgColor="#000000"
                      />
                    </div>
                  </div>

                  <div className="qr-info">
                    <h4>{qrData.name}</h4>
                    <p>ID: {qrData.id}</p>
                    <div className="qr-meta">
                      <span>ðŸ“ {qrData.destination}</span>
                      <span>ðŸ“… Valid until {new Date(qrData.expiryDate).toLocaleDateString()}</span>
                    </div>
                  </div>

                  <div className="qr-actions">
                    <button className="action-btn primary" onClick={() => downloadQR('png')}>
                      <span>ðŸ’¾</span>
                      <span>Download</span>
                    </button>
                    <button className="action-btn secondary" onClick={shareQR}>
                      <span>ðŸ“¤</span>
                      <span>Share</span>
                    </button>
                    <button className="action-btn tertiary" onClick={() => setIsQRFlipped(true)}>
                      <span>ðŸ”„</span>
                      <span>Flip</span>
                    </button>
                  </div>
                </div>

                {/* Back Side */}
                <div className="qr-card-back">
                  <div className="back-header">
                    <h3>ðŸš¨ Emergency Information</h3>
                  </div>
                  
                  <div className="emergency-info">
                    <div className="emergency-item">
                      <span className="emergency-icon">ðŸ“ž</span>
                      <div className="emergency-details">
                        <span className="emergency-label">Emergency Contact</span>
                        <span className="emergency-value">{qrData.emergencyContact}</span>
                      </div>
                    </div>
                    
                    <div className="emergency-item">
                      <span className="emergency-icon">ðŸ†”</span>
                      <div className="emergency-details">
                        <span className="emergency-label">Tourist ID</span>
                        <span className="emergency-value">{qrData.id}</span>
                      </div>
                    </div>
                    
                    <div className="emergency-item">
                      <span className="emergency-icon">ðŸ¥</span>
                      <div className="emergency-details">
                        <span className="emergency-label">Medical Emergency</span>
                        <span className="emergency-value">102</span>
                      </div>
                    </div>
                    
                    <div className="emergency-item">
                      <span className="emergency-icon">ðŸ‘®</span>
                      <div className="emergency-details">
                        <span className="emergency-label">Tourist Police</span>
                        <span className="emergency-value">1363</span>
                      </div>
                    </div>
                  </div>

                  <button className="flip-back-btn" onClick={() => setIsQRFlipped(false)}>
                    ðŸ”„ Flip Back
                  </button>
                </div>
              </div>
            </div>

            {/* Download Options */}
            <div className="download-section">
              <h4>ðŸ“¥ Download Options</h4>
              <div className="download-grid">
                <button className="download-btn png" onClick={() => downloadQR('png')}>
                  <span className="download-icon">ðŸ–¼ï¸</span>
                  <span className="download-text">PNG Image</span>
                  <span className="download-desc">High quality for printing</span>
                </button>
                <button className="download-btn svg" onClick={() => downloadQR('svg')}>
                  <span className="download-icon">ðŸ“„</span>
                  <span className="download-text">SVG Vector</span>
                  <span className="download-desc">Scalable for all sizes</span>
                </button>
                <button className="download-btn copy" onClick={copyToClipboard}>
                  <span className="download-icon">ðŸ“‹</span>
                  <span className="download-text">Copy Data</span>
                  <span className="download-desc">Raw JSON format</span>
                </button>
              </div>
            </div>
          </div>
        )}

        {/* Profile Tab */}
        {activeTab === 'profile' && (
          <div className="profile-section">
            <div className="profile-card">
              <div className="profile-avatar">
                <div className="avatar-circle">
                  <span className="avatar-initial">{qrData.name.charAt(0)}</span>
                </div>
                <div className="avatar-status active"></div>
              </div>
              
              <div className="profile-details">
                <h3>{qrData.name}</h3>
                <p className="profile-id">Tourist ID: {qrData.id}</p>
                
                <div className="detail-grid">
                  <div className="detail-item">
                    <span className="detail-icon">ðŸ“§</span>
                    <div className="detail-content">
                      <span className="detail-label">Email</span>
                      <span className="detail-value">{qrData.email}</span>
                    </div>
                  </div>
                  
                  <div className="detail-item">
                    <span className="detail-icon">ðŸ“±</span>
                    <div className="detail-content">
                      <span className="detail-label">Phone</span>
                      <span className="detail-value">{qrData.phone}</span>
                    </div>
                  </div>
                  
                  <div className="detail-item">
                    <span className="detail-icon">ðŸŽ¯</span>
                    <div className="detail-content">
                      <span className="detail-label">Destination</span>
                      <span className="detail-value">{qrData.destination}</span>
                    </div>
                  </div>
                  
                  <div className="detail-item emergency">
                    <span className="detail-icon">ðŸš¨</span>
                    <div className="detail-content">
                      <span className="detail-label">Emergency Contact</span>
                      <span className="detail-value">{qrData.emergencyContact}</span>
                    </div>
                  </div>
                </div>
                
                <div className="safety-score">
                  <div className="score-circle">
                    <div className="score-text">
                      <span className="score-number">{qrData.safetyScore}</span>
                      <span className="score-label">Safety Score</span>
                    </div>
                  </div>
                </div>
              </div>
            </div>

            <div className="profile-actions">
              <button className="profile-btn update" onClick={() => toast.info('Profile update coming soon')}>
                <span>âœï¸</span>
                <span>Update Profile</span>
              </button>
              <button className="profile-btn notify" onClick={() => toast.info('Emergency contacts notified')}>
                <span>ðŸ“ž</span>
                <span>Notify Contacts</span>
              </button>
            </div>
          </div>
        )}

        {/* Security Tab */}
        {activeTab === 'security' && (
          <div className="security-section">
            <div className="blockchain-card">
              <div className="blockchain-header">
                <h3>ðŸ”— Blockchain Verification</h3>
                <button 
                  className="toggle-hash-btn"
                  onClick={() => setShowBlockchain(!showBlockchain)}
                >
                  {showBlockchain ? 'ðŸ™ˆ Hide Hash' : 'ðŸ‘ï¸ Show Hash'}
                </button>
              </div>
              
              <div className="blockchain-content">
                <div className="blockchain-icon">
                  <div className="blockchain-animation">
                    <div className="block block1"></div>
                    <div className="block block2"></div>
                    <div className="block block3"></div>
                  </div>
                </div>
                
                <div className="blockchain-info">
                  <p>Your identity is secured using blockchain technology, ensuring authenticity and preventing tampering.</p>
                  
                  {showBlockchain && (
                    <div className="hash-display">
                      <label>Blockchain Hash:</label>
                      <div className="hash-container">
                        <code className="hash-code">{qrData.blockchainHash}</code>
                        <button className="copy-hash-btn" onClick={() => {
                          navigator.clipboard.writeText(qrData.blockchainHash);
                          toast.success('Hash copied to clipboard');
                        }}>
                          ðŸ“‹
                        </button>
                      </div>
                    </div>
                  )}
                </div>
              </div>
            </div>

            <div className="security-features">
              <h4>ðŸ›¡ï¸ Security Features</h4>
              <div className="features-grid">
                <div className="feature-item">
                  <span className="feature-icon">ðŸ”</span>
                  <div className="feature-content">
                    <h5>Encrypted Data</h5>
                    <p>All personal information is encrypted</p>
                  </div>
                </div>
                
                <div className="feature-item">
                  <span className="feature-icon">â°</span>
                  <div className="feature-content">
                    <h5>Time Stamped</h5>
                    <p>Generated: {new Date(qrData.timestamp).toLocaleString()}</p>
                  </div>
                </div>
                
                <div className="feature-item">
                  <span className="feature-icon">ðŸ“…</span>
                  <div className="feature-content">
                    <h5>Expiry Protection</h5>
                    <p>Valid until: {new Date(qrData.expiryDate).toLocaleDateString()}</p>
                  </div>
                </div>
                
                <div className="feature-item">
                  <span className="feature-icon">âœ…</span>
                  <div className="feature-content">
                    <h5>Authority Verified</h5>
                    <p>Issued by: {qrData.issuer}</p>
                  </div>
                </div>
              </div>
            </div>
          </div>
        )}
      </div>

      {/* Floating Action Button */}
      <div className="floating-actions">
        <button className="fab main-fab" onClick={() => toast.info('Quick actions menu')}>
          âš¡
        </button>
        <button className="fab refresh-fab" onClick={generateQRData} title="Regenerate QR">
          ðŸ”„
        </button>
        <button className="fab emergency-fab" onClick={() => toast.info('Emergency protocol activated')} title="Emergency">
          ðŸ†˜
        </button>
      </div>

      {/* Status Bar */}
      <div className="status-bar">
        <div className="status-item">
          <span className="status-icon">ðŸŸ¢</span>
          <span>Active</span>
        </div>
        <div className="status-item">
          <span className="status-icon">ðŸ”</span>
          <span>Secured</span>
        </div>
        <div className="status-item">
          <span className="status-icon">âœ…</span>
          <span>Verified</span>
        </div>
        <div className="status-item">
          <span className="status-icon">ðŸ“¡</span>
          <span>Connected</span>
        </div>
      </div>
    </div>
  );
};

export default EnhancedQRCode;
