import React, { useState, useEffect } from 'react';
import { toast } from 'react-toastify';

const Heatmap = () => {
  const [tourists, setTourists] = useState([]);
  const [selectedZone, setSelectedZone] = useState('all');
  const [timeRange, setTimeRange] = useState('1h');
  const [loading, setLoading] = useState(false);

  // Sample tourist location data
  const sampleTourists = [
    { id: 1, name: 'John Doe', lat: 28.6139, lng: 77.2090, zone: 'tourist-district', safetyScore: 88 },
    { id: 2, name: 'Jane Smith', lat: 28.6149, lng: 77.2095, zone: 'tourist-district', safetyScore: 92 },
    { id: 3, name: 'Mike Johnson', lat: 28.6120, lng: 77.2080, zone: 'market-area', safetyScore: 76 },
    { id: 4, name: 'Sarah Wilson', lat: 28.6145, lng: 77.2105, zone: 'tourist-district', safetyScore: 95 },
    { id: 5, name: 'David Brown', lat: 28.6100, lng: 77.2070, zone: 'construction', safetyScore: 65 },
    { id: 6, name: 'Lisa Chen', lat: 28.6155, lng: 77.2085, zone: 'tourist-district', safetyScore: 89 },
    { id: 7, name: 'Robert Kim', lat: 28.6125, lng: 77.2075, zone: 'market-area', safetyScore: 82 },
    { id: 8, name: 'Emily Davis', lat: 28.6105, lng: 77.2065, zone: 'construction', safetyScore: 71 }
  ];

  useEffect(() => {
    loadHeatmapData();
  }, [selectedZone, timeRange]);

  const loadHeatmapData = () => {
    setLoading(true);
    // Simulate API call
    setTimeout(() => {
      let filteredData = sampleTourists;

      if (selectedZone !== 'all') {
        filteredData = sampleTourists.filter(t => t.zone === selectedZone);
      }

      setTourists(filteredData);
      setLoading(false);
    }, 1000);
  };

  const getZoneStats = () => {
    const zones = {
      'tourist-district': { count: 0, avgSafety: 0, color: '#2ecc71', name: 'Tourist District' },
      'market-area': { count: 0, avgSafety: 0, color: '#f39c12', name: 'Market Area' },
      'construction': { count: 0, avgSafety: 0, color: '#e74c3c', name: 'Construction Zone' }
    };

    tourists.forEach(tourist => {
      if (zones[tourist.zone]) {
        zones[tourist.zone].count++;
        zones[tourist.zone].avgSafety += tourist.safetyScore;
      }
    });

    Object.keys(zones).forEach(zone => {
      if (zones[zone].count > 0) {
        zones[zone].avgSafety = Math.round(zones[zone].avgSafety / zones[zone].count);
      }
    });

    return zones;
  };

  const zoneStats = getZoneStats();

  const refreshData = () => {
    toast.info('🔄 Refreshing heatmap data...');
    loadHeatmapData();
  };

  const alertRiskZones = () => {
    const riskZones = Object.entries(zoneStats).filter(([_, stats]) => stats.avgSafety < 80);
    if (riskZones.length > 0) {
      toast.warning(`⚠️ Alert sent to tourists in ${riskZones.length} risk zone(s)`);
    } else {
      toast.success('✅ All zones are currently safe');
    }
  };

  const exportData = () => {
    const data = {
      timestamp: new Date().toISOString(),
      timeRange,
      selectedZone,
      totalTourists: tourists.length,
      zoneStats,
      tourists: tourists.map(t => ({
        id: t.id,
        name: t.name,
        zone: t.zone,
        safetyScore: t.safetyScore,
        coordinates: { lat: t.lat, lng: t.lng }
      }))
    };

    console.log('Exported Data:', data);
    toast.success('📊 Heatmap data exported successfully');
  };

  return (
    <div className="container-fluid py-4">
      <div className="row">
        <div className="col-12">
          <div className="card">
            <div className="card-header">
              <h3 className="mb-0">🗺️ Tourist Location Heatmap</h3>
              <p className="mb-0 mt-2">Real-time tourist distribution and density visualization</p>
            </div>
          </div>
        </div>
      </div>

      {/* Controls */}
      <div className="row mt-4">
        <div className="col-md-6">
          <div className="card">
            <div className="card-body">
              <div className="row">
                <div className="col-md-6">
                  <label className="form-label">🎯 Zone Filter</label>
                  <select 
                    className="form-control"
                    value={selectedZone}
                    onChange={(e) => setSelectedZone(e.target.value)}
                  >
                    <option value="all">All Zones</option>
                    <option value="tourist-district">🏛️ Tourist District</option>
                    <option value="market-area">🏪 Market Area</option>
                    <option value="construction">🚧 Construction Zone</option>
                  </select>
                </div>
                <div className="col-md-6">
                  <label className="form-label">⏰ Time Range</label>
                  <select 
                    className="form-control"
                    value={timeRange}
                    onChange={(e) => setTimeRange(e.target.value)}
                  >
                    <option value="1h">Last 1 Hour</option>
                    <option value="6h">Last 6 Hours</option>
                    <option value="24h">Last 24 Hours</option>
                    <option value="7d">Last 7 Days</option>
                  </select>
                </div>
              </div>
            </div>
          </div>
        </div>
        <div className="col-md-6">
          <div className="card">
            <div className="card-body">
              <div className="row text-center">
                <div className="col-3">
                  <div className="stat-number text-primary">{tourists.length}</div>
                  <div className="stat-label small">Total Tourists</div>
                </div>
                <div className="col-3">
                  <div className="stat-number text-success">{zoneStats['tourist-district'].count}</div>
                  <div className="stat-label small">Safe Zones</div>
                </div>
                <div className="col-3">
                  <div className="stat-number text-warning">{zoneStats['market-area'].count}</div>
                  <div className="stat-label small">Medium Zones</div>
                </div>
                <div className="col-3">
                  <div className="stat-number text-danger">{zoneStats['construction'].count}</div>
                  <div className="stat-label small">Risk Zones</div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Heatmap Visualization */}
      <div className="row mt-4">
        <div className="col-md-8">
          <div className="card">
            <div className="card-header">
              <h5 className="mb-0">🌡️ Interactive Heat Map</h5>
            </div>
            <div className="card-body p-0">
              {loading ? (
                <div className="d-flex justify-content-center align-items-center" style={{ height: '500px' }}>
                  <div className="loading-spinner"></div>
                </div>
              ) : (
                <div style={{ 
                  height: '500px', 
                  background: 'linear-gradient(135deg, #74b9ff 0%, #0984e3 100%)',
                  position: 'relative',
                  overflow: 'hidden'
                }}>
                  {/* Simulated heatmap with tourist markers */}
                  <div className="position-relative h-100 d-flex align-items-center justify-content-center">
                    <div className="text-center text-white mb-4">
                      <h4 className="mb-3">🗺️ Live Tourist Distribution</h4>
                      <p className="mb-1">Showing {tourists.length} tourists in {selectedZone === 'all' ? 'all zones' : selectedZone.replace('-', ' ')}</p>
                      <small>Updated every {timeRange === '1h' ? '30 seconds' : '5 minutes'}</small>
                    </div>

                    {/* Tourist District - High Density */}
                    {zoneStats['tourist-district'].count > 0 && (
                      <div className="position-absolute" style={{ 
                        top: '25%', 
                        left: '30%', 
                        width: Math.max(80, zoneStats['tourist-district'].count * 20) + 'px', 
                        height: Math.max(80, zoneStats['tourist-district'].count * 20) + 'px',
                        background: 'radial-gradient(circle, rgba(46, 204, 113, 0.8) 0%, rgba(46, 204, 113, 0.2) 70%)',
                        borderRadius: '50%',
                        display: 'flex',
                        alignItems: 'center',
                        justifyContent: 'center',
                        color: 'white',
                        fontWeight: 'bold',
                        fontSize: '0.9rem',
                        border: '2px solid rgba(46, 204, 113, 0.9)'
                      }}>
                        <div className="text-center">
                          <div>🏛️</div>
                          <div>{zoneStats['tourist-district'].count}</div>
                          <small>tourists</small>
                        </div>
                      </div>
                    )}

                    {/* Market Area - Medium Density */}
                    {zoneStats['market-area'].count > 0 && (
                      <div className="position-absolute" style={{ 
                        top: '55%', 
                        right: '25%', 
                        width: Math.max(60, zoneStats['market-area'].count * 15) + 'px', 
                        height: Math.max(60, zoneStats['market-area'].count * 15) + 'px',
                        background: 'radial-gradient(circle, rgba(243, 156, 18, 0.8) 0%, rgba(243, 156, 18, 0.2) 70%)',
                        borderRadius: '50%',
                        display: 'flex',
                        alignItems: 'center',
                        justifyContent: 'center',
                        color: 'white',
                        fontWeight: 'bold',
                        fontSize: '0.8rem',
                        border: '2px solid rgba(243, 156, 18, 0.9)'
                      }}>
                        <div className="text-center">
                          <div>🏪</div>
                          <div>{zoneStats['market-area'].count}</div>
                          <small>tourists</small>
                        </div>
                      </div>
                    )}

                    {/* Construction Zone - Low Density */}
                    {zoneStats['construction'].count > 0 && (
                      <div className="position-absolute" style={{ 
                        bottom: '20%', 
                        left: '20%', 
                        width: Math.max(50, zoneStats['construction'].count * 12) + 'px', 
                        height: Math.max(50, zoneStats['construction'].count * 12) + 'px',
                        background: 'radial-gradient(circle, rgba(231, 76, 60, 0.8) 0%, rgba(231, 76, 60, 0.2) 70%)',
                        borderRadius: '50%',
                        display: 'flex',
                        alignItems: 'center',
                        justifyContent: 'center',
                        color: 'white',
                        fontWeight: 'bold',
                        fontSize: '0.7rem',
                        border: '2px solid rgba(231, 76, 60, 0.9)'
                      }}>
                        <div className="text-center">
                          <div>🚧</div>
                          <div>{zoneStats['construction'].count}</div>
                          <small>tourists</small>
                        </div>
                      </div>
                    )}

                    {/* Individual tourist markers */}
                    {tourists.slice(0, 8).map((tourist, index) => (
                      <div 
                        key={tourist.id}
                        className="position-absolute"
                        style={{
                          top: `${25 + (index % 4) * 15}%`,
                          left: `${35 + (index % 3) * 10}%`,
                          background: 'white',
                          borderRadius: '50%',
                          width: '12px',
                          height: '12px',
                          border: `2px solid ${
                            tourist.zone === 'tourist-district' ? '#2ecc71' :
                            tourist.zone === 'market-area' ? '#f39c12' : '#e74c3c'
                          }`,
                          cursor: 'pointer'
                        }}
                        title={`${tourist.name} - Safety: ${tourist.safetyScore}%`}
                        onClick={() => toast.info(`📍 ${tourist.name} - Safety Score: ${tourist.safetyScore}%`)}
                      />
                    ))}

                    {/* Legend */}
                    <div className="position-absolute bottom-0 start-0 m-3">
                      <div className="bg-white bg-opacity-90 p-2 rounded">
                        <small className="fw-bold d-block mb-1">Legend:</small>
                        <div className="d-flex align-items-center mb-1">
                          <div style={{ width: '10px', height: '10px', backgroundColor: '#2ecc71', borderRadius: '50%', marginRight: '5px' }}></div>
                          <small>Safe Zone</small>
                        </div>
                        <div className="d-flex align-items-center mb-1">
                          <div style={{ width: '10px', height: '10px', backgroundColor: '#f39c12', borderRadius: '50%', marginRight: '5px' }}></div>
                          <small>Medium Risk</small>
                        </div>
                        <div className="d-flex align-items-center">
                          <div style={{ width: '10px', height: '10px', backgroundColor: '#e74c3c', borderRadius: '50%', marginRight: '5px' }}></div>
                          <small>High Risk</small>
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
              )}
            </div>
          </div>
        </div>

        <div className="col-md-4">
          <div className="card">
            <div className="card-header">
              <h5 className="mb-0">📊 Zone Analysis</h5>
            </div>
            <div className="card-body">
              {Object.entries(zoneStats).map(([zone, stats]) => (
                <div key={zone} className="mb-4">
                  <div className="d-flex justify-content-between align-items-center mb-2">
                    <h6 className="mb-0">
                      {zone === 'tourist-district' && '🏛️ Tourist District'}
                      {zone === 'market-area' && '🏪 Market Area'}
                      {zone === 'construction' && '🚧 Construction Zone'}
                    </h6>
                    <span className="badge" style={{ backgroundColor: stats.color, color: 'white' }}>
                      {stats.count} tourists
                    </span>
                  </div>
                  <div className="mb-2">
                    <small className="text-muted">Average Safety Score:</small>
                    <div className="progress mb-1" style={{ height: '6px' }}>
                      <div 
                        className="progress-bar" 
                        style={{ 
                          width: `${stats.avgSafety}%`,
                          backgroundColor: stats.color
                        }}
                      />
                    </div>
                    <div className="d-flex justify-content-between">
                      <small>{stats.avgSafety}%</small>
                      <small className={
                        stats.avgSafety >= 85 ? 'text-success' :
                        stats.avgSafety >= 70 ? 'text-warning' : 'text-danger'
                      }>
                        {stats.avgSafety >= 85 ? 'Excellent' :
                         stats.avgSafety >= 70 ? 'Good' : 'Needs Attention'}
                      </small>
                    </div>
                  </div>

                  {/* Zone-specific recommendations */}
                  <div className="mt-2">
                    <small className="text-muted">
                      {zone === 'tourist-district' && 'ℹ️ High security presence, ideal for tourism'}
                      {zone === 'market-area' && '⚠️ Busy area, tourists should stay alert'}
                      {zone === 'construction' && '🚨 Active construction, avoid after dark'}
                    </small>
                  </div>
                </div>
              ))}
            </div>
          </div>

          <div className="card mt-3">
            <div className="card-header">
              <h5 className="mb-0">⚡ Heat Map Actions</h5>
            </div>
            <div className="card-body">
              <div className="d-grid gap-2">
                <button 
                  className="btn btn-primary"
                  onClick={refreshData}
                  disabled={loading}
                >
                  {loading ? (
                    <>
                      <span className="spinner-border spinner-border-sm me-2" />
                      Refreshing...
                    </>
                  ) : (
                    '🔄 Refresh Data'
                  )}
                </button>

                <button 
                  className="btn btn-warning"
                  onClick={alertRiskZones}
                >
                  ⚠️ Alert Risk Zones
                </button>

                <button 
                  className="btn btn-success"
                  onClick={exportData}
                >
                  📊 Export Heatmap
                </button>

                <button 
                  className="btn btn-info"
                  onClick={() => toast.info('📡 Live tracking activated for all zones')}
                >
                  📡 Enable Live Tracking
                </button>
              </div>
            </div>
          </div>

          <div className="card mt-3">
            <div className="card-header">
              <h5 className="mb-0">🕒 Real-time Status</h5>
            </div>
            <div className="card-body">
              <div className="small">
                <div className="d-flex justify-content-between mb-2">
                  <span>Last Update:</span>
                  <span className="fw-bold">{new Date().toLocaleTimeString()}</span>
                </div>
                <div className="d-flex justify-content-between mb-2">
                  <span>Update Frequency:</span>
                  <span>Every 30 seconds</span>
                </div>
                <div className="d-flex justify-content-between mb-2">
                  <span>Active Connections:</span>
                  <span className="badge bg-success">{tourists.length}</span>
                </div>
                <div className="d-flex justify-content-between mb-2">
                  <span>Coverage Area:</span>
                  <span>Delhi NCR</span>
                </div>
                <div className="d-flex justify-content-between">
                  <span>System Status:</span>
                  <span className="badge bg-success">● Online</span>
                </div>
              </div>
            </div>
          </div>

          {/* Heat Map Insights */}
          <div className="card mt-3">
            <div className="card-header">
              <h5 className="mb-0">💡 Insights</h5>
            </div>
            <div className="card-body">
              <div className="small">
                {(() => {
                  const totalTourists = tourists.length;
                  const safeTourists = tourists.filter(t => t.safetyScore >= 80).length;
                  const riskTourists = tourists.filter(t => t.safetyScore < 70).length;

                  return (
                    <div>
                      <div className="mb-2">
                        <strong className="text-success">
                          ✅ {Math.round((safeTourists / totalTourists) * 100) || 0}% of tourists are in safe zones
                        </strong>
                      </div>

                      {riskTourists > 0 && (
                        <div className="mb-2">
                          <strong className="text-warning">
                            ⚠️ {riskTourists} tourists need attention
                          </strong>
                        </div>
                      )}

                      <div className="mb-2">
                        <strong className="text-info">
                          📈 Tourist density is highest in tourist district
                        </strong>
                      </div>

                      <div>
                        <strong className="text-muted">
                          🕐 Peak activity hours: 10 AM - 6 PM
                        </strong>
                      </div>
                    </div>
                  );
                })()}
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Data Table */}
      <div className="row mt-4">
        <div className="col-12">
          <div className="card">
            <div className="card-header">
              <h5 className="mb-0">📋 Tourist Location Data</h5>
            </div>
            <div className="card-body">
              <div className="table-responsive">
                <table className="table table-sm">
                  <thead>
                    <tr>
                      <th>Tourist</th>
                      <th>Zone</th>
                      <th>Coordinates</th>
                      <th>Safety Score</th>
                      <th>Status</th>
                    </tr>
                  </thead>
                  <tbody>
                    {tourists.map((tourist) => (
                      <tr key={tourist.id}>
                        <td>{tourist.name}</td>
                        <td>
                          <span className={`badge ${
                            tourist.zone === 'tourist-district' ? 'bg-success' :
                            tourist.zone === 'market-area' ? 'bg-warning' : 'bg-danger'
                          }`}>
                            {tourist.zone.replace('-', ' ')}
                          </span>
                        </td>
                        <td>
                          <small>{tourist.lat.toFixed(4)}, {tourist.lng.toFixed(4)}</small>
                        </td>
                        <td>
                          <span className={`badge ${
                            tourist.safetyScore >= 85 ? 'bg-success' :
                            tourist.safetyScore >= 70 ? 'bg-warning' : 'bg-danger'
                          }`}>
                            {tourist.safetyScore}%
                          </span>
                        </td>
                        <td>
                          <span className="badge bg-primary">Active</span>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Heatmap;
