import React, { useEffect, useRef, useState } from 'react';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';
import './Map.css';

// Fix for default markers in Leaflet with React
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: require('leaflet/dist/images/marker-icon-2x.png'),
  iconUrl: require('leaflet/dist/images/marker-icon.png'),
  shadowUrl: require('leaflet/dist/images/marker-shadow.png'),
});

const Map = () => {
  const mapRef = useRef(null);
  const mapInstance = useRef(null);
  const zonesLayer = useRef(null);
  const markersGroup = useRef(null);
  
  const [selectedZone, setSelectedZone] = useState(null);
  const [activeFilter, setActiveFilter] = useState('all');
  const [viewType, setViewType] = useState('attraction');
  const [showLegend, setShowLegend] = useState(true);
  const [showAttractions, setShowAttractions] = useState(true);
  const [mapReady, setMapReady] = useState(false);
  const [showStatistics, setShowStatistics] = useState(false); // New state for statistics

  // Enhanced Tourist Attraction Zones with more details
  const touristZones = [
    {
      id: 'heritage-zone',
      name: 'Heritage Zone (Old Delhi)',
      type: 'heritage',
      color: '#8B4513',
      description: 'Step into the heart of Mughal Delhi with centuries-old monuments, bustling markets, and authentic cultural experiences. This UNESCO heritage area showcases the grandeur of medieval Islamic architecture.',
      safetyRating: 4.2,
