  ];

  // Calculate statistics
  const calculateStatistics = () => {
    const zones = viewType === 'attraction' ? touristZones : safetyZones;
    
    if (viewType === 'attraction') {
      const totalAttractions = zones.reduce((sum, zone) => sum + zone.attractions.length, 0);
      const totalVisitors = zones.reduce((sum, zone) => 
        sum + zone.attractions.reduce((zoneSum, attraction) => 
          zoneSum + parseInt(attraction.visitors.replace(/\D/g, '')), 0), 0);
      const avgSafety = (zones.reduce((sum, zone) => sum + zone.safetyRating, 0) / zones.length).toFixed(1);
      const totalHotels = zones.reduce((sum, zone) => sum + (zone.nearbyHotels || 0), 0);
      const totalRestaurants = zones.reduce((sum, zone) => sum + (zone.restaurants || 0), 0);
      const popularZone = zones.reduce((max, zone) => 
        zone.popularityScore > max.popularityScore ? zone : max);
      
      return {
        totalZones: zones.length,
        totalAttractions,
        dailyVisitors: totalVisitors,
        avgSafetyRating: avgSafety,
        totalHotels,
        totalRestaurants,
        mostPopular: popularZone.name,
        heritageZones: zones.filter(z => z.type === 'heritage').length,
        modernZones: zones.filter(z => z.type === 'modern').length,
        spiritualZones: zones.filter(z => z.type === 'spiritual').length,
        avgAccessibility: (zones.reduce((sum, zone) => sum + (zone.accessibilityRating || 0), 0) / zones.length).toFixed(1)
      };
    } else {
      const avgResponseTime = zones.reduce((sum, zone) => 
        sum + parseInt((zone.responseTime || '5 minutes').replace(/\D/g, '')), 0) / zones.length;
      const safeZones = zones.filter(z => z.type === 'safe').length;
      const moderateZones = zones.filter(z => z.type === 'moderate').length;
      const riskyZones = zones.filter(z => z.type === 'risky').length;
      
      return {
        totalZones: zones.length,
        safeZones,
        moderateZones,
        riskyZones,
        avgResponseTime: `${avgResponseTime.toFixed(0)} min`,
        totalEmergencyContacts: zones.reduce((sum, zone) => sum + (zone.emergencyContacts?.length || 0), 0),
        safetyFeatures: zones.reduce((sum, zone) => sum + (zone.safetyFeatures?.length || 0), 0)
      };
    }
  };

  // Initialize map (same as before)
  useEffect(() => {
    if (!mapRef.current) return;
    
    const mapContainer = mapRef.current;
    if (mapContainer.offsetWidth === 0 || mapContainer.offsetHeight === 0) {
      return;
    }

    if (!mapInstance.current) {
      try {
        mapInstance.current = L.map(mapRef.current, {
          center: [28.6139, 77.2090],
          zoom: 11,
          minZoom: 9,
          maxZoom: 16,
          zoomControl: true,
          scrollWheelZoom: true,
          attributionControl: true,
          renderer: L.svg()
        });

        L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
          attribution: '© OpenStreetMap contributors',
          maxZoom: 18
        }).addTo(mapInstance.current);

        zonesLayer.current = L.layerGroup().addTo(mapInstance.current);
        markersGroup.current = L.layerGroup().addTo(mapInstance.current);

        setMapReady(true);

        setTimeout(() => {
          if (mapInstance.current) {
            mapInstance.current.invalidateSize();
            addZones();
            if (showAttractions && viewType === 'attraction') {
              addAttractionMarkers();
            }
          }
        }, 200);

      } catch (error) {
        console.error('Map initialization error:', error);
      }
    }

    return () => {
      if (mapInstance.current) {
        try {
          mapInstance.current.remove();
          mapInstance.current = null;
          setMapReady(false);
        } catch (error) {
          console.error('Map cleanup error:', error);
        }
      }
    };
  }, []);

  // Other useEffects (same as before)
  useEffect(() => {
    if (!mapReady) return;

    try {
      if (zonesLayer.current) {
        zonesLayer.current.clearLayers();
        addZones();
      }
      if (markersGroup.current) {
        markersGroup.current.clearLayers();
        if (showAttractions && viewType === 'attraction') {
          addAttractionMarkers();
        }
      }
    } catch (error) {
      console.error('Zone update error:', error);
    }
  }, [activeFilter, viewType, showAttractions, mapReady]);

  useEffect(() => {
    if (!mapReady) return;
    const timer = setTimeout(() => {
      if (mapInstance.current) {
        try {
          mapInstance.current.invalidateSize();
        } catch (error) {
          console.error('Map invalidation error:', error);
        }
      }
    }, 300);
    return () => clearTimeout(timer);
  }, [showLegend, selectedZone, mapReady, showStatistics]);

  // Zone functions (same implementation as before)
  const addZones = () => {
    if (!zonesLayer.current || !mapReady) return;
    // Same implementation as before
