                        </div>
                        
                        <div className="stat-card info">
                          <div className="stat-icon">⏱️</div>
                          <div className="stat-content">
                            <span className="stat-number">{statistics.avgResponseTime}</span>
                            <span className="stat-label">Response Time</span>
                          </div>
                        </div>
                      </div>
                    </div>

                    <div className="stats-details">
                      <div className="stats-section">
                        <h4>🚨 Emergency Resources</h4>
                        <div className="stats-list">
                          <div className="stats-item">
                            <span className="stats-icon">📞</span>
                            <span className="stats-text">Emergency Contacts: {statistics.totalEmergencyContacts}</span>
                          </div>
                          <div className="stats-item">
                            <span className="stats-icon">🛡️</span>
                            <span className="stats-text">Safety Features: {statistics.safetyFeatures}</span>
                          </div>
                        </div>
                      </div>
                    </div>
                  </>
                )}
              </div>
            </div>
          ) : selectedZone ? (
            // Enhanced Zone Details
            <div className="zone-details-content">
              <div className="zone-details-header" style={{ backgroundColor: selectedZone.color }}>
                <h3>{selectedZone.name}</h3>
                <div className="zone-badges">
                  <span className="zone-type-badge">{selectedZone.type}</span>
                  {selectedZone.safetyRating && (
                    <span className="safety-badge">🛡️ {selectedZone.safetyRating}</span>
                  )}
                </div>
                <button 
                  className="close-details-btn"
                  onClick={() => setSelectedZone(null)}
                >
                  ✖
                </button>
              </div>
              
              <div className="zone-details-body">
                {/* Enhanced Description */}
                <div className="zone-overview-section">
                  <p className="zone-description-enhanced">{selectedZone.description}</p>
                  
                  {/* Quick Stats */}
                  <div className="quick-stats">
                    <div className="quick-stat">
                      <span className="quick-stat-icon">⏰</span>
                      <span className="quick-stat-text">{selectedZone.bestTime}</span>
                    </div>
                    <div className="quick-stat">
                      <span className="quick-stat-icon">🕐</span>
                      <span className="quick-stat-text">{selectedZone.avgDuration}</span>
                    </div>
                    <div className="quick-stat">
                      <span className="quick-stat-icon">👥</span>
                      <span className="quick-stat-text">{selectedZone.crowdLevel}</span>
                    </div>
                  </div>
                </div>

                {/* Attractions Section */}
                {selectedZone.attractions && (
                  <div className="attractions-section-enhanced">
                    <h4>🎯 Top Attractions ({selectedZone.attractions.length})</h4>
                    <div className="attractions-list-enhanced">
                      {selectedZone.attractions.map((attraction, index) => (
                        <div key={index} className="attraction-card-enhanced">
                          <div className="attraction-header">
                            <h5 className="attraction-name">{attraction.name}</h5>
                            <div className="attraction-meta">
                              <span className="attraction-rating">{attraction.rating}⭐</span>
                              <span className="attraction-fee">{attraction.entryFee}</span>
                            </div>
                          </div>
                          <p className="attraction-type">{attraction.type}</p>
                          <div className="attraction-details">
                            <div className="detail-item">
                              <span className="detail-icon">🕐</span>
                              <span className="detail-text">{attraction.timings}</span>
                            </div>
                            <div className="detail-item">
                              <span className="detail-icon">👥</span>
                              <span className="detail-text">{attraction.visitors}</span>
                            </div>
                          </div>
                        </div>
                      ))}
                    </div>
                  </div>
                )}

                {/* Facilities & Services */}
                {selectedZone.facilities && (
                  <div className="facilities-section-enhanced">
                    <h4>🏢 Facilities & Services</h4>
                    <div className="facilities-grid">
                      {selectedZone.facilities.map((facility, index) => (
                        <div key={index} className="facility-item">
                          <span className="facility-text">{facility}</span>
                        </div>
                      ))}
                    </div>
                  </div>
                )}

                {/* Transportation */}
                {selectedZone.transportation && (
                  <div className="transportation-section-enhanced">
                    <h4>🚇 How to Reach</h4>
                    <div className="transportation-list-enhanced">
                      {selectedZone.transportation.map((transport, index) => (
                        <div key={index} className="transport-item-enhanced">
                          <span className="transport-icon">🚊</span>
                          <span className="transport-text">{transport}</span>
                        </div>
                      ))}
                    </div>
                  </div>
                )}

                {/* Tips Section */}
                {selectedZone.tips && (
                  <div className="tips-section-enhanced">
                    <h4>💡 Travel Tips</h4>
                    <div className="tips-list-enhanced">
                      {selectedZone.tips.map((tip, index) => (
                        <div key={index} className="tip-item-enhanced">
                          <span className="tip-bullet">💡</span>
                          <span className="tip-text">{tip}</span>
                        </div>
                      ))}
                    </div>
                  </div>
                )}

                {/* Additional Info for Tourist Zones */}
                {viewType === 'attraction' && (
                  <div className="additional-info">
                    <h4>📋 Additional Information</h4>
                    <div className="info-grid">
                      {selectedZone.nearbyHotels && (
                        <div className="info-item">
                          <span className="info-icon">🏨</span>
                          <span className="info-text">Nearby Hotels: {selectedZone.nearbyHotels}</span>
                        </div>
                      )}
                      {selectedZone.restaurants && (
                        <div className="info-item">
                          <span className="info-icon">🍽️</span>
                          <span className="info-text">Restaurants: {selectedZone.restaurants}</span>
                        </div>
                      )}
                      {selectedZone.popularityScore && (
                        <div className="info-item">
                          <span className="info-icon">⭐</span>
                          <span className="info-text">Popularity: {selectedZone.popularityScore}/100</span>
                        </div>
                      )}
                    </div>
                  </div>
                )}

                {/* Safety Features for Safety Zones */}
                {viewType === 'safety' && selectedZone.safetyFeatures && (
                  <div className="safety-features-section">
                    <h4>🛡️ Safety Features</h4>
                    <div className="safety-features-list">
                      {selectedZone.safetyFeatures.map((feature, index) => (
                        <div key={index} className="safety-feature-item">
                          <span className="safety-feature-bullet">✓</span>
                          <span className="safety-feature-text">{feature}</span>
                        </div>
                      ))}
                    </div>
                  </div>
                )}
              </div>
            </div>
          ) : (
            // Default Empty State
            <div className="no-zone-selected-enhanced">
              <div className="empty-state-content">
                <div className="empty-state-icon">🗺️</div>
                <h3>Explore Delhi's {viewType === 'safety' ? 'Safety Zones' : 'Tourist Zones'}</h3>
                <p>
                  {viewType === 'safety' 
                    ? 'Click on any colored zone to view detailed safety information, risk factors, and emergency contacts.'
                    : 'Click on any colored zone on the map to discover attractions, facilities, and travel tips.'
                  }
                </p>
                
                <div className="action-buttons">
                  <button 
                    className="action-button primary"
                    onClick={() => setShowStatistics(true)}
                  >
                    📊 View Statistics
                  </button>
                  <button 
                    className="action-button secondary"
                    onClick={() => setShowLegend(!showLegend)}
                  >
                    📋 {showLegend ? 'Hide' : 'Show'} Legend
                  </button>
                </div>

                {/* Quick Overview */}
                <div className="quick-overview">
                  <h4>Quick Overview</h4>
                  <div className="overview-stats">
                    <div className="overview-stat">
                      <span className="overview-number">{getFilteredZones().length}</span>
                      <span className="overview-label">Zones Available</span>
                    </div>
                    {viewType === 'attraction' && (
                      <div className="overview-stat">
                        <span className="overview-number">
                          {getFilteredZones().reduce((sum, zone) => sum + zone.attractions.length, 0)}
                        </span>
                        <span className="overview-label">Attractions</span>
                      </div>
                    )}
                  </div>
                </div>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Map;
