import React from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import { authService } from '../../services/auth';
import { toast } from 'react-toastify';

const Navbar = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const userType = authService.getUserType();
  const isAuthenticated = authService.isAuthenticated();

  const handleLogout = () => {
    if (window.confirm('Are you sure you want to logout?')) {
      authService.removeToken();
      toast.success('Logged out successfully');
      navigate('/login');
    }
  };

  // Don't show navbar on login/register pages
  if (['/login', '/register'].includes(location.pathname)) {
    return null;
  }

  const isActivePath = (path) => {
    return location.pathname === path ? 'nav-link active' : 'nav-link';
  };

  const getUserDisplayName = () => {
    const userInfo = authService.getUserInfo();
    return userInfo?.name || (userType === 'tourist' ? 'Tourist User' : 'Officer Smith');
  };

  return (
    <>
      <nav className="navbar navbar-expand-lg navbar-light bg-white shadow-sm sticky-top">
        <div className="container-fluid">
          {/* Brand */}
          <a 
            className="navbar-brand d-flex align-items-center" 
            href="#" 
            onClick={(e) => { e.preventDefault(); navigate('/'); }}
            style={{ cursor: 'pointer' }}
          >
            <span className="me-2" style={{ fontSize: '1.5rem' }}>ğŸ›¡ï¸</span>
            <div>
              <strong>Tourist Safety System</strong>
              <div className="small text-muted">Real-time Monitoring & Protection</div>
            </div>
          </a>

          {/* Mobile toggle button */}
          <button 
            className="navbar-toggler" 
            type="button" 
            data-bs-toggle="collapse" 
            data-bs-target="#navbarNav"
            aria-controls="navbarNav" 
            aria-expanded="false" 
            aria-label="Toggle navigation"
          >
            <span className="navbar-toggler-icon"></span>
          </button>

          <div className="collapse navbar-collapse" id="navbarNav">
            {/* Navigation Links */}
            <ul className="navbar-nav me-auto">
              {isAuthenticated && userType === 'tourist' && (
                <>
                  <li className="nav-item">
                    <a 
                      className={isActivePath('/tourist/home')}
                      href="#" 
                      onClick={(e) => { e.preventDefault(); navigate('/tourist/home'); }}
                    >
                      <span className="me-1">ğŸ </span> Home
                    </a>
                  </li>
                  <li className="nav-item">
                    <a 
                      className={isActivePath('/tourist/map')}
                      href="#" 
                      onClick={(e) => { e.preventDefault(); navigate('/tourist/map'); }}
                    >
                      <span className="me-1">ğŸ—ºï¸</span> Safety Map
                    </a>
                  </li>
                  <li className="nav-item">
                    <a 
                      className={isActivePath('/tourist/qr')}
                      href="#" 
                      onClick={(e) => { e.preventDefault(); navigate('/tourist/qr'); }}
                    >
                      <span className="me-1">ğŸ†”</span> My Digital ID
                    </a>
                  </li>
                  <li className="nav-item">
                    <a 
                      className={`${isActivePath('/tourist/sos')} text-danger`}
                      href="#" 
                      onClick={(e) => { e.preventDefault(); navigate('/tourist/sos'); }}
                    >
                      <span className="me-1">ğŸ†˜</span> Emergency SOS
                    </a>
                  </li>
                </>
              )}

              {isAuthenticated && userType === 'police' && (
                <>
                  <li className="nav-item">
                    <a 
                      className={isActivePath('/police/dashboard')}
                      href="#" 
                      onClick={(e) => { e.preventDefault(); navigate('/police/dashboard'); }}
                    >
                      <span className="me-1">ğŸ“Š</span> Dashboard
                    </a>
                  </li>
                  <li className="nav-item">
                    <a 
                      className={isActivePath('/police/tourists')}
                      href="#" 
                      onClick={(e) => { e.preventDefault(); navigate('/police/tourists'); }}
                    >
                      <span className="me-1">ğŸ‘¥</span> Tourists
                    </a>
                  </li>
                  <li className="nav-item">
                    <a 
                      className={isActivePath('/police/heatmap')}
                      href="#" 
                      onClick={(e) => { e.preventDefault(); navigate('/police/heatmap'); }}
                    >
                      <span className="me-1">ğŸ—ºï¸</span> Heatmap
                    </a>
                  </li>
                  <li className="nav-item">
                    <a 
                      className={isActivePath('/police/alerts')}
                      href="#" 
                      onClick={(e) => { e.preventDefault(); navigate('/police/alerts'); }}
                    >
                      <span className="me-1">ğŸš¨</span> Alerts
                    </a>
                  </li>
                  <li className="nav-item">
                    <a 
                      className={isActivePath('/police/scanner')}
                      href="#" 
                      onClick={(e) => { e.preventDefault(); navigate('/police/scanner'); }}
                    >
                      <span className="me-1">ğŸ“·</span> QR Scanner
                    </a>
                  </li>
                </>
              )}
            </ul>

            {/* Right side - User menu and actions */}
            {isAuthenticated && (
              <ul className="navbar-nav ms-auto">
                {/* System Status Indicator */}
                <li className="nav-item me-3">
                  <div className="d-flex align-items-center">
                    <span className="badge bg-success me-2 pulse">â—</span>
                    <small className="text-muted">System Online</small>
                  </div>
                </li>

                {/* Emergency Button for Tourists */}
                {userType === 'tourist' && (
                  <li className="nav-item me-3">
                    <button 
                      className="btn btn-danger btn-sm emergency-btn"
                      onClick={() => navigate('/tourist/sos')}
                    >
                      ğŸ†˜ Emergency
                    </button>
                  </li>
                )}

                {/* Notification Bell for Police */}
                {userType === 'police' && (
                  <li className="nav-item me-3">
                    <div className="position-relative">
                      <button 
                        className="btn btn-outline-primary btn-sm"
                        onClick={() => navigate('/police/alerts')}
                      >
                        ğŸ”” 
                      </button>
                      <span className="position-absolute top-0 start-100 translate-middle badge rounded-pill bg-danger">
                        3
                      </span>
                    </div>
                  </li>
                )}

                {/* User Dropdown */}
                <li className="nav-item dropdown">
                  <a 
                    className="nav-link dropdown-toggle d-flex align-items-center" 
                    href="#" 
                    id="navbarDropdown" 
                    role="button" 
                    data-bs-toggle="dropdown"
                    aria-expanded="false"
                  >
                    <span className="me-2">
                      {userType === 'tourist' ? 'ğŸ§³' : 'ğŸ‘®'}
                    </span>
                    <div>
                      <div>{getUserDisplayName()}</div>
                      <small className="text-muted">{userType.charAt(0).toUpperCase() + userType.slice(1)}</small>
                    </div>
                  </a>
                  <ul className="dropdown-menu dropdown-menu-end shadow">
                    <li>
                      <h6 className="dropdown-header">
                        {userType === 'tourist' ? 'ğŸ§³ Tourist Account' : 'ğŸ‘® Police Account'}
                      </h6>
                    </li>
                    <li><hr className="dropdown-divider" /></li>

                    {userType === 'tourist' && (
                      <>
                        <li>
                          <a 
                            className="dropdown-item" 
                            href="#" 
                            onClick={(e) => { e.preventDefault(); navigate('/tourist/home'); }}
                          >
                            <span className="me-2">ğŸ </span> My Dashboard
                          </a>
                        </li>
                        <li>
                          <a 
                            className="dropdown-item" 
                            href="#" 
                            onClick={(e) => { e.preventDefault(); navigate('/tourist/qr'); }}
                          >
                            <span className="me-2">ğŸ†”</span> Digital ID
                          </a>
                        </li>
                        <li>
                          <a 
                            className="dropdown-item" 
                            href="#" 
                            onClick={(e) => { e.preventDefault(); navigate('/tourist/map'); }}
                          >
                            <span className="me-2">ğŸ—ºï¸</span> Safety Map
                          </a>
                        </li>
                        <li>
                          <a 
                            className="dropdown-item" 
                            href="#" 
                            onClick={() => toast.info('Profile settings coming soon!')}
                          >
                            <span className="me-2">âš™ï¸</span> Settings
                          </a>
                        </li>
                      </>
                    )}

                    {userType === 'police' && (
                      <>
                        <li>
                          <a 
                            className="dropdown-item" 
                            href="#" 
                            onClick={(e) => { e.preventDefault(); navigate('/police/dashboard'); }}
                          >
                            <span className="me-2">ğŸ“Š</span> Command Center
                          </a>
                        </li>
                        <li>
                          <a 
                            className="dropdown-item" 
                            href="#" 
                            onClick={(e) => { e.preventDefault(); navigate('/police/alerts'); }}
                          >
                            <span className="me-2">ğŸš¨</span> Active Alerts
                          </a>
                        </li>
                        <li>
                          <a 
                            className="dropdown-item" 
                            href="#" 
                            onClick={(e) => { e.preventDefault(); navigate('/police/scanner'); }}
                          >
                            <span className="me-2">ğŸ“·</span> QR Scanner
                          </a>
                        </li>
                        <li>
                          <a 
                            className="dropdown-item" 
                            href="#" 
                            onClick={() => toast.info('System settings available in admin panel')}
                          >
                            <span className="me-2">âš™ï¸</span> System Config
                          </a>
                        </li>
                      </>
                    )}

                    <li><hr className="dropdown-divider" /></li>
                    <li>
                      <a 
                        className="dropdown-item" 
                        href="#" 
                        onClick={() => toast.info('Help documentation: Tourist Safety System v2.0')}
                      >
                        <span className="me-2">â“</span> Help & Support
                      </a>
                    </li>
                    <li>
                      <a 
                        className="dropdown-item" 
                        href="#" 
                        onClick={() => toast.info('Version 2.0.1 - Last updated: Sept 15, 2025')}
                      >
                        <span className="me-2">â„¹ï¸</span> About
                      </a>
                    </li>
                    <li><hr className="dropdown-divider" /></li>
                    <li>
                      <a 
                        className="dropdown-item text-danger" 
                        href="#" 
                        onClick={(e) => { e.preventDefault(); handleLogout(); }}
                      >
                        <span className="me-2">ğŸšª</span> Logout
                      </a>
                    </li>
                  </ul>
                </li>
              </ul>
            )}

            {/* Login button for non-authenticated users */}
            {!isAuthenticated && (
              <ul className="navbar-nav ms-auto">
                <li className="nav-item me-2">
                  <button 
                    className="btn btn-outline-primary"
                    onClick={() => navigate('/register')}
                  >
                    ğŸ“ Register
                  </button>
                </li>
                <li className="nav-item">
                  <button 
                    className="btn btn-primary"
                    onClick={() => navigate('/login')}
                  >
                    ğŸ” Login
                  </button>
                </li>
              </ul>
            )}
          </div>
        </div>
      </nav>

      {/* Custom CSS for navbar styling and animations */}
      <style jsx>{`
        .nav-link.active {
          background-color: rgba(13, 110, 253, 0.1) !important;
          border-radius: 6px;
          font-weight: 600;
          color: #0d6efd !important;
        }

        .nav-link:hover {
          background-color: rgba(13, 110, 253, 0.05);
          border-radius: 6px;
          transition: all 0.2s ease;
        }

        .emergency-btn {
          animation: emergency-pulse 2s infinite;
          font-weight: 600;
        }

        .pulse {
          animation: status-pulse 2s infinite;
        }

        @keyframes emergency-pulse {
          0% { 
            box-shadow: 0 0 0 0 rgba(220, 53, 69, 0.7);
            transform: scale(1);
          }
          50% {
            transform: scale(1.05);
          }
          70% { 
            box-shadow: 0 0 0 10px rgba(220, 53, 69, 0); 
          }
          100% { 
            box-shadow: 0 0 0 0 rgba(220, 53, 69, 0);
            transform: scale(1);
          }
        }

        @keyframes status-pulse {
          0%, 100% { opacity: 1; }
          50% { opacity: 0.5; }
        }

        .navbar-brand:hover {
          transform: translateY(-1px);
          transition: transform 0.2s ease;
        }

        .dropdown-menu {
          border: none;
          border-radius: 12px;
          padding: 8px 0;
        }

        .dropdown-item {
          border-radius: 8px;
          margin: 2px 8px;
          padding: 8px 12px;
        }

        .dropdown-item:hover {
          background-color: rgba(13, 110, 253, 0.1);
          color: #0d6efd;
        }

        .navbar {
          backdrop-filter: blur(10px);
          background-color: rgba(255, 255, 255, 0.95) !important;
        }

        .badge {
          font-size: 0.7rem;
        }

        @media (max-width: 768px) {
          .navbar-brand div {
            display: none;
          }
          .navbar-brand span {
            font-size: 1.2rem;
          }
        }
      `}</style>
    </>
  );
};

export default Navbar;
