import React, { useState, useEffect, useCallback } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';

// Common Components
import Navbar from './components/common/Navbar';

// Tourist Components
import Login from './components/tourist/Login';
import Register from './components/tourist/Register';
import TouristHome from './components/tourist/Home';
import TouristMap from './components/tourist/Map';
import QRDisplay from './components/tourist/QRCode';
import SOS from './components/tourist/SOS';
import SafetyCheck from './components/tourist/SafetyCheck';

// Police Components  
import PoliceDashboard from './components/police/Dashboard';
import TouristList from './components/police/TouristList';
import Heatmap from './components/police/Heatmap';
import AlertPanel from './components/police/AlertPanel';

// Services
import { authService } from './services/auth';

function App() {
  const [authState, setAuthState] = useState({
    isAuthenticated: false,
    userType: null,
    loading: true,
    initialized: false
  });

  // Memoized auth state updater to prevent re-renders
  const updateAuthState = useCallback((isAuth, userType) => {
    setAuthState(prevState => {
      // Only update if there's actually a change
      if (prevState.isAuthenticated === isAuth && prevState.userType === userType) {
        return prevState;
      }
      
      console.log('Auth state updated:', { isAuth, userType });
      return {
        ...prevState,
        isAuthenticated: isAuth,
        userType: userType,
        loading: false
      };
    });
  }, []);

  // Initialize authentication only once
  useEffect(() => {
    let isMounted = true;
    
    const initializeAuth = async () => {
      try {
        // Small delay to prevent flashing
        await new Promise(resolve => setTimeout(resolve, 50));
        
        if (!isMounted) return;
        
        const isAuth = authService.isAuthenticated();
        const type = authService.getUserType();
        
        console.log('App initialization - Auth check:', { isAuth, type });
        
        if (isMounted) {
          setAuthState({
            isAuthenticated: isAuth,
            userType: type,
            loading: false,
            initialized: true
          });
        }
      } catch (error) {
        console.error('Auth initialization error:', error);
        if (isMounted) {
          setAuthState({
            isAuthenticated: false,
            userType: null,
            loading: false,
            initialized: true
          });
        }
      }
    };

    if (!authState.initialized) {
      initializeAuth();
    }

    return () => {
      isMounted = false;
    };
  }, []); // Empty dependency array - only run once

  const ProtectedRoute = ({ children, requiredUserType }) => {
    // Don't render anything while loading
    if (authState.loading || !authState.initialized) {
      return (
        <div className="d-flex justify-content-center align-items-center min-vh-100">
          <div className="spinner-border text-primary" role="status">
            <span className="visually-hidden">Loading...</span>
          </div>
        </div>
      );
    }

    if (!authState.isAuthenticated) {
      return <Navigate to="/login" replace />;
    }
    
    if (requiredUserType && authState.userType !== requiredUserType) {
      return <Navigate to="/login" replace />;
    }
    
    return children;
  };

  const DefaultRoute = () => {
    if (authState.loading || !authState.initialized) {
      return (
        <div className="d-flex justify-content-center align-items-center min-vh-100">
          <div className="spinner-border text-primary" role="status">
            <span className="visually-hidden">Loading...</span>
          </div>
        </div>
      );
    }

    if (authState.isAuthenticated) {
      if (authState.userType === 'tourist') {
        return <Navigate to="/tourist/home" replace />;
      } else if (authState.userType === 'police') {
        return <Navigate to="/police/dashboard" replace />;
      }
    }
    
    return <Navigate to="/login" replace />;
  };

  // Show loading screen during initialization
  if (!authState.initialized) {
    return (
      <div className="d-flex justify-content-center align-items-center min-vh-100 bg-light">
        <div className="text-center">
          <div className="spinner-border text-primary mb-3" role="status" style={{ width: '3rem', height: '3rem' }}>
            <span className="visually-hidden">Loading...</span>
          </div>
          <h5 className="text-primary">🛡️ Tourist Safety System</h5>
          <p className="text-muted">Initializing application...</p>
        </div>
      </div>
    );
  }

  return (
    <Router>
      <div className="App">
        <Navbar 
          authState={authState}
          updateAuthState={updateAuthState} 
        />
        <div className="main-content">
          <Routes>
            {/* Public Routes */}
            <Route 
              path="/login" 
              element={
                <Login 
                  authState={authState}
                  updateAuthState={updateAuthState} 
                />
              } 
            />
            <Route path="/register" element={<Register />} />
            
            {/* Tourist Routes */}
            <Route 
              path="/tourist/home" 
              element={
                <ProtectedRoute requiredUserType="tourist">
                  <TouristHome />
                </ProtectedRoute>
              } 
            />
            <Route 
              path="/tourist/map" 
              element={
                <ProtectedRoute requiredUserType="tourist">
                  <TouristMap />
                </ProtectedRoute>
              } 
            />
            <Route 
              path="/tourist/qr" 
              element={
                <ProtectedRoute requiredUserType="tourist">
                  <QRDisplay />
                </ProtectedRoute>
              } 
            />
            <Route 
              path="/tourist/sos" 
              element={
                <ProtectedRoute requiredUserType="tourist">
                  <SOS />
                </ProtectedRoute>
              } 
            />
            <Route 
              path="/tourist/safety-check" 
              element={
                <ProtectedRoute requiredUserType="tourist">
                  <SafetyCheck />
                </ProtectedRoute>
              } 
            />
            
            {/* Police Routes */}
            <Route 
              path="/police/dashboard" 
              element={
                <ProtectedRoute requiredUserType="police">
                  <PoliceDashboard />
                </ProtectedRoute>
              } 
            />
            <Route 
              path="/police/tourists" 
              element={
                <ProtectedRoute requiredUserType="police">
                  <TouristList />
                </ProtectedRoute>
              } 
            />
            <Route 
              path="/police/heatmap" 
              element={
                <ProtectedRoute requiredUserType="police">
                  <Heatmap />
                </ProtectedRoute>
              } 
            />
            <Route 
              path="/police/alerts" 
              element={
                <ProtectedRoute requiredUserType="police">
                  <AlertPanel />
                </ProtectedRoute>
              } 
            />
            
            {/* Default Route */}
            <Route path="/" element={<DefaultRoute />} />
            
            {/* 404 Route */}
            <Route path="*" element={<Navigate to="/" />} />
          </Routes>
        </div>
        
        <ToastContainer 
          position="top-right" 
          autoClose={2000}
          hideProgressBar={false}
          newestOnTop={false}
          closeOnClick
          rtl={false}
          pauseOnFocusLoss={false}
          draggable
          pauseOnHover={false}
          theme="light"
        />
      </div>
    </Router>
  );
}

export default App;
