    return {
      totalTourists,
      avgSafetyScore,
      totalAlerts,
      totalPoliceStations,
      avgResponseTime,
      riskDistribution,
      peakHour: getCurrentPeakHour(),
      mostActiveZone: getMostActiveZone(),
      safestZone: getSafestZone()
