        return 0.3;
      case 'activity':
        if (zone.activity === 'very-high') return 1.0;
        if (zone.activity === 'high') return 0.8;
        if (zone.activity === 'medium') return 0.5;
        return 0.2;
      default: // density
        return Math.min(tourists / 100, 1.0);
    }
  };

