import React, { useState, useEffect } from 'react';
import { toast } from 'react-toastify';
import { geolocationService } from '../../services/geolocation';
import { mockData } from '../../services/api';
import Loading from '../common/Loading';

const Map = () => {
  const [loading, setLoading] = useState(true);
  const [currentLocation, setCurrentLocation] = useState(null);
  const [safetyZones, setSafetyZones] = useState([]);
  const [nearbyTourists, setNearbyTourists] = useState([]);

  useEffect(() => {
    loadMapData();
    getCurrentLocation();
  }, []);

  const loadMapData = async () => {
    try {
      setLoading(true);
      setSafetyZones(mockData.safetyZones);
      
      setNearbyTourists([
        { id: 1, name: 'Tourist A', distance: '0.5 km', status: 'safe' },
        { id: 2, name: 'Tourist B', distance: '1.2 km', status: 'safe' },
        { id: 3, name: 'Tourist C', distance: '2.1 km', status: 'caution' }
      ]);
      
      await new Promise(resolve => setTimeout(resolve, 1000));
    } catch (error) {
      console.error('Error loading map data:', error);
      toast.error('Failed to load map data');
    } finally {
      setLoading(false);
    }
  };

  const getCurrentLocation = async () => {
    try {
      const position = await geolocationService.getEnhancedPosition();
      setCurrentLocation(position);
      toast.success('Location updated successfully');
    } catch (error) {
      console.error('Location error:', error);
      toast.error('Unable to get current location');
    }
  };

  if (loading) {
    return <Loading message="Loading safety map..." size="medium" />;
  }

  return (
    <div className="container-fluid py-4">
      <div className="row">
        <div className="col-12">
          <div className="card">
            <div className="card-header">
              <h3 className="mb-0">🗺️ Interactive Safety Map</h3>
              <p className="mb-0 mt-2">Real-time safety zones and tourist locations</p>
            </div>
          </div>
        </div>
      </div>

      <div className="row mt-4">
        <div className="col-md-8">
          <div className="card h-100">
            <div className="card-header">
              <h5 className="mb-0">🌍 Live Map View</h5>
            </div>
            <div className="card-body">
              <div 
                className="bg-light rounded d-flex align-items-center justify-content-center"
                style={{ height: '500px', background: 'linear-gradient(135deg, #74b9ff 0%, #0984e3 100%)' }}
              >
                <div className="text-center text-white">
                  <div style={{ fontSize: '4rem' }}>🗺️</div>
                  <h4>Interactive Map</h4>
                  <p className="mb-3">Showing safety zones and tourist locations</p>
                  
                  {currentLocation && (
                    <div className="mb-3">
                      <div className="badge bg-success fs-6 mb-2">
                        📍 Your Location: {currentLocation.address}
                      </div>
                    </div>
                  )}
                  
                  <div className="row text-center">
                    {safetyZones.map(zone => (
                      <div key={zone.id} className="col-4 mb-2">
                        <div className={`badge fs-6 ${
                          zone.type === 'safe' ? 'bg-success' :
                          zone.type === 'medium' ? 'bg-warning' : 'bg-danger'
                        }`}>
                          {zone.type === 'safe' ? '🟢' : zone.type === 'medium' ? '🟡' : '🔴'} {zone.name}
                        </div>
                      </div>
                    ))}
                  </div>
                  
                  <button 
                    className="btn btn-light mt-3"
                    onClick={getCurrentLocation}
                  >
                    📍 Update My Location
                  </button>
                </div>
              </div>
            </div>
          </div>
        </div>

        <div className="col-md-4">
          <div className="card mb-3">
            <div className="card-header">
              <h5 className="mb-0">📍 Current Location</h5>
            </div>
            <div className="card-body">
              {currentLocation ? (
                <div>
                  <p><strong>Address:</strong> {currentLocation.address}</p>
                  <p><strong>Coordinates:</strong> {currentLocation.formatted}</p>
                  <p><strong>Accuracy:</strong> ±{Math.round(currentLocation.accuracy)}m</p>
                  <div className="badge bg-success">✅ Location Active</div>
                </div>
              ) : (
                <div className="text-muted text-center">
                  <div style={{ fontSize: '2rem' }}>📍</div>
                  <p>Location not available</p>
                  <button 
                    className="btn btn-primary btn-sm"
                    onClick={getCurrentLocation}
                  >
                    Get Location
                  </button>
                </div>
              )}
            </div>
          </div>

          <div className="card mb-3">
            <div className="card-header">
              <h5 className="mb-0">🛡️ Safety Zones</h5>
            </div>
            <div className="card-body">
              {safetyZones.map(zone => (
                <div key={zone.id} className="mb-3">
                  <div className="d-flex justify-content-between align-items-center mb-1">
                    <strong>{zone.name}</strong>
                    <span className={`badge ${
                      zone.type === 'safe' ? 'bg-success' :
                      zone.type === 'medium' ? 'bg-warning' : 'bg-danger'
                    }`}>
                      {zone.type === 'safe' ? '🟢 Safe' : 
                       zone.type === 'medium' ? '🟡 Caution' : '🔴 Unsafe'}
                    </span>
                  </div>
                  <p className="small text-muted mb-0">{zone.description}</p>
                </div>
              ))}
            </div>
          </div>

          <div className="card">
            <div className="card-header">
              <h5 className="mb-0">👥 Nearby Tourists</h5>
            </div>
            <div className="card-body">
              {nearbyTourists.map(tourist => (
                <div key={tourist.id} className="d-flex justify-content-between align-items-center mb-2">
                  <div>
                    <div>{tourist.name}</div>
                    <div className="small text-muted">{tourist.distance}</div>
                  </div>
                  <span className={`badge ${
                    tourist.status === 'safe' ? 'bg-success' : 'bg-warning'
                  }`}>
                    {tourist.status === 'safe' ? '✅' : '⚠️'}
                  </span>
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>

      <div className="row mt-4">
        <div className="col-12">
          <div className="alert alert-info">
            <h6 className="alert-heading">🗺️ Map Features</h6>
            <div className="row">
              <div className="col-md-3">
                <strong>🟢 Safe Zones:</strong> Well-patrolled areas with security
              </div>
              <div className="col-md-3">
                <strong>🟡 Caution Zones:</strong> Busy areas, stay alert
              </div>
              <div className="col-md-3">
                <strong>🔴 Unsafe Zones:</strong> Avoid especially after dark
              </div>
              <div className="col-md-3">
                <strong>📍 Real-time:</strong> Location updated every 30 seconds
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Map;
