import React from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import { authService } from '../../services/auth';
import { toast } from 'react-toastify';

const Navbar = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const userType = authService.getUserType();
  const isAuthenticated = authService.isAuthenticated();

  const handleLogout = () => {
    if (window.confirm('Are you sure you want to logout?')) {
      // Clear all authentication data
      authService.removeToken();
      toast.success('Logged out successfully');
      
      // Force redirect to login page
      navigate('/login');
      window.location.reload(); // Ensure clean state
    }
  };

  // Don't show navbar on login/register pages
  if (['/login', '/register'].includes(location.pathname)) {
    return null;
  }

  const isActivePath = (path) => {
    return location.pathname === path ? 'nav-link active' : 'nav-link';
  };

  const getUserDisplayName = () => {
    const userInfo = authService.getUserInfo();
    return userInfo?.name || (userType === 'tourist' ? 'Tourist User' : 'Officer Smith');
  };

  return (
    <>
      <nav className="navbar navbar-expand-lg navbar-light bg-white shadow-sm sticky-top">
        <div className="container-fluid">
          {/* Brand */}
          <a 
            className="navbar-brand d-flex align-items-center" 
            href="#" 
            onClick={(e) => { e.preventDefault(); navigate('/'); }}
            style={{ cursor: 'pointer' }}
          >
            <span className="me-2" style={{ fontSize: '1.5rem' }}>üõ°Ô∏è</span>
            <div>
              <strong>Tourist Safety System</strong>
              <div className="small text-muted">Real-time Monitoring & Protection</div>
            </div>
          </a>
          
          {/* Mobile toggle button */}
          <button 
            className="navbar-toggler" 
            type="button" 
            data-bs-toggle="collapse" 
            data-bs-target="#navbarNav"
            aria-controls="navbarNav" 
            aria-expanded="false" 
            aria-label="Toggle navigation"
          >
            <span className="navbar-toggler-icon"></span>
          </button>
          
          <div className="collapse navbar-collapse" id="navbarNav">
            {/* Navigation Links */}
            <ul className="navbar-nav me-auto">
              {isAuthenticated && userType === 'tourist' && (
                <>
                  <li className="nav-item">
                    <a 
                      className={isActivePath('/tourist/home')}
                      href="#" 
                      onClick={(e) => { e.preventDefault(); navigate('/tourist/home'); }}
                    >
                      <span className="me-1">üè†</span> Home
                    </a>
                  </li>
                  <li className="nav-item">
                    <a 
                      className={isActivePath('/tourist/map')}
                      href="#" 
                      onClick={(e) => { e.preventDefault(); navigate('/tourist/map'); }}
                    >
                      <span className="me-1">üó∫Ô∏è</span> Safety Map
                    </a>
                  </li>
                  <li className="nav-item">
                    <a 
                      className={isActivePath('/tourist/qr')}
                      href="#" 
                      onClick={(e) => { e.preventDefault(); navigate('/tourist/qr'); }}
                    >
                      <span className="me-1">üÜî</span> My Digital ID
                    </a>
                  </li>
                  <li className="nav-item">
                    <a 
                      className={`${isActivePath('/tourist/sos')} text-danger`}
                      href="#" 
                      onClick={(e) => { e.preventDefault(); navigate('/tourist/sos'); }}
                    >
                      <span className="me-1">üÜò</span> Emergency SOS
                    </a>
                  </li>
                </>
              )}
              
              {isAuthenticated && userType === 'police' && (
                <>
                  <li className="nav-item">
                    <a 
                      className={isActivePath('/police/dashboard')}
                      href="#" 
                      onClick={(e) => { e.preventDefault(); navigate('/police/dashboard'); }}
                    >
                      <span className="me-1">üìä</span> Dashboard
                    </a>
                  </li>
                  <li className="nav-item">
                    <a 
                      className={isActivePath('/police/tourists')}
                      href="#" 
                      onClick={(e) => { e.preventDefault(); navigate('/police/tourists'); }}
                    >
                      <span className="me-1">üë•</span> Tourists
                    </a>
                  </li>
                  <li className="nav-item">
                    <a 
                      className={isActivePath('/police/heatmap')}
                      href="#" 
                      onClick={(e) => { e.preventDefault(); navigate('/police/heatmap'); }}
                    >
                      <span className="me-1">üó∫Ô∏è</span> Heatmap
                    </a>
                  </li>
                  <li className="nav-item">
                    <a 
                      className={isActivePath('/police/alerts')}
                      href="#" 
                      onClick={(e) => { e.preventDefault(); navigate('/police/alerts'); }}
                    >
                      <span className="me-1">üö®</span> Alerts
                    </a>
                  </li>
                </>
              )}
            </ul>
            
            {/* Right side - User menu and actions */}
            {isAuthenticated && (
              <ul className="navbar-nav ms-auto">
                {/* System Status Indicator */}
                <li className="nav-item me-3">
                  <div className="d-flex align-items-center">
                    <span className="badge bg-success me-2 pulse">‚óè</span>
                    <small className="text-muted">System Online</small>
                  </div>
                </li>
                
                {/* Emergency Button for Tourists */}
                {userType === 'tourist' && (
                  <li className="nav-item me-3">
                    <button 
                      className="btn btn-danger btn-sm emergency-btn"
                      onClick={() => navigate('/tourist/sos')}
                    >
                      üÜò Emergency
                    </button>
                  </li>
                )}
                
                {/* User Info and Logout */}
                <li className="nav-item me-3">
                  <div className="d-flex align-items-center">
                    <span className="me-2">
                      {userType === 'tourist' ? 'üß≥' : 'üëÆ'}
                    </span>
                    <div className="me-3">
                      <div className="small fw-bold">{getUserDisplayName()}</div>
                      <div className="small text-muted">{userType?.charAt(0).toUpperCase() + userType?.slice(1)}</div>
                    </div>
                  </div>
                </li>
                
                {/* Logout Button */}
                <li className="nav-item">
                  <button 
                    className="btn btn-outline-danger btn-sm"
                    onClick={handleLogout}
                    title="Logout"
                  >
                    üö™ Logout
                  </button>
                </li>
              </ul>
            )}
            
            {/* Login button for non-authenticated users */}
            {!isAuthenticated && (
              <ul className="navbar-nav ms-auto">
                <li className="nav-item me-2">
                  <button 
                    className="btn btn-outline-primary"
                    onClick={() => navigate('/register')}
                  >
                    üìù Register
                  </button>
                </li>
                <li className="nav-item">
                  <button 
                    className="btn btn-primary"
                    onClick={() => navigate('/login')}
                  >
                    üîê Login
                  </button>
                </li>
              </ul>
            )}
          </div>
        </div>
      </nav>

      {/* Custom CSS for navbar styling and animations */}
      <style jsx>{`
        .nav-link.active {
          background-color: rgba(13, 110, 253, 0.1) !important;
          border-radius: 6px;
          font-weight: 600;
          color: #0d6efd !important;
        }
        
        .nav-link:hover {
          background-color: rgba(13, 110, 253, 0.05);
          border-radius: 6px;
          transition: all 0.2s ease;
        }
        
        .emergency-btn {
          animation: emergency-pulse 2s infinite;
          font-weight: 600;
        }
        
        .pulse {
          animation: status-pulse 2s infinite;
        }
        
        @keyframes emergency-pulse {
          0% { 
            box-shadow: 0 0 0 0 rgba(220, 53, 69, 0.7);
            transform: scale(1);
          }
          50% {
            transform: scale(1.05);
          }
          70% { 
            box-shadow: 0 0 0 10px rgba(220, 53, 69, 0); 
          }
          100% { 
            box-shadow: 0 0 0 0 rgba(220, 53, 69, 0);
            transform: scale(1);
          }
        }
        
        @keyframes status-pulse {
          0%, 100% { opacity: 1; }
          50% { opacity: 0.5; }
        }
      `}</style>
    </>
  );
};

export default Navbar;
