import React, { useState, useEffect } from 'react';
import { toast } from 'react-toastify';

const AlertPanel = () => {
  const [alerts, setAlerts] = useState([]);
  const [filter, setFilter] = useState('all');
  const [loading, setLoading] = useState(false);
  const [selectedAlerts, setSelectedAlerts] = useState([]);

  // Sample alerts data
  const sampleAlerts = [
    {
      id: 1,
      type: 'SOS',
      touristId: 'tourist-001',
      touristName: 'John Doe',
      message: 'Emergency SOS Alert - Immediate assistance required',
      location: { lat: 28.6139, lng: 77.2090, address: 'India Gate, Delhi' },
      timestamp: new Date(Date.now() - 5 * 60000), // 5 minutes ago
      status: 'active',
      priority: 'critical',
      assignedOfficer: 'Officer Smith',
      responseTime: null
    },
    {
      id: 2,
      type: 'ZONE_VIOLATION',
      touristId: 'tourist-003',
      touristName: 'Mike Johnson',
      message: 'Tourist entered unsafe zone after dark hours',
      location: { lat: 28.6100, lng: 77.2070, address: 'Construction Area, Delhi' },
      timestamp: new Date(Date.now() - 15 * 60000), // 15 minutes ago
      status: 'acknowledged',
      priority: 'high',
      assignedOfficer: 'Officer Brown',
      responseTime: '2 minutes'
    },
    {
      id: 3,
      type: 'OFFLINE',
      touristId: 'tourist-005',
      touristName: 'Lisa Chen',
      message: 'Tourist offline for more than 2 hours - welfare check required',
      location: { lat: 28.6120, lng: 77.2080, address: 'Last seen: Market Area' },
      timestamp: new Date(Date.now() - 45 * 60000), // 45 minutes ago
      status: 'investigating',
      priority: 'medium',
      assignedOfficer: 'Officer Davis',
      responseTime: '5 minutes'
    },
    {
      id: 4,
      type: 'SAFETY_SCORE',
      touristId: 'tourist-007',
      touristName: 'Robert Kim',
      message: 'Safety score dropped below critical threshold (55%)',
      location: { lat: 28.6150, lng: 77.2090, address: 'Tourist District, Delhi' },
      timestamp: new Date(Date.now() - 60 * 60000), // 1 hour ago
      status: 'resolved',
      priority: 'low',
      assignedOfficer: 'Officer Wilson',
      responseTime: '1 minute'
    },
    {
      id: 5,
      type: 'SOS',
      touristId: 'tourist-008',
      touristName: 'Emma Watson',
      message: 'Medical emergency - tourist requesting ambulance',
      location: { lat: 28.6145, lng: 77.2105, address: 'Red Fort, Delhi' },
      timestamp: new Date(Date.now() - 30 * 60000), // 30 minutes ago
      status: 'resolved',
      priority: 'critical',
      assignedOfficer: 'Officer Johnson',
      responseTime: '30 seconds'
    }
  ];

  useEffect(() => {
    loadAlerts();
  }, []);

  const loadAlerts = () => {
    setLoading(true);
    // Simulate API call
    setTimeout(() => {
      setAlerts(sampleAlerts);
      setLoading(false);
    }, 500);
  };

  const getFilteredAlerts = () => {
    return alerts.filter(alert => {
      if (filter === 'all') return true;
      if (filter === 'active') return alert.status === 'active';
      if (filter === 'critical') return alert.priority === 'critical';
      if (filter === 'unresolved') return alert.status !== 'resolved';
      return alert.type === filter;
    });
  };

  const updateAlertStatus = (alertId, newStatus) => {
    setAlerts(alerts.map(alert => 
      alert.id === alertId 
        ? { 
            ...alert, 
            status: newStatus,
            responseTime: newStatus === 'acknowledged' && !alert.responseTime ? '1 minute' : alert.responseTime
          }
        : alert
    ));

    const alert = alerts.find(a => a.id === alertId);
    toast.success(`✅ Alert #${alertId} (${alert?.touristName}) marked as ${newStatus}`);
  };

  const bulkUpdateAlerts = (status) => {
    if (selectedAlerts.length === 0) {
      toast.warning('Please select alerts first');
      return;
    }

    setAlerts(alerts.map(alert => 
      selectedAlerts.includes(alert.id) 
        ? { ...alert, status }
        : alert
    ));

    toast.success(`✅ ${selectedAlerts.length} alerts marked as ${status}`);
    setSelectedAlerts([]);
  };

  const toggleAlertSelection = (alertId) => {
    setSelectedAlerts(prev => 
      prev.includes(alertId) 
        ? prev.filter(id => id !== alertId)
        : [...prev, alertId]
    );
  };

  const getAlertIcon = (type) => {
    switch (type) {
      case 'SOS': return '🆘';
      case 'ZONE_VIOLATION': return '⚠️';
      case 'OFFLINE': return '📴';
      case 'SAFETY_SCORE': return '📉';
      case 'MEDICAL': return '🏥';
      default: return '⚡';
    }
  };

  const getPriorityBadge = (priority) => {
    switch (priority) {
      case 'critical': return 'bg-danger';
      case 'high': return 'bg-warning';
      case 'medium': return 'bg-info';
      case 'low': return 'bg-secondary';
      default: return 'bg-secondary';
    }
  };

  const getStatusBadge = (status) => {
    switch (status) {
      case 'active': return 'bg-danger';
      case 'acknowledged': return 'bg-warning';
      case 'investigating': return 'bg-info';
      case 'resolved': return 'bg-success';
      default: return 'bg-secondary';
    }
  };

  const getTimeSince = (date) => {
    const minutes = Math.floor((new Date() - new Date(date)) / 60000);
    if (minutes < 1) return 'Just now';
    if (minutes < 60) return `${minutes}m ago`;
    const hours = Math.floor(minutes / 60);
    if (hours < 24) return `${hours}h ago`;
    return `${Math.floor(hours / 24)}d ago`;
  };

  const assignToOfficer = (alertId) => {
    const officer = prompt('Assign to officer:');
    if (officer) {
      setAlerts(alerts.map(alert => 
        alert.id === alertId 
          ? { ...alert, assignedOfficer: officer }
          : alert
      ));
      toast.success(`Alert assigned to ${officer}`);
    }
  };

  const viewLocation = (location) => {
    const message = `Location: ${location.address}\nCoordinates: ${location.lat}, ${location.lng}`;
    alert(message);
  };

  const emergencyDispatch = () => {
    const criticalAlerts = alerts.filter(a => a.priority === 'critical' && a.status === 'active');
    if (criticalAlerts.length > 0) {
      toast.error(`🚨 Emergency dispatch activated for ${criticalAlerts.length} critical alerts`);
    } else {
      toast.info('No critical alerts requiring emergency dispatch');
    }
  };

  const filteredAlerts = getFilteredAlerts();

  return (
    <div className="container-fluid py-4">
      <div className="row">
        <div className="col-12">
          <div className="card">
            <div className="card-header">
              <h3 className="mb-0">🚨 Alert Management Center</h3>
              <p className="mb-0 mt-2">Monitor, manage and respond to tourist safety alerts in real-time</p>
            </div>
          </div>
        </div>
      </div>

      {/* Alert Statistics */}
      <div className="row mt-4">
        <div className="col-md-3">
          <div className="stat-card">
            <div className="d-flex justify-content-between align-items-center">
              <div>
                <div className="stat-number text-danger">{alerts.filter(a => a.status === 'active').length}</div>
                <div className="stat-label">Active Alerts</div>
              </div>
              <div className="text-danger" style={{ fontSize: '2rem' }}>🚨</div>
            </div>
          </div>
        </div>
        <div className="col-md-3">
          <div className="stat-card">
            <div className="d-flex justify-content-between align-items-center">
              <div>
                <div className="stat-number text-warning">{alerts.filter(a => a.priority === 'critical').length}</div>
                <div className="stat-label">Critical Priority</div>
              </div>
              <div className="text-warning" style={{ fontSize: '2rem' }}>⚡</div>
            </div>
          </div>
        </div>
        <div className="col-md-3">
          <div className="stat-card">
            <div className="d-flex justify-content-between align-items-center">
              <div>
                <div className="stat-number text-info">{alerts.filter(a => a.status === 'investigating').length}</div>
                <div className="stat-label">Under Investigation</div>
              </div>
              <div className="text-info" style={{ fontSize: '2rem' }}>🔍</div>
            </div>
          </div>
        </div>
        <div className="col-md-3">
          <div className="stat-card">
            <div className="d-flex justify-content-between align-items-center">
              <div>
                <div className="stat-number text-success">{alerts.filter(a => a.status === 'resolved').length}</div>
                <div className="stat-label">Resolved Today</div>
              </div>
              <div className="text-success" style={{ fontSize: '2rem' }}>✅</div>
            </div>
          </div>
        </div>
      </div>

      {/* Filters and Actions */}
      <div className="row mt-4">
        <div className="col-md-8">
          <div className="card">
            <div className="card-body">
              <div className="row">
                <div className="col-md-6">
                  <label className="form-label">🎯 Filter Alerts</label>
                  <select 
                    className="form-control"
                    value={filter}
                    onChange={(e) => setFilter(e.target.value)}
                  >
                    <option value="all">All Alerts</option>
                    <option value="active">🔴 Active Only</option>
                    <option value="critical">⚡ Critical Priority</option>
                    <option value="unresolved">⏳ Unresolved</option>
                    <option value="SOS">🆘 SOS Alerts</option>
                    <option value="ZONE_VIOLATION">⚠️ Zone Violations</option>
                    <option value="OFFLINE">📴 Offline Tourists</option>
                  </select>
                </div>
                <div className="col-md-6 d-flex align-items-end">
                  <button 
                    className="btn btn-primary me-2"
                    onClick={loadAlerts}
                    disabled={loading}
                  >
                    {loading ? '🔄' : '🔄'} Refresh
                  </button>
                  <button 
                    className="btn btn-danger"
                    onClick={emergencyDispatch}
                  >
                    🚨 Emergency Dispatch
                  </button>
                </div>
              </div>
            </div>
          </div>
        </div>
        <div className="col-md-4">
          <div className="card">
            <div className="card-body">
              <div className="d-flex justify-content-between align-items-center">
                <span>
                  Showing {filteredAlerts.length} of {alerts.length} alerts
                  {selectedAlerts.length > 0 && (
                    <span className="ms-2 badge bg-primary">{selectedAlerts.length} selected</span>
                  )}
                </span>
                <div>
                  <button 
                    className="btn btn-success btn-sm me-2"
                    onClick={() => toast.info('Alert report exported')}
                  >
                    📊 Export
                  </button>
                  <div className="dropdown d-inline">
                    <button 
                      className="btn btn-warning btn-sm dropdown-toggle" 
                      type="button" 
                      data-bs-toggle="dropdown"
                      disabled={selectedAlerts.length === 0}
                    >
                      Bulk Actions
                    </button>
                    <ul className="dropdown-menu">
                      <li><a className="dropdown-item" href="#" onClick={() => bulkUpdateAlerts('acknowledged')}>Mark as Acknowledged</a></li>
                      <li><a className="dropdown-item" href="#" onClick={() => bulkUpdateAlerts('investigating')}>Mark as Investigating</a></li>
                      <li><a className="dropdown-item" href="#" onClick={() => bulkUpdateAlerts('resolved')}>Mark as Resolved</a></li>
                    </ul>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Alerts List */}
      <div className="row mt-4">
        <div className="col-12">
          <div className="card">
            <div className="card-header">
              <h5 className="mb-0">📋 Alert Queue ({filteredAlerts.length})</h5>
            </div>
            <div className="card-body p-0">
              {filteredAlerts.length > 0 ? (
                filteredAlerts.map((alert) => (
                  <div key={alert.id} className="alert-item border-bottom p-3">
                    <div className="row align-items-center">
                      <div className="col-md-1">
                        <div className="form-check">
                          <input 
                            className="form-check-input" 
                            type="checkbox"
                            checked={selectedAlerts.includes(alert.id)}
                            onChange={() => toggleAlertSelection(alert.id)}
                          />
                        </div>
                      </div>

                      <div className="col-md-7">
                        <div className="d-flex align-items-start">
                          <div className="me-3">
                            <span style={{ fontSize: '1.5rem' }}>{getAlertIcon(alert.type)}</span>
                          </div>
                          <div className="flex-grow-1">
                            <div className="d-flex align-items-center mb-2">
                              <h6 className="mb-0 me-2">Alert #{alert.id} - {alert.touristName}</h6>
                              <span className={`badge ${getPriorityBadge(alert.priority)} me-2`}>
                                {alert.priority.toUpperCase()}
                              </span>
                              <span className={`badge ${getStatusBadge(alert.status)}`}>
                                {alert.status.toUpperCase()}
                              </span>
                            </div>
                            <p className="mb-1">{alert.message}</p>
                            <div className="small text-muted">
                              <span className="me-3">📍 {alert.location.address}</span>
                              <span className="me-3">🕒 {getTimeSince(alert.timestamp)}</span>
                              <span className="me-3">👮 {alert.assignedOfficer || 'Unassigned'}</span>
                              {alert.responseTime && (
                                <span className="me-3">⏱️ Response: {alert.responseTime}</span>
                              )}
                            </div>
                          </div>
                        </div>
                      </div>

                      <div className="col-md-4">
                        <div className="btn-group w-100">
                          {alert.status === 'active' && (
                            <button 
                              className="btn btn-warning btn-sm"
                              onClick={() => updateAlertStatus(alert.id, 'acknowledged')}
                            >
                              ✅ Acknowledge
                            </button>
                          )}
                          {alert.status === 'acknowledged' && (
                            <button 
                              className="btn btn-info btn-sm"
                              onClick={() => updateAlertStatus(alert.id, 'investigating')}
                            >
                              🔍 Investigate
                            </button>
                          )}
                          {alert.status === 'investigating' && (
                            <button 
                              className="btn btn-success btn-sm"
                              onClick={() => updateAlertStatus(alert.id, 'resolved')}
                            >
                              ✅ Resolve
                            </button>
                          )}

                          <div className="dropdown">
                            <button 
                              className="btn btn-secondary btn-sm dropdown-toggle" 
                              type="button" 
                              data-bs-toggle="dropdown"
                            >
                              Actions
                            </button>
                            <ul className="dropdown-menu">
                              <li><a className="dropdown-item" href="#" onClick={() => viewLocation(alert.location)}>📍 View Location</a></li>
                              <li><a className="dropdown-item" href="#" onClick={() => assignToOfficer(alert.id)}>👮 Assign Officer</a></li>
                              <li><a className="dropdown-item" href={`tel:${alert.touristId}`}>📞 Call Tourist</a></li>
                              <li><hr className="dropdown-divider" /></li>
                              <li><a className="dropdown-item text-danger" href="#" onClick={() => toast.warning('Priority escalated')}>⬆️ Escalate Priority</a></li>
                            </ul>
                          </div>
                        </div>
                      </div>
                    </div>
                  </div>
                ))
              ) : (
                <div className="text-center py-5">
                  <h5 className="text-muted">🎉 All Clear!</h5>
                  <p className="text-muted">
                    {filter === 'all' 
                      ? 'No alerts in the system - all tourists are safe' 
                      : 'No alerts match your current filter criteria'}
                  </p>
                  {filter !== 'all' && (
                    <button 
                      className="btn btn-primary"
                      onClick={() => setFilter('all')}
                    >
                      Show All Alerts
                    </button>
                  )}
                </div>
              )}
            </div>
          </div>
        </div>
      </div>

      {/* Quick Response Panel */}
      <div className="row mt-4">
        <div className="col-12">
          <div className="card">
            <div className="card-header">
              <h5 className="mb-0">⚡ Emergency Response Center</h5>
            </div>
            <div className="card-body">
              <div className="row">
                <div className="col-md-2">
                  <button 
                    className="btn btn-danger w-100 py-3"
                    onClick={emergencyDispatch}
                  >
                    🚨<br />Emergency<br />Dispatch
                  </button>
                </div>
                <div className="col-md-2">
                  <button 
                    className="btn btn-warning w-100 py-3"
                    onClick={() => toast.info('Safety broadcast sent to all tourists')}
                  >
                    📢<br />Safety<br />Broadcast
                  </button>
                </div>
                <div className="col-md-2">
                  <button 
                    className="btn btn-info w-100 py-3"
                    onClick={() => toast.info('Backup units requested')}
                  >
                    🚁<br />Request<br />Backup
                  </button>
                </div>
                <div className="col-md-2">
                  <button 
                    className="btn btn-success w-100 py-3"
                    onClick={() => toast.info('Incident report generated')}
                  >
                    📊<br />Generate<br />Report
                  </button>
                </div>
                <div className="col-md-2">
                  <button 
                    className="btn btn-primary w-100 py-3"
                    onClick={() => toast.info('All zone commanders notified')}
                  >
                    👮‍♂️<br />Notify<br />Commanders
                  </button>
                </div>
                <div className="col-md-2">
                  <button 
                    className="btn btn-secondary w-100 py-3"
                    onClick={() => window.location.href = '/police/dashboard'}
                  >
                    🏠<br />Back to<br />Dashboard
                  </button>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Alert Timeline */}
      <div className="row mt-4">
        <div className="col-12">
          <div className="card">
            <div className="card-header">
              <h5 className="mb-0">📈 Alert Timeline (Last 24 Hours)</h5>
            </div>
            <div className="card-body">
              <div className="row text-center">
                <div className="col-md-2">
                  <div className="border-end">
                    <h4 className="text-danger">12</h4>
                    <small>Total Alerts</small>
                  </div>
                </div>
                <div className="col-md-2">
                  <div className="border-end">
                    <h4 className="text-warning">8</h4>
                    <small>Resolved</small>
                  </div>
                </div>
                <div className="col-md-2">
                  <div className="border-end">
                    <h4 className="text-info">2.5m</h4>
                    <small>Avg Response</small>
                  </div>
                </div>
                <div className="col-md-2">
                  <div className="border-end">
                    <h4 className="text-success">96%</h4>
                    <small>Resolution Rate</small>
                  </div>
                </div>
                <div className="col-md-2">
                  <div className="border-end">
                    <h4 className="text-primary">5</h4>
                    <small>SOS Alerts</small>
                  </div>
                </div>
                <div className="col-md-2">
                  <h4 className="text-secondary">3</h4>
                  <small>Zone Violations</small>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* System Status */}
      <div className="row mt-4">
        <div className="col-12">
          <div className="alert alert-info">
            <h6 className="alert-heading">📡 Real-time Alert Monitoring</h6>
            <div className="row">
              <div className="col-md-3">
                <strong>System Status:</strong> <span className="badge bg-success">Operational</span>
              </div>
              <div className="col-md-3">
                <strong>Last Update:</strong> {new Date().toLocaleTimeString()}
              </div>
              <div className="col-md-3">
                <strong>Active Monitors:</strong> 24/7 Coverage
              </div>
              <div className="col-md-3">
                <strong>Response Team:</strong> <span className="badge bg-primary">Ready</span>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AlertPanel;
